css:
@-moz-document url-prefix("http://www.windows93.net/b/"){

/* made mostly with inspect element, might edit later
would personally use yotsuba in the themes menu along with this */

/* Inline #3 | http://www.windows93.net/b/ */

body {
  background: #1E1E1E;
  color: #AAAAAA;
}

.filetitle {
  color: #7868DA !important;
  font-weight: bold;
}

hr {
  border-top: 2px solid #313131;
}

a {
  color: #C369AB !important;
}

.filesize {
  font-size: 10px;
}

.omittedposts {
  color: #C27CFF;
  font-size: 10pt;
  border-top: 2px dashed #313131;
}

.reply {
  color: #b7b7b7;
  background: #313131;
  border: 2px solid #282828;
  border-top: none;
  border-left: none;
}

.commentpostername {
  color: #C27CFF;
}

a:hover {
  color: white !important;
}

.replytitle {
  color: #7868DA;
}

.logo a:hover {
  color: #C27CFF !important;
}

.logo a {
  text-decoration: none;
  color: #C27CFF !important;
  font-family: tahoma, monospace, sans-serif;
  font-size: 20pt;
  font-weight: bold;
}

/* yot.css | http://www.windows93.net/b/css/yot.css */

.postername {
  /* color: #117743; */
  color: #C27CFF;
}


/* Inline #4 | http://www.windows93.net/b/ */

.postblocktitle {
  background: none;
  color: #C27CFF;
}

.passvalid, .postblocktitle{background: none;}

.postblock {
  color: #CCCCCC;
  background: #474747;
  border: none;
}

input[type="text"], input[type="password"], input[type="number"], textarea {
  background: #313131;
  color: #CCCCCC;
  border: 1px solid #666 !important;
}

input[type="text"]:focus, input[type="password"]:focus, input[type="number"], textarea:focus{border-color: #CCCCCC !important}


/* Inline #4 | http://www.windows93.net/b/imgboard.php?mode=catalog */

.cata {
  color: #AAA !important;
}

.catthread {
  background: #303030;
  border: 2px solid #464646;
}

.catthumb {
  box-shadow: 0 0 4px rgba(0,0,0,0.55);
}
}
