@-moz-document url-prefix("https://kogama.com.br"), url-prefix("https://www.kogama.com") {
/* 
Created by: MB_LIthium 
Stylus - Firefox

V.0.0.3
*/

:root {
/* Mude as cores por aqui dos principais elementos. Faça sua própria combinação! :) */
    --color-hr: #2f3640; /* header elements */
    --bg-theme: #353b48; /* Cor 2 */
    --bg-timeline: #353b48; /* Background da Timeline do Perfil */
    --xp-progress: #2ed573; /* Progress XP */
    --xp-bar: #7bed9f; /* XP Bar */
    /* --chat-send --chat-reply */
}
/* Header principal */
html body#root-page-mobile header#pageheader {
    width: 90%;
    margin-top: 50px;
    margin-left: 50px;
    margin-right: 50px;
    background-color: var(--color-hr);
    position: absolute;
    display: block;
    border-radius: 20px;
}
/* SubHeader principal */
html body#root-page-mobile div#content.authenticated div#content-container header#pagesubheader {
    margin-top: 50px;
    display: block;
    margin-left: 50px;
    margin-right: 50px;
    width: 90%;
    background-color: var(--bg-theme);
}

/* XP BAR */
.xp-bar .progress {
    background-color: var(--xp-bar);
}
.xp-bar .progress .progression-bar {
    background-color: var(--xp-progress);
}
div.xp-level a div.xp-bar div.xp-text {
    color: var(--bg-theme)
}
/* Configurações do Fundo principal */

html body#root-page-mobile {
    background-color: var(--bg-theme);
    background-image:linear-gradient(180deg,var(--bg-theme),var(--bg-theme));
}
html body#root-page-mobile div#content.authenticated div#content-container div#main-content {
    background-color: transparent;
}
html body#root-page-mobile div#content.authenticated {
    background-color: transparent;
}

/* Lista de jogos - Menu Principal */

#root-page-mobile #content.authenticated #content-container #main-content {
    margin-left: 50px;
    width: calc(100% - 10px);
}



/* Menu */
html body#root-page-mobile header#pageheader div.pageheader-inner div#profile-extended {
    position: absolute;
    margin-right: 20px;
    margin-top: 5px;
    height: auto;
    background-color: var(--bg-theme);
}
html body#root-page-mobile header#pageheader div.pageheader-inner div#profile-extended h4.username {
    background-color: var(--color-hr)
        
}
html body#root-page-mobile header#pageheader div.pageheader-inner div#profile-extended div#header-user-info section.user-credits {
    background-color: var(--color-hr)
}

/* Button */
div.button-wrapper button.pure-button.pure-button-primary.button-fill {
   background-color: #1e90ff;
}

/* Coupom */
html.no-touch body#root-page-mobile div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content article#coupon-redeem.content-content {
    margin: 65px 0 0 50px;
    padding: 0 500px 0 0;
}
/* Ads */

div#kogama_mobile_leaderboard_1_container.ads-display.ads-leaderboard {
    display: none; /* Home Page */
}

div#kogama_mobile_leaderboard_2.ads-display.ads-leaderboard.ads-leaderboard-bottom {
    display: none; /* Home Page Bottom */
}

html body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content div#kogama-rectangle-container {
    content: "Gostou do tema? Que tal mandar um feedback para melhorar cada vez mais!";
    width: 50px;
    display: none;
}

/*
html body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content div#kogama-wide-skyscraper-container {
    display: none;
}
div#kogama-rectangle-container {
    display: none;
}

}

@-moz-document url-prefix("https://kogama.com.br"), url-prefix("https://www.kogama.com") {
/* Chat */
div#content.authenticated div#chat-extended-side {
    display: block;
    position: relative;
    width: 350px;
    height: 600px;
    margin-right: 80px;
    margin-top: 0;
    border-radius: 10px 10px 20px 20px;
}
div#content.authenticated div#chat-extended-side div._3TORb {
    background-color: var(--color-hr);
}
/* Chat amigos */
div#content.authenticated div#chat-extended-side div._3TORb div div._1Yhgq div._1lvYU {
    background-color: var(--color-hr);
    margin-top: 10px;
}
/* Chat Fundo */
div#content.authenticated div#chat-extended-side div._3TORb div div._1Yhgq {
    background-color: var(--color-hr);
}

/* Chat Mensagens */

div#content.authenticated div#chat-extended-side div._3TORb div div.zUJzi {
    margin-right: 60px;
}
div#content.authenticated div#chat-extended-side div._3TORb div div.zUJzi div.o_DA6 div.uwn5j {
    background-color: var(--color-hr);
}
div#content.authenticated div#chat-extended-side div._3TORb div div.zUJzi div._2BvOT div._375XK div.F3PyX {
    background-color: var(--color-hr);
}
div#content.authenticated div#chat-extended-side div._3TORb div div.zUJzi div._2BvOT div._375XK div._2XaOw {
    background-color: var(--color-hr);
}
div#content.authenticated div#chat-extended-side div._3TORb div div.zUJzi div._2BvOT div._375XK textarea {
    background-color: var(--color-hr);
    border: none;
    color: white;
}
div#content.authenticated div#chat-extended-side div._3TORb div div.zUJzi div._2BvOT div._375XK div._2XaOw div._1j2Cd._1Xzzq p { /* Balão */
    background-color: var(--bg-theme);
    border-radius: 15px 5px 15px 5px;
    text-shadow: none;
    text-decoration: none;
    box-shadow: none;
    color: white;
    text-align: left;
    hyphens: auto;
    /*padding: 20px 50px 20px 50px;*/
}
div#content.authenticated div#chat-extended-side div._3TORb div div.zUJzi div._2BvOT div._375XK div._2XaOw div._1j2Cd.undefined p { /* Balão 2 */
    background-color: #2ed573;
    border-radius: 15px 5px 15px 5px;
    text-shadow: none;
    text-decoration: none;
    box-shadow: none;
    color: #2f3542;
    text-align: left;
    hyphens: auto;
}
}

@-moz-document url-prefix("https://kogama.com.br/profile"), url-prefix("https://www.kogama.com/profile") {
html body#root-page-mobile header#pageheader {
    /* Cabeçalho da página */
    width: 90%;
    margin-top: 50px;
    margin-left: 50px;
    margin-right: 50px;
    background-color: var(--color-hr);
    position: absolute;
    display: block;
    border-radius: 20px;
}


html body#root-page-mobile div#content.authenticated div#content-container div#main-content div#mobile-page { /* Perfil Timeline */
    width: 90%;
    margin-top: 0px;
    margin-left: 50px;
    margin-right: 50px;
    position: relative;
    display: block;
    background-color: var(--bg-timeline);
    border-radius: 20px;
}
html body#root-page-mobile div#content.authenticated {
    /* Cor de fundo */
    margin-top: -50px;
    padding-top: 125px;
    background-color: var(--bg-theme);
}

}

@-moz-document url-prefix("https://kogama.com.br/"), url-prefix("https://www.kogama.com") {
/* Adjust Build */
html body#root-page-mobile.build div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content {
    margin: 0 25% 0 50px;
} 

/* Adjust shop */
html body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content article.content-shop.content-content {
    margin: 0 25% 0 50px;
}

/* Shop */
html.no-touch body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content {
    display: block;
    margin: 0 0 0 50px;
}
div.shop-image {
    border-radius: 15px;
}
#mobile-page .content-shop .shop-list .shop-item a {
    -webkit-box-shadow: none;
    box-shadow: none;
}
html body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content section.content-shop.content-content {
    /* list */
    margin: 0 15% 0 50px;
}
html.no-touch body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content article#product-detail.content-content.product-detail-avatar div.product-container div.product-purchase-container div.product-purchase div.product-image.product-image-avatar {
    border-radius: 90px 15px 90px 15px; /* Image Avatar */
}
html.no-touch body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content article#product-detail.content-content.product-detail-model div.product-container div.product-purchase-container div.product-purchase div.product-image.product-image-model {
    border-radius: 80px 15px 80px 15px; /* Image Model */
}
html.no-touch body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content article#product-detail.content-content.product-detail-avatar div.product-container header.product-header h4 {
    color: #eccc68; /* "Avatar" color */
}
html.no-touch body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content article#product-detail.content-content.product-detail-model div.product-container header.product-header h4 {
    color: #2ed573;
    
}
a.pure-button.pure-button-xsmall.delete {
    border-radius: 50px;
}
.pure-button {
    border-radius: 50px; /* button*/
}


/* Button Buy */
html.no-touch body#root-page-mobile.shop div#content.authenticated div#content-container div#main-content div#mobile-page div#mobile-page-content div#marketplace-detail-content article#product-detail.content-content.product-detail-avatar div.product-container div.product-purchase-container div#product-purchase-link div button.pure-button.pure-button-primary.pure-button-xlarge.purchase-button {
    margin: 10px 0 10px 0;
    border-radius: 35px;
    background-color: #2ed573;
}
#mobile-page #product-detail #product-purchase-link .purchase-button {
    margin: 10px 0 0 0;
    border-radius: 25px;
    background-color: #2ed573;
}
#mobile-page #product-detail #product-purchase-link {
    background-color: transparent;
}





#mobile-page #product-detail #product-purchase-link {
    background-color: transparent;
}

/* Elite Subscribe */
html body#root-page-mobile.subscription {
    margin: 50px 0 0 0;
}
html body#root-page-mobile.purchase {
    margin: 50px 0 0 0;
}

}

