css:
@-moz-document domain("masteringphysics.com") {
/*
	SINGLE QUESTIONS
*/

html, body {
	background: #242424;
	color: #f6f3e8;
}

section.assignment-meta-info dd.assignment-complete-score {
	color: #8ac6f2;
	
}

.return-assignment-link {
	color: #8ac6f2;
}

#item-nav h3 {
	color: #8ac6f2;
}

.nav span.nav-circle {
	background-color: #303030;
	border: 1px solid #656565;
}

#playersite:not(.print) .introBox:not(.hidden) {
	background-color: #303030;
}

.section.part.foldy.open > .hd {
	background-color: #303030;
}

.section.part.foldy > .hd * {
	color: #f6f3e8;
}

.resources a {
	color: #8ac6f2;
}

.answer .work {
	background-color: #303030;
	border: 1px solid #656565;
	
}


/* equation editor stuff */
.EEToolbar {
	background-color: #303030;
	border: 1px solid #656565;
}

.transparentEEButton, .showPaletteButton.inactive, .paletteButton {
	filter: invert();
}

.EEPaletteContainer {
	background-color: #444;
}

/* back to general */

.btn-default .fa[class*="fa-"], .btn-default {
	color: #f6f3e8;
	background-color: #303030;
}

.btn-default {
	border: 1px solid #656565;
}

.btn-default:hover {
	background-color: #aaa!important;
	color: #303030;
}

.btn-default:focus {
	background-color: #c0c0c0;
	color: #303030;
}

.btn-default:hover>i:before, .btn-default:active>i:before {
	background-color: #aaa!important; /* not sure why specifity is broken here */
	color: #303030;
}

.btn-default:focus>i:before {
	background-color: #c0c0c0;
	color: #303030;
	
}

::-webkit-scrollbar {
    width: 12px;
}
 
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px black; 
    border-radius: 10px;
}
 
::-webkit-scrollbar-thumb {
    border-radius: 3px;
    -webkit-box-shadow: inset 0 0 6px #f6f3e8; 
}

#playersite:not(.print) .section.transition {
	background-color: #303030;
	border: 1px solid #656565;
}

.answer > .responsesBox > .responses {
	background-color: #303030;
	border: 1px solid #454545;
	box-shadow: 1px 1px 1px 0 #656565;
}

.answer > .responsesBox > .responses > div.comment, .answer > .responsesBox > .responses > div.after {
	color: #f6f3e8;
}

.btn-primary.disabled, .btn-primary[disabled] {
	background-color: #bbb;
	border-color: #ddd;
	color: #666;
}

.answer > .responsesBox > .responses > div.feedback, .answer > .responsesBox > .responses > div.followup {
	color: #f6f3e8;
}

.notice-bar .completion .fa {
	color: #8ac6f2;
}

.notice-bar .completion {
	color: #f6f3e8;
}

.eqEditor.focused .eqDocument .current {
	background-color: #33A1C9;
}

textarea {
	color: #f6f3e8;
	background-color: #444;
}

div.appletSite.equationEditor {
	background-color: #444;
}

/* Drag and drop stuff */
.sorting li.sort-item, .ranking li.rank-item {
	background-color: #454545;
}

.sorting ul.sort-item-list li.sort-item.unreusable, .ranking ul.rank-item-list li.rank-item.unreusable {
	background-color: #777799;
}

#MathJax_Zoom {
	background-color: #555555;
	border: 1px solid #aaa;
	-webkit-box-shadow: 5px 5px 15px #888;
}

.sorting div.sort-bin-container .sort-bin.colored, .ranking div.rank-area-container {
	background-color: #454545;
}

.sorting div.sort-bin-container .sort-bin .sort-bin-header-txt, .ranking .rank-area-container .rank-pre-text, .ranking .rank-area-container .rank-post-text {
	background-color: transparent;
}

.sorting .sorting-reset-button, .sorting .help-button, .ranking .ranking-reset-button, .ranking .help-button {
	background-color: #454545;
	color: #f6f3e8;
}

.ranking .rank-drop-area .rank-zone.yui3-dd-drop-over,
.ranking .rank-drop-area .rank-zone.filled.multiple.yui3-dd-drop-over {
	background-color: #664411;	
}

/*
	SINGLE CHAPTER PAGE
*/

#mainBodyContainer {
	background: #242424;
	color: #f6f3e8;
}

section.assignment-meta-info {
	background-color: #303030;
	border: 1px solid #656565;
}

a.assignmentDueDate, a.assignmentDueDate:hover {
	color: white;
}

a {
	color: #8ac6f2;
}

.msticon-check-complete, .section.part > .hd .part-complete i.fa, .notice-bar .completion .fa {
	color: #038238;
}

object.inline-svg.mlm-logo {
    filter: invert();
}

/*
	COURSE HOME
*/

.collapsible-container--title--h3--a, .collapsible-container--title--h3--a:focus, .collapsible-container--title--h3--a:hover {
    background-color: #454545!important;
    border: 1px solid #888!important;
    color: #f6f3e8!important;
}

.list-assignments>li {
	background-color: #303030;
	border: 1px solid #656565;
}

.list-announcements p.status {
	color: #f6f3e8;
}

.description, .assignment-status {
	color: #f6f3e8;
}

.hint-block.foldy > .bd {
	background-color: #454545;
}

.section.hint > .head, .section.hint > .head.unrevealed, .hint-block > .hd span.hints-head , .hint-block.foldy > .hd .hint-block-icon > i.fa{
	 color: #90aAfa;
}

select, .pane .head {
	background: #333;
	color: #f6f3e8;
}

.nav span.nav-circle {
	border: 1px solid #f6f3e8;
}
.nav span.nav-circle .fa {
	color: #f6f3e8;
}

.nav span.nav-circle.disabled .fa {
	color: #777;
}
.nav span.nav-circle.disabled {
	    border: 1px solid #666;
}

/* EXPIRED SESSION */
.mx-session-expired-message-container {
	color: #f6f3e8;
}
}
