css:
@-moz-document domain("youtube.com") {
/* remove related video sidebar */
#related {
    display:none;
}

/* remove video endscreen */
.html5-endscreen {
    display: none;
}

/* remove up next screen */
.ytp-upnext {
    display: none;
}

/* remove hashtag links above title*/
.super-title.ytd-video-primary-info-renderer {
    display:none
}

/* remove like, dislike, save and share buttons*/
#top-level-buttons, #sentiment, #info-text #count, #info-text #dot {
	display:none !important;
}

#subscribe-button.ytd-video-secondary-info-renderer {
	display:none;
}

/* remove channel subscription count */
#owner-sub-count {
	display:none;
}

/* remove view counts */
#count {
    display:none;
}

.style-scope .ytd-comments-header-renderer {
    display:none;
}

/* remove comment like and dislike counts and buttons */
#vote-count-middle, #like-button, #dislike-button {
    display: none !important;
}
}
