css:
@-moz-document domain("accounts.hetzner.com") {
.top-bar {
    background-color: #383838;
}
.login-image-container,
.idp-background {
    background-image: none;
    background-color: #141414;
}
body,
.top-bar .wrapper-logo .logo-text h3,
.top-bar .top-bar-item a.top-bar-link,
.top-bar .top-bar-item .dropdown-menu.dropdown-list-language li a,
.top-bar .dropdown .dropdown-menu a.dropdown-link,
.container-account .additional-delete-account,
.btn.btn-black-light {
    color: #ededed;
}
.btn.btn-black-light:hover {
    color: #fff;
}
.login-container,
.registry-container,
.container-2fa .container-auth-methods .new-auth-container {
    background-color: #262626;
}
.top-bar .top-bar-item .dropdown-menu,
.invoice-container .sorting-bar,
.container-2fa .container-auth-methods .header-bar {
    background-color: #383838;
    border: 0px solid #141414;
}
.top-bar .top-bar-item .dropdown-menu:hover {
    background-color: #383838;
}
.btn.btn-yellow {
    background-color: #d50c2d;
    color: #ededed;
}
.btn.btn-yellow:hover {
    background-color: #ed0d32;
}

.btn.btn-white,
.btn.btn-white:hover {
    background-color: #383838;
    color: #ededed;
}
.login-container .form-group-addon,
.registry-container .form-group-addon {
    border: 2px solid #383838;
}
.top-bar .wrapper-logo .divider-container .divider-wrapper .divider {
    border-right: 1px solid #595959;
}
.top-bar .top-bar-item .dropdown-menu.dropdown-list-apps li:last-child {
    border-top: 1px solid #595959;
}
.list-payment-solutions {
    display: none;
}
footer div.footer-post-section div.text-center {
    margin-top: 10px;
}
.footer-post-section,
.footer-post-section-idp,
.invoice-container ul.invoice-list li .invoice-wrapper,
.container-2fa .container-auth-methods .list-auth-methods > li .auth-method-container.active {
    background-color: #262626;
    color: #ededed;
}
.supplement-section .supplement-element-left,
.supplement-section .supplement-element-left.supplement-element-light-grey {
    border-right: 14px solid #262626;
}
.supplement-section .supplement-element-right,
.supplement-section .supplement-element-right.supplement-element-light-grey {
    border-left: 14px solid #262626;
}
.supplement-section .supplement-element-middle,
.supplement-section .supplement-element-middle.supplement-element-light-grey {
    background-color: #262626;
}
.footer-post-section ul.footer-links-list li a {
    color: #ededed;
}
.login-container .form-signin .signin-content .form-group input[type="email"],
.login-container .form-signin .signin-content .form-group input[type="password"],
.login-container .form-signin .signin-content .form-group input[type="text"],
.registry-container .form-signin .signin-content .form-group input[type="email"],
.registry-container .form-signin .signin-content .form-group input[type="password"],
.registry-container .form-signin .signin-content .form-group input[type="text"] {
    background: #141414;
}
.input-group-addon,
.container-account .additional-delete-account,
.av-content .av-approval {
    background-color: #212121;
}
.hr-grey {
    background-color: #383838;
    height: 1px;
}
body p a,
body ul a,
main a {
    color: #ededed;
}
main a:active,
main a:focus,
main a:hover {
    color: #fff;
}

.logo-top-bar,
.top-bar-link img {
    filter: invert(1) saturate(0) brightness(500%);
}
.top-bar .top-bar-item .dropdown-menu.dropdown-list-apps li:hover {
    background-color: #3d3d3d;
}
.idp-container,
.nav-secondary,
.invoice-container ul.account-credit-list li.no-search-results,
.invoice-container .search-row {
    border-width: 0px;
    background-color: #262626;
}
.nav-secondary .nav-secondary-content ul.nav-list li a.link-active,
.invoice-container .search-row .input-group input,
.invoice-container .search-row .input-group span.input-group-addon {
    color: #ef1f42;
}
select:not([multiple]),
.invoice-container .search-row .input-group input,
.invoice-container .search-row .input-group span.input-group-addon,
.invoice-container .search-row .input-group input:focus {
    background-color: #212121;
    border: 1px solid #595959;
    color: #ededed;
}
.form-control:focus {
    border-color: #ed0d32;
    box-shadow: none;
}
input[type="email"],
input[type="number"],
input[type="password"],
input[type="tel"],
input[type="text"],
textarea,
textarea.form-control {
    background-color: #212121;
    box-shadow: inset 0 0 0 1px #595959;
    color: #ededed;
}
.invoice-container ul.invoice-list li {
    border-width: 0px;
}
}
