css:
@-moz-document url-prefix("https://jump.tf/"), url-prefix("https://jump.tf/forum/") {
#bodyarea,
#bodybg,
body,
.description,
.description_board,
#content,
.hentry,
.post-navigation,
.comment-respond,
.site {
    background-color: #1b1b1c;
}

.windowbg,
.windowbg2,
.roundframe,
.plainbox,
.widget {
    background-color: #2d2d2f;
}

.smalltext,
strong,
.list_posts,
.bbc_standard_quote,
.bbc_alternate_quote,
.pagesection,
.navigate_section ul li a,
h5 
{
    color: #e3e3e3;
}


.bbc_standard_quote,
.site-footer .sidebar-container,
#preview_body {
    background-color: #383a3c;
}
.bbc_alternate_quote {
    background-color: #4b4d4f;
}
body,
td,
th,
tr {
    color: #fff;
}
a:link,
a:visited,
a.new_win:hover {
    color: #D17C00;
}

.table_list tbody.content td.info a.subject,
.navigate_section ul li a:hover {
    color: #D17C00;
}


textarea,
input {
    background: #4b4d4f;
    color: white;
}

.stickybg {
    background: #514641;
}
.stickybg2 {
    background: #423a35;
}

.lockedbg
{
	background: #383844;
}
.lockedbg2
{
	background: #2f2f37;
}

div.title_barIC {
    background: #4b4d4f;
}

.counter {
    color: #b0b0b0;
}


div.title_barIC h4.titlebg,
span.upperframe,
span.upperframe span,
span.lowerframe,
span.lowerframe span,
.windowbg span.topslice,
.windowbg span.topslice span,
.windowbg span.botslice,
.windowbg span.botslice span,
.windowbg2 span.topslice ,
.windowbg2 span.topslice span,
.windowbg2 span.botslice ,
.windowbg2 span.botslice span ,
.approvebg span.topslice,
.approvebg span.topslice span,
.approvebg span.botslice,
.approvebg span.botslice span 
{
    background: none;
}
}
