.CodeMirror {
    color: #ddd;
}

.CodeMirror-gutters {
    border-right: 1px solid #2f2f2f;
}

.cm-s-default .cm-def {
    color: #ffcc00;
    opacity: 0.8;
}

.cm-s-default .cm-builtin {
    color: #42aaff;
}

::-webkit-scrollbar {
    width: 8px;
	height: 4px;
    outline: none;
}
::-webkit-scrollbar-thumb {
    background: #0f0f0f;
    border-radius: 4px;
}
::-webkit-scrollbar-track {
    background: #1f1f1f;
}

.Sideblock_theme_normal .Sideblock-Content {
    background: #1a1a1a;
}

.language-css .token.string, .style .token.string, .token.entity, .token.operator, .token.url {
    background: none;
}

code[class*=language-], pre[class*=language-] {
    text-shadow: none;
}

.material__comment-heading, .material__note-heading {
    color: #1f1f1f;
}

.material__note p,
.material__note li {
    color: #3f3f3f;
}

.material__note var {
    color: #1d1d1d;
    font-weight: bold;
}

.material__note {
    border-radius: 10px;
}


.material__illustration[src*=svg] {
    filter: brightness(0.8) invert(1);
}
}
