css:
@-moz-document domain("facebook.com") {
*[dir=auto],
	
				*	{
		font-family: "Noto Naskh Arabic" , "Segoe UI" !important;
		
                                        }
}

@-moz-document url-prefix("https://twitter.com/"), url-prefix("https://tweetdeck.twitter.com/") {
*[dir=rtl],
	html[dir=rtl],
	div[dir=rtl],
	span[dir=rtl],
	p[dir=rtl],
	span,
	p,
	[dir=rtl],
 *{font-family: "Noto Naskh Arabic" , Segoe UI !important;
   		font-size: 100% !important;
		line-height: 110% !important; 
}
}
