css:
@-moz-document url-prefix("https://broskins.com"), domain("broskins.com") {
.p-body {
    background-color: #262626 !important;
}

.urnote {
    background-color: #3a3a3a !important;
}

.p-title-value {
    color: #a0271d !important;
}

.newcheck {
    border-top: #a0271d 4px solid !important;
}

.p-sectionLinks-inner {
    background-color: #a0271d !important;
}
.p-sectionLinks {
    background: #a0271d !important;
}

.p-sectionLinks-list .p-navEl-link, .p-sectionLinks-list .p-navEl-splitTrigger {
    color: white !important;
}

.block-container {
    color: #fff;
    background:#3a3a3a !important;
}




.percircle:after {
    background-color: #262626;
}
h1 {
    color: white;
}





.flex-container {
    display: flex;
    background-color: #262626;
}
.flex-container a {
    background-color: #262626;
    border: 1px solid #ddd;
    color: white;
}
.flex-container > div {

    color: #bcdef5;
}
html {
    background-color: #262626;
    color:  #bcdef5;
}




table.dataTable.compact tbody th, table.dataTable.compact tbody td {
    background-color: #3a3a3a;
}
table.dataTable.display tbody tr.odd > .sorting_1, table.dataTable.order-column.stripe tbody tr.odd > .sorting_1 {
    background-color: #313131;
}
table.dataTable.display tbody tr.even > .sorting_1, table.dataTable.order-column.stripe tbody tr.even > .sorting_1 {
    background-color: #262626;
}
.dataTables_wrapper .dataTables_length, .dataTables_wrapper .dataTables_filter, .dataTables_wrapper .dataTables_info, .dataTables_wrapper .dataTables_processing, .dataTables_wrapper .dataTables_paginate {
    color: #b7d8ef;
}
table.dataTable thead th, table.dataTable thead td {
    border-bottom: 1px solid #fff;
}
table.dataTable.row-border tbody th, table.dataTable.row-border tbody td, table.dataTable.display tbody th, table.dataTable.display tbody td {
    border-top: 1px solid #a0271d;
}
.dataTables_wrapper .dataTables_paginate .paginate_button {
    color: #666 !important;
}
.dataTables_wrapper .dataTables_length, .dataTables_wrapper .dataTables_filter, .dataTables_wrapper .dataTables_info, .dataTables_wrapper .dataTables_processing, .dataTables_wrapper .dataTables_paginate {
    color: #666;
}




table.dataTable{
    background-color: #3a3a3a;
}
table.dataTable tbody th, table.dataTable tbody td {
    background-color: #3a3a3a;
        border-color: white;
}

.block-container {

    border-top-color: #ffffff00 !important;
    border-right-color: #d8d8d800 !important;
    border-left-color: #d8d8d800 !important;
    border-bottom-color: #cbcbcb00 !important;
}
.block-header {
    background: linear-gradient(90deg, #232323, #a0271d);
    border-bottom: 1px solid #2577b1;
}
a {
    color: #44b2ff;
}



/*Whats New Page*/
.message-cell.message-cell--user, .message-cell.message-cell--action {
    background: #252323;
    border-right: 1px solid #2577b1;
}
.message-responseRow {
    background: #252323;
    border: 1px solid #252323;
}
.block-footer {
    border-top: 1px solid #2577b1;
    background: linear-gradient(90deg, #232323, #a0271d);
}
.block-filterBar {
    border-bottom: 1px solid #2577b1;
    background: linear-gradient(90deg, #232323, #a0271d);
}
}
