css:
@-moz-document domain("avaloniaui.net") {
:root {
    --bg-color: rgb(35, 35, 35);
    --fg-color: rgb(220, 220, 220);
    --border-color: rgb(100, 100, 100);
    --sides-bg-color: rgb(20, 20, 20);
    --link-normal-color: hsl(280, 43%, 60%);
    --link-hover-color: hsl(280, 43%, 50%);
    --note-bg-color: rgba(255, 229, 100, 0.1);
    --note-border-color: rgba(255, 229, 100, 0.3);
    --code-bg-color: rgb(50, 50, 50);
    --code-fg-color: rgb(185, 185, 185);
    --widget-fg-color: rgb(235, 235, 235);
    --widget-bg-color: rgb(45, 45, 45);
    --widget-hover-bg-color: rgb(70, 70, 70);
    --widget-border-color: rgb(70, 70, 70);
    --listitem-even-bg-color: rgb(45, 45, 45);
    --listitem-odd-bg-color: rgb(50, 50, 50);
    --listitem-hover-bg-color: rgb(70, 70, 70);
    --sidemenu-tree-active-bg-color: hsl(280, 35%, 20%);
}

body.layout-boxed {
    background: var(--sides-bg-color);
}

a {
    color: var(--link-normal-color);
}

a:hover, a:active, a:focus {
    color: var(--link-hover-color);
}

code {
    color:  var(--code-fg-color);
    background-color: var(--code-bg-color);
}

.content-wrapper, .main-footer, .content h1 {
    border-color: var(--border-color);
}

.main-footer {
    background-color: var(--border-color);
}

.wrapper, .content-wrapper, .main-sidebar, .left-side, .right-side {
    color: var(--fg-color);
    background: var(--bg-color);
}

.sidebar-menu li a:link, .sidebar-menu li a:visited, .sidebar-menu li a:active {
    color: var(--fg-color);
}

.note {
    background-color: var(--note-bg-color);
    border-color: var(--note-border-color);
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus,
.tab-content.platform-choice {
    color: var(--widget-fg-color);
    background-color: var(--widget-bg-color);
    border-color: var(--widget-border-color);
}

.nav-tabs {
    border-color: var(--widget-border-color);
}

.nav > li > a:hover, .nav > li > a:active, .nav > li > a:focus {
    color: var(--widget-fg-color);
    background-color: var(--widget-hover-bg-color);
    border-color: var(--widget-border-color);
}

.box {
    background-color: var(--widget-bg-color);
    border-color: var(--widget-border-color);
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-color: var(--widget-border-color);
}

.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: var(--listitem-even-bg-color);
}

.table-striped tr {
    background-color: var(--listitem-odd-bg-color);
}

.table-hover > tbody > tr:hover {
    background-color: var(--listitem-hover-bg-color);
}

.sidebar-menu li.selected {
    background-color: var(--sidemenu-tree-active-bg-color);
}
}
