css:
@-moz-document domain("ramweb.colostate.edu") {
/*
	RAMWEB HOME
*/


body {
	background-image: linear-gradient(#242424, #242424);
	color: #f6f3e8;
}

.content-box {
	background: #333;
}

a, a:active {
	color: #95e454;
}

#WelcomeLabel {
	color: #95e454;
}

.information-icon {
	color: #8ac6f2;
}

/*
	REGISTRATION
*/

.form-content-box {
	background: #333;
}

.Back-Button, .Back-Button:visited {
	color: #95e454;
}

/*
	TRANSFER CREDITS
*/

.table-striped > tbody > tr:nth-child(odd) > td, .table-striped > tbody > tr:nth-child(odd) > th {
	background: #454545;
}

/*
	GRADES
*/

.panel-body, .panel-footer {
	background: #454545;
}

/*
	FINANCIAL AID
*/

.WS-content-box {
	background: #333;
}

.WS-link-content {
	background: #454545;
}

select {
	color: #f6f3e8;
	background-color: #454545;
}

html {
	background: #454545;
}
}

@-moz-document domain("canvas.colostate.edu") {
#maincontent {
	color: #f6f3e8;
	background: #333;
}

#maincontent>div {
	background: #454545;
}

body h1, body h2, body h3, body h4, body h5, body h6, body .h1, body .h2, body .h3, body .h4, body .h5, body .h6 {
	color: #f6f3e8;
}


a, a:active {
	color: #95e454;
}

select, body div.azList .search .form-control {
	color: #f6f3e8;
	background-color: #454545;
}

div.azList .search .btn {
	color: #f6f3e8;
}

body div.azList .search .btn {
	background-color: #656565;
}

html {
	background: #454545;
}
}

@-moz-document domain("shibidp.colostate.edu") {
#maincontent {
	color: #f6f3e8;
	background: #333;
}

#maincontent>div {
	background: #454545;
}


a, a:active {
	color: #95e454;
}

h1 {
	color: #95d454;
}

h2 {
	color: #95e454!important;
}

h3 {
	color: #95c454;
}

h4 {
	color: #95a454;
}

legend {
	color: #f6f3e8;
}

input {
	color: #f6f3e8;
	background-color: #454545;
}

button {
	filter: invert();
}

html {
	background: #454545;
}
}

@-moz-document domain("uachieve.is.colostate.edu") {
/*
	DARS
*/
/* MAIN */
.btn-default {
	color: #f6f3e8;
	background: #555;
	border: 1px solid #f6f3e8;
}

.card {
	background: #f6f3e8;
}

#headerLogo>img {
	content:url("https://brand.colostate.edu/wp-content/uploads/sites/47/2019/02/CSU-Signature-rev.png");
	height: 63px;
	width: auto;
}

html, body, #header, #footerContainer {
	color: #f6f3e8;
	background: #333;
}

.bg-light {
	background: #454545!important;
}

/* Request audit page */
.card-header {
	background: #656565;
}

#currentProgram {
	background: #454545;
}

a {
	color: #8ac6f2;
}

.card-body {
	background: #454545;
}

.form-control {
	color: #f6f3e8;
	background-color: #333;
}

.verticalListing th, table tr th a, table tr th a:hover, .navarea a, .navarea a:hover {
	color: #f6f3e8;
}

/* Completed Audit Requests */
.table-striped > tbody > tr:nth-child(odd) > td, .table-striped > tbody > tr:nth-child(odd) > th {
	background: #656565;
}
.table-striped > tbody > tr:nth-child(even) > td, .table-striped > tbody > tr:nth-child(even) > th {
	background: #454545;
}

/* Audit results */
.main-menu .navbar-nav > .active > a, .main-menu .navbar-nav > .active > a:hover, .main-menu .navbar-nav > .active > a:focus, .main-menu .nav > li:hover {
	color: #151515!important;
	background: #ccc!important;
}

.flot-text, .graphOrLabel, .flot-y-axis>div {
	color: #f6f3e8!important;
}

#auditMenu a, #auditRequirements .requirement .toggler {
	color: #f6f3e8;
	background-color: transparent;
}

#auditRequirements .auditSubrequirements .subrequirement .subreqBody .completedCourses .takenCourse {
	background-color: #454545;
}

#auditRequirements .auditSubrequirements .course a {
	color: #d6d3e8;
}

.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
	color: #8ac6f2;
	background-color: #656565;
}

select {
	color: #f6f3e8;
	background-color: #454545;
}

.flot-base {
	filter: invert(20%) saturate(2) contrast(110%) invert() hue-rotate(190deg);
}

/* Transfer Coursework */

.groupByHeader td.title {
	background: #888!important;
	color: white;
}

/* Settings */
.dropdown-menu {
	background: #656565;
}
.dropdown-menu>li>a {
	color: #f6f3e8!important;
}
.dropdown-menu>li>a:hover {
	color: #333!important;
}
}

@-moz-document domain("catalog.colostate.edu") {
/* Programs */

#wrapper, #header {
	background: #333;
}

#logo>a>img {
	content:url("https://brand.colostate.edu/wp-content/uploads/sites/47/2019/01/CSU-Ram-357.png");
}

#school-text>a>img {
	filter: invert(23%) contrast(111%) invert() hue-rotate(140deg) saturate(6);
}

#school-text span {
	color: #65a434;
}

#main-nav > li > a, #cl-menu ul li a, #container h1.page-title {
	color: #95d454;
}

#breadcrumbs ul li a {
	color: #7aa64c;
}

#breadcrumbs ul li, #class-sched-search a {
	color: #609536;
}

#right-col {
	background: #454545;
}

#content th {
	background: #888!important;
	color: white;
}

tbody {
	background: #656565;
	color: #f6f3e8!important;
}

#content a {
	color: #e56d50;
}

/* program single */
#content {
	color: #f6f3e8;
}

#content tr.odd {
	background: #454545;
}

#content h1, #content h2, #content h3 {
	color: #f6f3e8;
}

/* Course popup bubble */
.lfjsbubble .lfjsbubbletopright, .lfjsbubble .lfjsbubbletop, .lfjsbubble .lfjsbubblemainwrapper, .lfjsbubble .lfjsbubblebottom, .lfjsbubble .lfjsbubblebottomright, .lfjsbubble .lfjsbubbletail {
	filter: invert(85%);
}

.lfjsbubble .lfjsbubblecontent {
	filter: invert();
	background-color: black;
	color: #f6f3e8;
}

a {
	color: #8ac6f2;
}

/* AUCC */

#content h4, #content h5, #content h6 {
	color: #f6f3e8;
}

/* key */
#content ul.letternav li a {
	color: #f6f3e8;
}

#content ul.letternav li.inactive {
	color: #858585;
}

/* search bar */
#search input.searchbox, #search label {
	color: #f6f3e8;
	background-color: #454545;
}

#search input.submit {
	background: #858585 url(https://catalog.colostate.edu/images/search-icon.png) no-repeat center center;
	background-size: 55%;
}

#search {
	background: transparent;
}

#print-icon {
	filter: brightness(1.5);
}
}
