css:
@-moz-document domain("webwork.uprm.edu"), domain("webwork.uprm.edu/webwork2") {
body
  { color: #fff;
    background: #202020;
  }

h1 {
    color:#fff;}
h2 {
    color:#fff;
text-shadow:0px 0px 0px;
font-weight: normal;}

b {
    color:#f6f6f6;
    font-weight: bold;
}

*[knowl] {
    
    border-bottom: 2px dotted #fff;
    
}

#toggle-sidebar.btn {color: white;
    background-image: linear-gradient(to bottom, #454a45, #454a45);
border-color: #575252;}


.breadcrumb {color:red;
    padding: 8px 15px;
    margin: 0 0 20px;
    list-style: none;
    background-color: #454a45;
    
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    border-color: #575252;}

.breadcrumb > .active {
    color: #fff;
    text-shadow: 0 1px 0 #2b2b2b;
}

a
{color:#5ec86e;
text-shadow: 0 1px 0 #2b2b2b;
font-weight: bold;}

a:hover, a:focus {
    color: #c9eabf;
}

tbody
{background-color: #454a45;
color:#fff;
border-color: #575252; 
padding: 0.5px;
    
border: 2px solid #575252;
 border-radius: 25px;}

.info-box
{color: #fff;
background-color: #454a45;
border-color: #575252; }

ul.nav li a:hover {color: #e2ffe2;
    background:#787878;
}

.info-box h2
{color: #fff;
background-color: #2f8831;
border-color: #575252; }

.btn-primary {
    color: #ffffff;
    background-color: #2f8831;
    background-image: none;
}

.btn-info {
    color: #ffffff;
    background-color: #254377;
    background-image: none;
}


div.ResultsWithError {
    color: #e64242;
    font-weight: bold;
    background-color: inherit;
}

div.correct-progress {
    height: 20px;
    color: white;
    background-color: #6ed16e;
    border: 2px solid #27b227;
    border-radius: 4px;
    display: inline-block;
    float: left;
    box-sizing: border-box;
    text-align: center;
}

.btn:focus {
    outline-style: solid;
    outline-color: #aaaa00;
    outline-width: 0px;
}

a:focus {
    outline-style: solid;
    outline-color: #aaaa00;
    outline-width: 0px;
}

table.attemptResults td {
    background-color: #5f665f;
    border-style: none;
    border-color:  #575252;
    border-width: 1px;
    
    border-spacing: 1px;
}

.popover-content { color: #2b2b2b;
}

table.attemptResults {
    border-style: outset;
    border-width: 1px;
    margin-bottom: 1em;
    border-spacing: 1px;
}

.table-bordered {
    border: 1px solid #4f4f4f;
    border-collapse: none;
    *border-collapse: none;
    border-left: 0;
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
}


table.attemptResults th {
    background-color: #5f665f;
    border-style: outset;
    border-color:  #575252;
    border-width: 1px;
    margin-bottom: 1em;
    border-spacing: 1px;
}

.nav-list > li > a {text-shadow:0 1px 0 #2b2b2b}

table.attemptResults td.FeedbackMessage {
    background-color: #cc9f24;
}
.parsehilight {
    color: inherit;
    background-color: #cc9f24;
}

table.attemptResults td.ResultsWithError {
    background-color: #bd1c1c;
    color: #000000;
}

table.attemptResults td.ResultsWithoutError {
    background-color: #28bd28;
}

span.ResultsWithoutError {
    color: #f2f1f1;
    background-color: #28bd28;
}

span.ResultsWithErrorInResultsTable {
    color: #f2f1f1;
    background-color: inherit;
}

ul.courses-list a {
    border: 1px solid #454a45;
    background: #575252;
}


#site-links ul {
    border: 1px solid #575252;
    background: #454a45;
    border-radius: 8px;
    padding: 0.5em;
}

.nav-list > .active > a, .nav-list > .active > a:hover, .nav-list > .active > a:focus {
    color: #ffffff;
    background-color: #2f8831;
}

.well-small {color: #fff; background: #3d423d; border-color: #575252;
}

textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    color: #fff;
    background-color: #5f665f;
    border: 1px solid #423f3f;
    -webkit-box-shadow: inset 0 1px 1px #423f3f;
    -moz-box-shadow: inset 0 1px 1px #423f3f;
    box-shadow: inset 0 1px 1px #423f3f;
    -webkit-transition: border linear .2s, box-shadow linear .2s;
    -moz-transition: border linear .2s, box-shadow linear .2s;
    -o-transition: border linear .2s, box-shadow linear .2s;
    transition: border linear .2s, box-shadow linear .2s;
}

*[knowl]:hover, *[knowl].active {
    border-bottom: 2px solid #3a3d3a;
    background: #606560;
    color: #cbffcb;
    padding: 0;
    margin: 0 0 0 0;
    border-radius: 3px;
    -moz-border-radius: 3px;

}

.knowl-output {
    background: #606560;
    border-left: 1px solid  #373333;
    border-right:  1px solid  #373333;
    border-bottom:  1px solid  #373333;
    border-bottom-left-radius: 10px;
    -moz-border-radius-bottomleft: 10px;
    border-bottom-right-radius: 10px;
    -moz-border-radius-bottomright: 10px;
    display: none;
    padding: 0px;
    margin-top: 10px;
    margin-bottom: 0px;
    margin-right: 0px;
}



.knowl-footer {
    position: relative;
    bottom: -10px;
    font-size: x-small;
    background: #5f665f00;
    color: #5f665f00;
    padding: 1px 0 1px 10px;
    margin: 0 0 0 0;}

img[src="http://webwork.uprm.edu/img/porticoUPRM.gif"] { 
    content: url("https://i.imgur.com/iNSa1da.png");
    width:150px;
    height:150px;
    margin-bottom:15px;
    margin-top:-5px;
    }
}
