/* Name: Gamespot - Awesome Dark(lulz)
 * Author: Neon-Tiger, but really it's just a very slightly touched Gamespot Opacity (by bededog, with help of the_foreign_guy and jakeboundville ^_^)
 * Updated: May 27, 2009
 * Description: Makes Gamespot all nice and dark, with Awesome Face watching over your posts. For the lulz.
 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamespot.com")
{
/****************************************
*										*
*										*
*	Opera Users: Start copying here		*
*										*
*										*
****************************************/



/* Changes the background of site */
html body:not([class="mceContentBody"]):not([class="bwp"])
{
    background: black !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
    background-image: url('http://fc04.deviantart.com/fs30/i/2008/123/5/8/Awesome_Face_Wallpapers_by_IcyAero.jpg') !important;
}



/****************************************
	Changes the background of various 
	objects to various shades of grey and black
****************************************/
#mini .users .friends li.friend, #legacy_layout.mini #content, #tradeshow_layout #content, #full_col_wrap, #feature_layout #content, #profile_layout #content, #siteblog_layout #content, #sky_layout #content, #mpu_layout #content, #promo, #container .ad_unit_glead, #container .ad_unit_leader, div.notice, div.notice .wrap, div.notice h2.corner, div#full_layout, div#video_promo, #col_wrap #main_col .module_head, #side_col .module_head, #image_index .module, .alt #content, #side .contain_singles .body li, #top_cheats ul.tabs a, .user_image_list .body ul.list_user_img li, #npd-module, .posted_comments .tabs li.on, #hd_player_wrap, table.cheats th, #more_videos .pagination ol li.on, #releasecalendar li, #releasecalendar .more,
.mini #main .blog_entry .body, ul.blog_entries, ul.profile_reviews, .tiled_items, #main ul.contacts li.contact, .story_body, #news_story p.deck, #moderation_layout #content, .moderation_head .msg, form.moderation fieldset, .blog_profile_banner, div.profile_games li.game,  #superleader .ad_unit,  #masthead .sh_wrap, #site_promos, #site_nav .sh_wrap, #mini .col_wrap .module, #site_footer, #site_footer ul.primary, .gs_story .deck, #gs_review .body .module, .cheat_details, li.cheat p, table.faqs tr td, ul.gameguides li.gameguide, table.updates tbody td, #summary_module .body, .table .sticky
{
     background: rgba(0, 0, 0, .7) !important;
}

div#tags_body, div#unions_module div.body, div.content_nav > ul.content_nav, 
     div[class~="contain_all"] > div.body, li.contain_all,
     div.module > div.body > ul.others, ul.topic_msgs * div.details, div.action_wrap,
     div.list_users_module > div.body, div.videos > div.body,
     #latest_news ul.stories, .module .foot, ul.stories, ul.downloads, ul.videos,
     #more_videos #filters, #more_videos #tabs, #more_videos ul.videos, #pop_cheats .pop_cheat,
     .add_comment form .actions, #col_wrap, div#primary_nav a.on, a.on > span, div#secondary_nav
{  
     color: white !important;
     background: rgba(50, 50, 50, .7) !important;
}

ul.comments li.comment, ul.comments ul.details li,
     tbody.sticky_topics > tr, tbody.sticky_topics > tr.alt, .comments .add_comment form .text_field,
     .topic_tag_field
{
    background: rgba(65, 65, 65, .6) !important;
}

div.author, form.edit_msg, form.edit_msg div.post_actions, .topic_tag_field
{
    border: none !important;
    background: rgba(50, 50, 50, .7) !important;
}



/****************************************
	Changes the background of various 
	objects into more boring greys
****************************************/
.board_topics th, table[class="table cb"] th, #full_col_wrap #main_col .module_head, .forum_poll .results div.perc, div.bb_quote > div.msg_head, ul.msg_actions * a, div.posted, ul.blog_actions * input, fieldset * input.submit, div#upload_video > div.body > div.wrap > ul > li > a, div.poll_choices * a, div.head, a.forum_button, span.submit, input.forum_button, ul.profile_actions * a, ul.profile_actions * span, #side_col .module, #side_col .bubble, .search_results .search_nav ul.content_types li, .list_users_module ul.users li.user, .topten .head ul.tabs a, #news_story .byline, .posted_comments .pagination, #gslive_schedule .shows h3, #filter_results table th, #browse_filters.updates ul.tabs li.on, #browse_filters.updates ul.tabs li, ul.games li.game .score_summary, #download_filter ul.downloads li.alt, #releasecalendar h4, #more_videos .tabs ul li, #more_videos .filters li.on, #video_promo_side, #more_videos .pagination, ul.profile_reviews .score, .gs_story .embscreen_large, .moderation_head .title, form.moderation .fieldset_head, #primary_nav, #bwhead, .useract_toolbar .useract_actions, #more_videos li.type a, ul.review_scores li.editor_score, ul.review_scores li.review_score, div.review_scores ul.actions li.about, div.review_scores ul.actions li.review a, #site_footer ul.site, .gs_story .byline, .profile_blog li.blog_entry_summary div.date:first-child, .post_msg_actions .submit, .post_msg_actions .preview, #masthead form.search span.field, #userpanel, form.edit_msg ul.post_msg_actions .reset, form.edit_msg ul.post_msg_actions .spell_check, #main .body div.embscreen
{
        border-color: transparent !important;
        color: white !important;
        background: rgba(30, 30, 30, .8) !important;
}

tbody[class="topics"] > tr.alt,
      ul.videos li[class="video alt"], .body ul.browse_categories li[class="browse_category alt"],
      .body ul.browse_categories li.browse_category.end, #top_cheats .body li.alt,
      tbody tr[class="alt"], .topten li.product, .topten li.product li.date, #side_col ul.episodes li.alt, .topten li li.score
{
      background: rgba(50, 50, 50, .5) !important;
}



/****************************************
	Changes the background of various
	objects into even more boring shades of boring grey
****************************************/
tbody[class="topics"] > tr, .feature_top ul.stories li.alt, .feature_top .details,
     ul.videos li[class="video"], #top_cheats .body li, #find_features .body li,
     .board_topics td.icon, .forum_poll, .forum_actions,
     .forum_module .board_description, #main_col .module .forums_nav, .pb10, .images .page_nav,
     .topten li.alt, .topten li.alt li.date, #side_col ul.episodes li, .topten li.alt li.score, .nowplaying_module .body li a.boxshot, .nowplaying_module .body li:first-child, .contacts_page_nav, .list_contacts .alpha_nav, #more_videos .content_filters, ul.review_scores li.review_score span.data, #site_footer ul.family, #npd-list tr:not([class="alt"])
{
     background: rgba(35, 35, 35, .2) !important;
}

form.edit_msg .field div.data div.field, #legacy_layout .pagination, #bwbox, #site_footer h1, #site_footer .promos
{
     border: none !important;
     background: rgba(35, 35, 35, .2) !important;
}



/****************************************
	Changes the background of various
	objects into transparent backgrounds
****************************************/
#content, #promo, #content_promo, #main, .mini #content_promo, #mini ul.content_nav li.on,
     .wrap has_crumbs, #forum_header, #gutter_top, div#main, ul.blog_actions * input,
     div#main div.blog_entry div.body > div.wrap, ul.content_nav > li.on, div.col_wrap > div,
     div#content_promo, .forum_actions .forum_actions_wrap, div#wrap, div.wrap  has_crumbs,
     div.crumbs forum_crumbs, div.body, div#forum_header, .contain_all .body, #shoulder,
     #main_col .module_head h2,  #side_col .module_head h2, .contain_singles .body li .wrap, .contain_all .body .body ul, #top_cheats ul.tabs, #top_cheats ul.tabs span, .search_results .search_nav, .search_results .search_nav ul.content_types span, .search_results .search_nav ul.content_types li a, #main .topten .head .wrap, .topten .head h2 em.gs, .topten .head ul.tabs span, .topten .head ul.tabs, .note,  #main_col, #main_col_wrap, #side_col .module .head ul.tabs, #content_wrap, .module .head h2, .posted_comments .page_nav, .userpanel_updates *, .posted_comments .tabs li.on span, #filter_results table tbody, #browse_filters.updates ul.tabs li span, #browse_filters.updates ul.tabs li a, #flash_video_player, table.cheats tbody, #hub_features .body ul.stories li.story, .contain_all .body ul, #more_videos .tabs ul li a, #more_videos .filters li.on a, #video_promo_side #now_playing, .playlist ul.videos, #video_promo_side.col, .forum_tags, #side .contain_singles .body li li, #main ul.contacts li.contact .wrap, .page_nav, #profile_layout .user_blogs ul.blogs li.blog, #site_nav, #site_header, #site_promos .sh_wrap, #masthead form.search, #masthead form.search fieldset, #primary_nav a, #primary_nav a span, #primary_nav ul, #masthead form.search span.btn, #masthead form.search fieldset .wrap, .useract_toolbar, #more_videos li.type span, div.review_scores ul.actions li.review a span, #site_footer .nav, #site_footer .promos ul, #site_footer .promos .wrap, #site_footer .gs .nav .wrap, .profile_blog li.blog_entry_summary .date .day, .profile_blog li.blog_entry_summary .date .month
{
     background: transparent !important;
}

body.standard all site_region_1, div#site_layout
{
        background: transparent !important;
}

div#gutter_top, #cnet_footer, #gutter_btm, #gutter
{
        background: transparent !important;
}

table.boards_hub tbody > tr.alt *, tbody.sticky_topics > tr.alt *,
     tbody.sticky_topics td.lastpost, .sticky_topics td.icon,
     table.board_topics td.first, table.board_topics td.icon, table.board_topics,
     table.board_topics tr.alt *, .layout .topic_msg, .bb_quote, .contain_all,
     tbody td, table, .choices *, .forum_actions .full_page_nav .actions,
     form.search input.submit, #masthead_actions fieldset, #masthead_actions form,
     .comments .add_comment form textarea, ul.comments ul.details li,
     form.edit_msg ul.post_msg_actions, div.stats_module li.profile_action span, .contain_all .body .body, #side_col .module .page_flipper 
{
    background: transparent !important;
}

#forum_poll, #show_messages .theader, .separator, .forum_tags *
{
     background: transparent !important;
     border: none !important;
}

#main_col .module .forums_nav, form.edit_msg ul.post_msg_actions,
     .comments .add_comment form .text_field, .add_comment form .actions, .topten .body ol
{
     border-color: transparent !important;
}

body
{
     background: transparent !important;
}



/****************************************
	Modifications to the header 
	area of site
****************************************/
div#masthead
{
        background: transparent !important;
}

div.mh_wrap
{
        background: transparent !important;
}

#userpanel
{
     height: 75px !important;
     width: 220px !important;
     margin-top: 0px !important;
     margin-right: 30px !important;
}

#userpanel .profile
{
     margin-top: 15px !important;
}

#site_nav
{
     border: none !important;
}

#superleader .ad_unit, #site_promos
{
    width: 980px !important;
    margin-left: auto !important;
    margin-right: auto !important;
}

#masthead .sh_wrap
{
     height: 100px !important;
}

#container_layout
{
     border: none !important;
     padding-left: 1px !important;
}

#masthead form.search span.btn button.submit
{
     background-image: url('http://i45.photobucket.com/albums/f60/bededog/searchIcon-1.png') !important;
     background-position: center !important;
     background-repeat: no-repeat !important;
}

#site_header .takeover
{
     display: none !important;
}



/****************************************
	This changes the color of various
	text found around the site
	(thanks for the code jakeboundville)
****************************************/
a.unionname, a.title, .module ul.actions *, ul.stories .deck a.more, #mini .module .body * a, ul.games li.game .score_summary dd.word, #more_videos .filters ul li a, ul.games li.game .score_summary a, #browse_filters ul.letternav a, ul.stories a, .module .body ul.details a, .profile_blog li.blog_entry_summary a, ul.downloads a, .contain_all .body ul a, .hub_boards_module a.title, ul.contacts li.contact .user_stats li a
{
        color: #a172ff !important
}

#container .title a, .user_blogs .stats .posted a, .user_blogs ul.blogs li.blog .wrap a, .topten ul.stats li.score a, .topten li .rank, #more_videos .tabs ul li a, #more_videos .pagination a, .userpanel_updates .content a, #profile_layout.mpu #side a, ul.profile_reviews li.review a, div.message * a, div#forum_header * a, div.board_description a, ul.pages * a, div.page_flipper > a, .layout .topic_msg .details .usersig a, ul.contacts li.contact a, #news_story a
{
     color: #c45bdb !important;
}

/**Sticky Topics link**/
/*Change links to topics to the original blue for unread messages in sticky topics */
tbody[class="topics sticky_topics"] > tr > td[class="topic"] > a[class="topic_title unread new_messages"] 
{
color: #f60 !important;
}

/*Change links to topics to the original blue for read sticky topics */
tbody[class="topics sticky_topics"] > tr > td[class="topic"] > a[class="topic_title read new_messages"]
{
color: #d50 !important;
}

/*Change links to topics to the original blue for unread sticky topics */
tbody[class="topics sticky_topics"] > tr > td[class="topic"] > a[class="topic_title read"]
{
color: #f60 !important;
}

/*Change link color for last user in sticky topic*/
tbody[class="topics sticky_topics"] > tr > td[class="lastpost"] > a
{
color: #d50 !important;
}

/*Change link color for number of posts in sticky topic*/
tbody[class="topics sticky_topics"] > tr > td[class="posts last"] > a
{
color: #d50 !important;
}

/**Normal Topics Link**/
/*Change links to topics to the original orange for unread topics */
tbody[class="topics"] > tr > td[class="topic"] > a[class="topic_title read"]
{
color: #f90 !important;
}

/*Change links to topics to the original orange for unread messages in topics */
tbody[class="topics"] > tr > td[class="topic"] > a[class="topic_title unread new_messages"]
{
color: #f90 !important;
}

/*Change links to topics to the original orange for read messages in topics */
tbody[class="topics"] > tr > td[class="topic"] > a[class="topic_title read new_messages"]
{
color: #d70 !important;
}

/*Change links to topics to the original blue for unread sticky topics */
tbody[class="topics"] > tr > td[class="lastpost"] > a
{
color: #d70 !important;
}

/*Change link color for number of posts in topic*/
tbody[class="topics"] > tr > td[class="posts last"] > a
{
color: #d70 !important;
}
/**End of Topic Links**/ 

/* Change links to orange for the reply and quote buttons*/
ul[class="msg_actions javascript_on"] > li > a
{
color: white !important;
}

/*Change usernames of basic users to white. Subscribers are not affected*/
div[class="username"] > a:not([style]), #masthead form.search input.text
{
color: white !important;
} 

/* Fixes some color issues with upload video thing */
div#upload_video > div.body > div.wrap > p, .module ul.details li, .userpanel_updates .feed table th, ul.friend_feed .date, .moderation_module .confirmation .confirmation_msg, #primary_nav a, #site_footer .promos, #site_footer .promos *, #main .site_blog .blog_entry h3, .alt #side .contain_singles .body li
{
        color: white !important;
}

/* Changes look of some text to ligher colors to be readable on light theme. */
.blog_post, div.story_body, div.deck, div.review_proscons, ul.boards * a, ul.unions * a, li.icon_key > span, div#tags_body > a, li.friend > div.wrap > a.username, div#forum_header * div, #main .blog_entry ul.details li.posted span, form.moderation fieldset, form.moderation fieldset.moderation_subject li, form.moderation fieldset.moderation_subject li span, form.edit_msg input.text
{
       color: white !important;
} 

/* Changes look of some text to lighter colors to be readable on light theme. */
div.feature_top > div.body, div.thumb * a, div#profile_layout * div.body, #npd-list td, #npd-list td a, .note, .module .head h2, .topten p.msg, .topten ul.stats .label, a.blog_title
{
       color: white !important;
}

/* Changes color of text saying message was edited(IE 'Edited on Nov 11, 2008 7:21 pm CT') The text has the same color as the message and it annoyed me! >=(*/
div.msg_revision
{
       color: gray !important;
       font-size: 10px !important;
}

table.board_topics *, .layout .topic_msg .author .status, #userpanel *
{
     color: #cccccc !important;
}

.alt .module .body p
{
     color: #cbcbcb !important;
}

.defaultSkin .mceLeft, table.mceLayout, table.mceLayout td
{
     background: white !important;
}



/****************************************
	This makes the sig area bigger
	and centers sigs again
****************************************/
.layout .topic_msg .details .usersig
{
      max-width: 800px !important;
      width: auto !important;
}



/****************************************
	This modifies the position and
	padding of some objects so 
	they look better with the theme
****************************************/
#masthead form.search
{
    margin-right: 75px !important;
}
#container_layout
{
    margin-top: -15px !important;
}
.forum_crumbs
{
    padding-top: 15px !important;
}
.story_body, .review_proscons, .gs_story .deck p, li.blog_entry_summary, #main .blog_entry .blog_post 
{
     padding: 7px !important;
}
#main .blog_entry .blog_post 
{
     margin-top: -10px !important;
}
#news_story p.deck
{
     margin-top: -15px !important;
     margin-bottom: 0px !important;
     border-bottom: 1px gray dotted !important;
}
.gs_story .deck
{
     margin-top: -15px !important;
     margin-bottom: 0px !important;
     border-bottom: 1px gray dotted !important;
}
.story_body .video_embed
{
     margin-left: -7px !important;
}



/****************************************
	This changes some images on the site
****************************************/
#content form.mini_search span.submit button, #site_footer .search button.submit
{
       background-image: url('http://i45.photobucket.com/albums/f60/bededog/search_icon_purple.jpg') !important;
}

/* Changes Gamespot logo */
.logo a
{
        background: none !important;
}
.logo
{
        background-repeat: no-repeat !important;
        background-image: url('http://i377.photobucket.com/albums/oo217/jachichorro/gs-awesomedark-logo.png') !important;
        margin-left: 25px !important;
}

.page_flipper a, #video_promo_side ul.actions li.play a span
{
     background-image: url('http://i45.photobucket.com/albums/f60/bededog/arrows-1.gif') !important;
}



/****************************************
	This is just some random stuff
	that needed to be changed
****************************************/
li[class="field preview"] .msg_wrap
{
     max-width: 770px !important;
     overflow: auto !important;
}

form.edit_msg .field div.data
{
     width: auto !important;
     max-width: 930px !important;
}

.tiled_items li.user_image 
{
      width: 138px !important; 
}
.tiled_items li.user_image img 
{ 
     height: 100px !important;
     width: 133px !important; 
}

.layout .topic_msg .details
{
      min-height: 150px !important;
}

div.boards_module > div.body
{
      border: 0px !important;
}



/****************************************
*										*
*										*
*	This replaces banned user page and	*
*	some error messages with some		*
*	cute and funny ASCII images			*
*										*
*	Moderators: It is suggested you		*
*	delete this part of code so it		*
*	doesn't interfere with your work	*
*										*
*										*
****************************************/
div.notice .user_banned
{
     background-image: url('http://i45.photobucket.com/albums/f60/bededog/dragon-1.png') !important;
     background-position: center !important;
     background-repeat: no-repeat !important;
     height: 360px !important;
}

div.notice .error
{
     background-image: url('http://i45.photobucket.com/albums/f60/bededog/error.png') !important;
     background-position: center !important;
     background-repeat: no-repeat !important;
     height: 360px !important;
}

div.notice > div.body > div[class~="wrap"] *
{
     display: none !important;
}

.error404 .body
{
     background-image: url('http://i45.photobucket.com/albums/f60/bededog/404error.png') !important;
     background-position: center !important;
     background-repeat: no-repeat !important;
     height: 400px !important;
}
    
.error404 .body > *
{
     display: none !important;
}
/****************************************
*										*
*										*
*	End of ban and error message changes*
*										*
*										*
****************************************/



/****************************************
*										*
*										*
*	This hides the side stuff on forum	*
*	pages and makes the forum topics	*
*	take up the entire space			*
*										*
*										*
****************************************/
div.forum_icon_key, div.friends_module, div.top_unions_module, div#tags_module,
    div[class="module contain_all forum_module boards_module"], div.ad_unit_mpu
{
      display: none !important;
}

div#mpu_layout div.board_topics_module
{
      width: 960px !important;
}
/****************************************
*										*
*										*
*	End of code that hides the side		*
*	forum stuff							*
*										*
*										*
****************************************/



/****************************************
*										*
*										*
*	Opera Users: Stop copying here		*
*										*
*										*
****************************************/



/****************************************
*										*
*										*
*	This rounds the corners of various	*
*	objects and buttons of site			*
*										*
*										*
****************************************/
#site_header
{
    margin-top: 15px !important;
}
#hotslot
{
    margin-left: 20px !important;
}
.module .head, #primary_nav a.on, #mini .module, .tabs li.on, #site_header #site_promos
{
     -moz-border-radius-topleft: 10px !important;
     -moz-border-radius-topright: 10px !important;
     border: 0px transparent solid !important;
}
.tabs li.on
{
     border-bottom: 2px !important;
}
.list_users_module ul.users li.user, #content a.forum_button, ul.msg_actions li.msg_action a, .layout .topic_msg .details .posted, div.bb_quote .msg_head, div.stats_module li.profile_action a, .profile_blog li.blog_entry_summary .date, div.useract_actions, ul.profile_reviews .score, ul.comments li.comment, .post_msg_actions .submit, .post_msg_actions .preview, form.edit_msg, .forum_actions .topic_actions, #summary_module .body, .embscreen_large, ul.review_scores li.editor_score, ul.review_scores li.review_score, div.review_scores ul.actions li.about, div.review_scores ul.actions li.review a, .posted_comments .pagination, form.site_search .wrap, #userpanel, form.edit_msg ul.post_msg_actions .reset, form.edit_msg ul.post_msg_actions .spell_check, #main .body div.embscreen, .forum_poll
{
     -moz-border-radius: 10px !important;
     border: 1px transparent solid !important;
}
.layout .topic_msg .author, #masthead form.search span.field
{
     -moz-border-radius-topleft: 10px !important;
     -moz-border-radius-bottomleft: 10px !important;
     border: 0px transparent solid !important;
}
#masthead form.search span.submit
{
     -moz-border-radius-topright: 10px !important;
     -moz-border-radius-bottomright: 10px !important;
     border: 0px transparent solid !important;
}
.bb_quote
