css:
@-moz-document domain("devszczepaniak.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #e03e33 #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #e03e33 !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #e03e33 !important;
}
input[type="text"]::placeholder,input[type="password"]::placeholder,input[type="email"]::placeholder,input[type="search"]::placeholder,input[type="submit"]::placeholder,textarea::placeholder,select::placeholder{
    color: #fff !important;
}
input[type="submit"]:hover{
    background: #e03e33 !important;
    color: #fff !important;
}
/*HEADER*/
.site-header{
    background: #1c1c1c;
}
.site-header img{
    background: #fff;
    border: 4px solid #e03e33;
    padding-left: 0px;
    max-width: 200px;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
button.cta-outline{
    background: linear-gradient(to right,#753a88,#cc2b5e);
    color: #fff;
}
.newsletter-form,.about-me-section,.contact-form-box{
    background: #1c1c1c;
    border-top: 1px solid #e03e33;
}
.blog-item{
    background: #1c1c1c;
}
.project-link{
    background: #1c1c1c;
    border: 1px solid #e03e33;
}
.project-link:hover{
    background: #e03e33;
    transition: 0.2s;
}
.project-link:hover a{
    color: #1c1c1c !important;
}
.owl-carousel .owl-nav .owl-next, .owl-carousel .owl-nav .owl-prev{
    background: #1c1c1c;
    border: 1px solid #e03e33;
    color: #e03e33;
}
.owl-carousel .owl-nav .owl-next:hover, .owl-carousel .owl-nav .owl-prev:hover{
    background: #e03e33;
    color: #1c1c1c;
    transition: 0.2s;
}
/*STRONA-GŁÓWNA*/
/*FOOTER*/
.external-links:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #e03e33;
}
/*FOOTER*/
}
