css:
@-moz-document domain("jeuxonline.info") {
/* Dark JOL par SophieAW*/

#content, html, body, #main-wrapper, .takeover #page, .btn-default, .pagination > li > a, .pagination > li > span {
    background: #121212;
    background-color: #121212;
}

.jol-tabs .jol-tab-active a, .jol-module{
    background-color: #292929;
    text-decoration: none;
    border: 1px solid #000000;
}

.jol-tabs li a:hover, .jol-module, #top-menu ul li a, #menu ul li a, .pagination > li > a, .pagination > li > span {
    color: #d0d0d0;
    text-shadow: none;
}

.tcat a:visited, .tcat_avisited, a:visited, body_avisited {
color: #b97e44;
}

a:hover, .tcat a:link, .tcat_alink, .thread-title, a.jol-text, div.titreForum a.lienTitre, div.titreForumFerme a.lienTitre, a:link, body_alink {
color: #cca73c;
}

.breadcrumbs a, .breadcrumbs a:hover, #header-tabs a, #header-tabs a:hover, .breadcrumbs strong {
    text-shadow: none;
    text-decoration: none;
}

.jol-module-title a, .jol-tabs .jol-tab-active a, .btn-default, .alt2 .citation, .alt2 .spoiler, .alt2 .jol-video-embed-title, .alt1 .citation, .alt1 .spoiler, .alt1 .jol-video-embed-title, .jol-contentfeed-meta, .jol-article-meta, .jol-text-light, a.jol-text-light, .jol-list-news .jol-list-news-meta {
    color: #e0e0e0;
    text-shadow: none;
}

.img-thumbnail, .jol-image, .jol_image, .jol_actus_image, .jol_actus_video, .jol_video, .jol-module-header, .jol-module-footer, blockquote, .alt2 .citation, .alt2 .spoiler, .alt2 .jol-video-embed-title, .alt1 .citation, .alt1 .spoiler, .alt1 .jol-video-embed-title, .jol-window .jol-window-head, .review-author {
    background-color: #404040;
    border: 1px solid #000;
}


ul.jol-list-messages:hover li.jol-list-item.comment:hover {
    background-color: #404040;
    border-bottom: 1px solid #000;
}


.jol-button, .jol-slider a.jol-slider-handle, .jol-contentfeed-meta, .jol-list-news .jol-list-news-meta, .jol-article-meta {
    border: 1px solid #000;
    background-color: #757575;
}

.jol-separator {
    border-bottom: 1px solid #000;
    background-color: #000;
}

#menu-site, #menu-sheet,  .jol-menu ul ul li, .jol-module-header .jol-tabs, .jol-window-body, #header-tabs a, #header-tabs a:hover, .thead {
    background-color: #7d7d7d; 
    text-decoration: none;
}

.comments-reply, .comments-login {
    border-top: 1px solid #e0e0e0;
    -moz-box-shadow: 0 2px 3px #ccc;
    -webkit-box-shadow: 0 2px 3px #ccc;
    background-image: -moz-linear-gradient(top,#8c8c8c,#353535);
    background-image: -webkit-gradient(linear,left top,left bottom,from(#8c8c8c),to(#353535));
    background-image: -webkit-linear-gradient(top,#8c8c8c,#353535);
}

.jol-form-input, .jol-form input, input.jol-form-input, .jol-form textarea, .jol-form select, .jol-form option, .ui-form input, .ui-form textarea, .ui-form select, .ui-form option, .select2-container--bootstrap .select2-selection, .select2-search--dropdown, .select2-container--bootstrap .select2-search--dropdown .select2-search__field, .select2-container--bootstrap .select2-results__option, .tcat {
    background-color: #4c4c4c;
    color: #e0e0e0;
}

.jol-table tbody tr:nth-child(odd), .jol-article table:not(.table) tbody tr:nth-child(odd) {
    background-color: #5a5a5a;
}

.jol-table tbody tr:nth-child(even), .jol-article table:not(.table) tbody tr:nth-child(even) {
    background-color: #3a3a3a;
}

.alt1, .alt1Active, .table-striped > tbody > tr:nth-of-type(2n+1), .tfoot {
    background: #5a5a5a;
    color: #e0e0e0;
}

.alt2, .alt2Active {
    background: #3a3a3a;
    color: #e0e0e0;
}

.vbmenu_control {
    background-color: #353535;
}

.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
    border-top-color: #000000;
}

.breadcrumb, .jol-navbar-more, .jol-navbar-more:hover, .modal-content, .nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover, .nav > li > a:focus, .nav > li > a:hover, .nav-pills > li.active > a, .nav-pills > li.active > a:focus, .nav-pills > li.active > a:hover {
    background-color: #565656;
}
}
