css:
@-moz-document domain("pinterestdlabiznesu.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #bc071c #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #bc071c !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #bc071c !important;
}
input[type="submit"]:hover{
    background: #bc071c !important;
    color: #fff !important;
}
.fa{
    color: #bc071c;
}
span{
    margin-top: 0;
    margin-left: 0;
}
/*HEADER*/
header#masthead.site-header,#masthead.affix .navigation,.search-form .search-submit:hover{
    background: #1c1c1c;
}
.navigation{
    background: #1c1c1c;
    border-top: 1px solid #bc071c;
    border-bottom: 1px solid #bc071c;
}
.navigation .width-navigation .inner-navigation .menu-right .search-form::after{
    color: #bc071c;
}
.navigation .width-navigation .inner-navigation .menu-right .search-form:hover::after{
    color: #1c1c1c !important;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
body #wrapper-container.bg-type-color,.panel-row-style{
    background: #1c1c1c !important;
}
.so-widget-sow-button-atom-865bea5c0821 .ow-button-base a{
    background: #bc071c;
}
.so-widget-sow-button-atom-865bea5c0821 .ow-button-base a span{
    color: #1c1c1c !important;
}
.woocommerce a.button,.thim-list-posts .link_read_more a{
    background: #bc071c;
    color: #1c1c1c !important;
    border: 1px solid #bc071c;
}
.so-widget-sow-button-atom-865bea5c0821 .ow-button-base a.ow-button-hover:hover,.woocommerce a.button:hover,.thim-list-posts .link_read_more a:hover{
    background: #1c1c1c;
    color: #bc071c !important;
}
.so-widget-sow-button-atom-865bea5c0821 .ow-button-base a.ow-button-hover:hover span,.sow-features-list .sow-icon-container.sow-container-round::before,.so-widget-sow-features-default-d127c727f6cc .sow-features-list .sow-features-feature h5,.sow-icon-fontawesome{
    color: #bc071c !important;
}
.panel-widget-style{
    border: 0 !important;
}
.thim-slider .flex-direction-nav a,.thim-slider .flex-direction-nav a:hover{
    border: 1px solid #bc071c;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.main-top,.tagcloud{
    background: #1c1c1c;
}
#back-to-top .fa-chevron-up{
    color: #1c1c1c;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.loop-pagination li .page-numbers{
    background: #1c1c1c;
}
.loop-pagination li .page-numbers:hover,.loop-pagination li .page-numbers.current{
    background: #bc071c;
}
/*LOGIN*/
.woocommerce button.button{
    background: #bc071c;
    color: #1c1c1c !important;
    border: 1px solid #bc071c;
}
.woocommerce button.button:hover{
    background: #1c1c1c;
    color: #bc071c !important;
}
/*LOGIN*/
/*PODSTRONY*/
/*FOOTER*/
footer#colophon{
    background: #bc071c;
    text-align: center;
}
footer#colophon:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #1c1c1c;
    cursor: none;
}
/*FOOTER*/
}
