css:
@-moz-document domain("deccanherald.com") {
header {
    position: fixed;
    width: 100%;
    z-index: 99999;
    background: #000;
    top: 0;
    left: 0;
}
body {
    background:black;
}
.hasBGColor .notab-snippets {
    background-color: #000;
}
.sanspro-semib {
    font-family:"Source Sans Pro", sans-serif;
    font-weight: 600;
    font-color:#666666;
}
.snippet-list-block__content {
    padding: 0 12px 9px 20px;
    float: left;
    clear: none;
    text-align: inherit;
    width: 75%;
    margin-left: 0;
    margin-right: 0;
    height: 60px;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    color:#fff;
}
.notab-snippets--graybg {
    background: #fff;
}
.node p {
    margin-bottom: 20px;
    font-size: 17px;
    color: #666666;
    line-height: 25px;
}
@media(min-width: 992px){
  .article-title h2, .article-title h1 {
    font-size: 36px;
    line-height: 40px;
    width: 80%;
    color: #666666;
  }}
@media(min-width: 768px) {
  .article-title h2, .article-title h1 {
    font-size: 30px;
    line-height: 34px;
    width: 80%;
    color: #666666;
  }}
.sanspro-b {
    font-family:"Source Sans Pro", sans-serif;
    font-weight: 700;
    color: #666666;
}
.hasBGColor .lg-vr-slides__bottom, .hasBGColor .sm-hr-card__content, .hasBGColor .lg-vr-card__bottom, .hasBGColor .nopic-card, .hasBGColor .notab-snippets {
    background-color: #000;
}
.lg-vr-slides__bottom, .lg-vr-card__bottom, .sm-hr-card__content {
    background-color: #000;
}
.notab-snippets {
    background-color: #000;
}
.notab-snippets--business {
    background: #000;
}
.sanspro-semib {
    font-family:"Source Sans Pro", sans-serif;
    font-weight: 600;
    color: #666666;
}
.secLightGray {
    background-color: #000;
}
.sq-horoscope-bg {
    height: 274px;
    background-color: #000 !important;
}
.field-name-field-tags .field-items .field-item {
    background: #666;
    display: inline-block;
    color: #666;
    padding: 10px 20px;
    border-radius: 3px;
    margin-right: 10px;
    margin-top: 10px;
}
a:-webkit-any-link {
    color: -webkit-link;
    cursor: pointer;
    text-decoration: underline;
    color:#cdcdcd;
}
#just-in-container {
    background-color: #000;
    padding: 0;
    height: 30px;
    border-bottom: solid 1px #666666;
}
}
