css:
@-moz-document regexp('https?://(www\\.)?ledevoir\\.com/.*') {
  body, main, nav, .editor .read-also, #articles.single .comments .comments-wrapper, #articles.single .comments .comments-wrapper .comments-list article p, #footer .footer-nav, #footer .footer-nav ul a span, #header .header-top, #header .header-top .header-articles-carousel, .no-backdropfilter #header .header-top .blur, #navigation > ul, #navigation > ul a, #navigation > ul > li > a, #sliding-panel, #navigation > ul > li > .sub-menu, #sliding-panel .actions, #navigation .categories, #header .header-top .btn-search, .dropdown .btn { 
      background-color: #333;
      color: #ccc;
  }

  a, p {
      color: #ccc;
  }

  #articles.single .comments .comments-wrapper .comments-list article h2, h4, .most-popular ul a h2, #articles.single .article-header.text-dark h1, #header .header-top .btn-open-sliding-panel, #header .header-top .header-articles-carousel h3 small, #header .header-top .article-reminder h2, #articles.featured .article h2 a, #footer .footer-nav h2, h2, .editor a, #articles.single .author-offset .author h3 a {
      color: #eee
  }

  div .editor .read-also, .editor blockquote, #articles.featured .featured-story.minimal, .card-blue, #footer .footer-search, #articles.featured .opinions, #articles.featured .family-articles, .extra, figcaption, .extra.encadre_chiffre, #articles.single.author-offset .author > h3 > a > span, .extra.encadre_pourcent {
      border-top-color: #ccc;
      background-color: #444
  }

  #articles.featured .featured-story.minimal, #articles.featured .featured-story.text-dark .intro time, #header .header-top .featured-articles ul a span, #header .header-top .header-articles-carousel h3 a span, #articles.single figure figcaption, #header .header-top .article-reminder small, #articles.featured .turquoise .category-articles .opinion header h3 a, #articles.featured .featured-story.text-dark .intro .weather .dropdown .btn, #articles.featured .featured-story.text-dark .intro .weather, .extra_legende, #articles.single .author-offset .author ul a span, .specs time {
      color: #bbb;
  }

  svg {
      filter: invert(100%) sepia(0%) saturate(1854%) hue-rotate(180deg) brightness(89%) contrast(79%); /* #ccc */
  }

  .brand svg {
      filter: invert(100%) sepia(0%) saturate(0%) hue-rotate(180deg) brightness(100%) contrast(150%); /* #ccc */
  }

  .breadcrumb a {
      border-bottom-color: #ccc
  }

  .electric-violet .editor a {
      color: #b1d;
  }
}
