css:
@-moz-document domain("plancke.io") {
body, html, .page-title-box, .footer, .progress, code, pre {
    background-color: #2F3136;
    color: #ddd;
}

.progress-bar-primary {
    background-color: #474A52;
}

.btn-primary {
    background-color: #1d1d25 !important;
    border: 1px solid #1d1d25 !important;
}

.btn-primary:hover, .btn-primary:focus {
    background: #3A3A4A !important;
    border: 1px solid #3A3A4A !important;
}

#sidebar-menu ul ul a {
    color: #ddd !important;
    background-color: #2F3136 !important;
}

.waves-ripple {
    background-color: #FFF5 !important;
}

.subdrop, #sidebar-menu > ul > li > a:hover, #sidebar-menu > ul > li > a.active, #wrapper.enlarged .left.side-menu #sidebar-menu ul > li:hover > a {
    border-left-color: #474A52 !important
}

td {
    border-bottom: 1px solid #7d7d7d;
    border-top: 0px;
}

.table > thead > tr > th {
    border-bottom: 2px solid #7d7d7d;
}

#sidebar-menu > ul > li > a:hover, #sidebar-menu > ul > li > a.subdrop {
    background-color: #2F3136 !important;
    color: #FFF !important;
}

#sidebar-menu ul ul a:hover {
    background-color: #202225 !important;
    color: white !important
}

.card-box, .portlet, .portlet .portlet-body, thead, .panel-footer {
    background-color: #32353B
}

.panel-default > .panel-heading, .panel {
    background-color: #32353B;
    color: #ddd !important;
}

.nvd3 text {
    fill: #fff !important;
}

h1, h2, h3, h4, h5, h6, .text-inverse, th, .text-primary {
    color: #ddd;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #42454d
}

tbody, .table > thead > tr > th {
    color: #ddd;
}

.input-group .form-control:last-child, .input-group-addon:last-child, .input-group-btn:first-child > .btn-group:not(:first-child) > .btn, .input-group-btn:first-child > .btn:not(:first-child), .input-group-btn:last-child > .btn, .input-group-btn:last-child > .btn-group > .btn, .input-group-btn:last-child > .dropdown-toggle, .form-inline .form-control, .form-control {
    background-color: #2F3136 !important;
    color: #ddd !important;
    border: 0px solid;
}

.btn-custom.btn-default, .dropdown-menu > li > a, .open > .dropdown-menu {
    background: #36393F;
    color: white !important;
    border: 0px;
}

.dropdown-menu > li > a:hover {
    background: #525760;
    color: white !important;
    border: 0px;
}

.open > .dropdown-toggle.btn-default {
    background: #36393F !important;
    color: white !important;
    border: 0px;
}

#wrapper.enlarged .left.side-menu #sidebar-menu ul > li:hover > a {
    background-color: #2F3136 !important;
}

.table-bordered > tbody > tr > td, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > td, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > thead > tr > th {
    border: 0px solid
}

table.table-bordered.dataTable > tr {
    border: 0.1px solid white !important;
}

.table-bordered {
    border: 0px;
}

.pagination > li > a, .pagination > li > span, .pagination > .disabled > a {
    background-color: #42454d;
    color: #ddd;
    border: 0px;
}

.content-page span {
    text-shadow: 1px 1px #000 !important;
}

.pagination > li > a:hover {
    background-color: #2F3136;
    color: #ddd;
}

.side-menu.left {
    background-color: #202225;
}

.pagination > .active > a, .pagination > .active > a:hover, .pagination > .disabled > a, .pagination > .disabled > a:focus, .pagination > .disabled > a:hover {
    background: #2F3136;
    color: #ddd;
}

.navbar-default, .topbar .topbar-left {
    background: #202225;
}

.progress-bar-success {
    background-color: hsl(223, 7%, 30%);
}

.panel-border.panel-danger .panel-heading {
    background: linear-gradient(to top, #32353B, #f82f2f44)
}

table code {
    font-family: inherit;
}

.badge-success {
    background-color: #00571b;
}

.badge-warning {
    background-color: #805500
}

.badge-danger {
    background-color: #91100d;
}

::-webkit-scrollbar-button {
    display: none;
}

::-webkit-scrollbar-thumb {
    background-color: #42454d !important;
}

::-webkit-scrollbar-thumb:hover {
    background-color: #595d69 !important;
}

::-webkit-scrollbar-track {
    background-color: #2F3136;
}

::-webkit-scrollbar {
    width: 13px;
}

input:-webkit-autofill, input:-webkit-autofill:hover, input:-webkit-autofill:focus, textarea:-webkit-autofill, textarea:-webkit-autofill:hover, textarea:-webkit-autofill:focus, select:-webkit-autofill, select:-webkit-autofill:hover, select:-webkit-autofill:focus {
    -webkit-animation-name: autofill;
    -webkit-animation-fill-mode: both;
}

@-webkit-keyframes autofill {
    to {
        color: #fff;
        background: #2F3136;
    }
}
}
