/* Custom Colours */

:root
{
    --SBBackground: /*[[SBBackground]]*/;
    --SBButton: /*[[SBButton]]*/;
    --SBButtonHover: /*[[SBButtonHover]]*/;
    --SBButtonActive: /*[[SBButtonActive]]*/;
}

/* ---------------------- BAK Defaults ----------------------- 

:root
{
    --SBBackground: #111111;
    --SBButton: #333333;
    --SBButtonHover: #444444;
    --SBButtonActive: #555555;

*/

/* ------------------------- Chrome ------------------------- */

::-webkit-scrollbar,
::-webkit-scrollbar-track,
::-webkit-scrollbar-corner
{
  background-color: var(--SBBackground);
  height: 15px;
  width: 15px;
}

::-webkit-scrollbar-button,
::-webkit-resizer,
::-webkit-scrollbar-thumb
{
  background-color: var(--SBButton);
  height: 15px;
  width: 15px;
}

::-webkit-scrollbar-button:hover,
::-webkit-resizer:hover,
::-webkit-scrollbar-thumb:hover
{
  background-color: var(--SBButtonHover);
}

::-webkit-scrollbar-button:active,
::-webkit-resizer:active,
::-webkit-scrollbar-thumb:active
{
  background-color: var(--SBButtonActive);
}


