  }
  .statusbar {
    background-color: #CFB082;
    border: 2px solid #896554;
  }
  #bbmenu.user ul.slidedown li ul.slidedownmenu li .ava {
    border: 1px solid #CFB082;
  }
  #bbmenu.user ul.slidedown li ul.slidedownmenu li .level p {
    color: #CFB082;
  }
  .xpvalue {
    background: -moz-linear-gradient(top,#E1AE52 0%,#C38343 100%);
  }
  
  /*Navigation*/
  .leftnav { 
    background: -webkit-linear-gradient(top,#5D3637 0%,#896554 100%);
    border-bottom-width: 2px;
    border-right-width: 2px;
    border-left-width: 2px;
    border-radius: 0 0 85px 85px;
    border-top-color: #896554;
    border-right-color: #896554;
    border-bottom-color: #896554;
    border-left-color: #896554;
    border-top-style: ridge;
    border-right-style: ridge;
    border-bottom-style: ridge;
    border-left-style: ridge;
    box-shadow: none;
    margin-left: -2px;
    width: 148px;
    height: 650px;
    margin-top: -1px;
  }
  .leftnav > ul > li > ul > li a:active, .leftnav > ul > li > ul > li a:link, .leftnav > ul > li > ul > li a:visited {
    color: #CFB082;
  }
  .leftnav > ul > li, .leftnav > ul > li a:active, .leftnav > ul > li a:visited {
    color: #CFB082;
  }
  .subtitle {
    font-family: Fredericka the great;
    color: #DC8459;
  }
  
  /*DP Banner*/
  .header-cta {
    background-color: #896554;
  }
  .header-cta h2 {
    color: #fff;
  }

  
  /*Body*/
  .center { 
    background-color: #CFB082;
  }
  body.background {
    background: #5D3637 url("https://i.imgur.com/Cr3Kn2J.jpg") no-repeat fixed 0 0/100% auto !important;
  }
  .container {
    background-color: #CFB082;
  }
  body.background {
    background: #5D3637 url("https://www.horsereality.com/images/body-background-blur.jpg") no-repeat fixed 0 0/100% auto;
  }
  
  /*Premium*/
  .premium-only {
    border: 1px #AF223B solid;
    background: #AF223B42;
  }
  h3 {
    color: #CFB082;
  }
  
  /*Lists*/
  .even { 
    background-color: #896554
  }
  .odd {
    background-color: #5D3637;
  }
  .subtop {
    color: #CFB082;
  }
  
  /*Pages*/
  .numbers {
    background: #896554;
  }
  .numselected {
    color: #CFB082;
    background: #5D3637;
  }
  
  /*Selection bars*/
  input, select { 
