css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("allegro.pl") {

#headerBox {margin-bottom: 0px !important;}

#left ul {padding-top: 0px !important;}
#left ul li a{
 border: none !important;
 display: inline !important;
 color: #3378C5 !important;
 font-weight: bold !important;
 text-decoration: none !important;
 padding: 0 18px !important;
}
#left ul li a:hover {text-decoration: underline !important;}

#left ul li a:hover{
 background: transparent !important;
}

#right h2 {
 height: 20px;
 font-size: 16px !important;
 font-family: "Calibri", verdana, arial !important;
 font-weight: bold !important;
}

#right h3 {
 font-size: 16px !important;
 font-family: "Calibri", verdana, arial !important;
 font-weight: bold !important;
}

#promoItems {margin-top: 10px !important;}

#smallShowCase1, #smallShowCase2{
display: none !important;
}

#promoItems div ul {
font-size:12px !important;
font-weight:bold !important;
padding:8px !important;
}
#footerContentBox{
display: none;
}

#left h2 {
 font-size: 16px !important;
 font-family: "Calibri", verdana, arial !important;
 font-weight: bold !important;
}

#left #searchBox, #left #searchBox form{
background: transparent !important;
color: black !important;
}

#left #searchBox{
 border-top: 1px solid #C8C8C8;
 height: 75px !important;
}


#left #searchBox h2{
 color: black !important;
 padding-top: 5px !important;
 font-weight: bold !important;
}

#left #searchBox a{
 font-size: 12px !important;
 color: black !important;
 vertical-align: bottom !important;
}

#left #searchBox #string{
 padding: 2px !important;
 width: 140px !important;
height: 19px !important;
 float: left;
margin-top: 0px !important;
}

#left #searchBox .form-submit{
 margin-top: 0px !important;
}

#left p.more {padding-top: 5px !important;}
#left p.more a {font-size: 12px !important;}

#left .corner{
display: none;
}

#right #watched a, #right #watched p {font-size: 12px !important; }

#pagecontent1 table.advContent {display: none !important;}
#tabMainBox td.cellHover {background: #FFFFCC !important;}

}
