@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document domain("sg.lyceum130.ru"){

:root {
--mainColor: black;
--background-image: url(https://w.wallhaven.cc/full/dg/wallhaven-dgzj9o.jpg);
--backgroundSize: 150% !important;
}    
    
/* Весь сайт */
/* Фон */



