body {
background-image: url(https://w.wallhaven.cc/full/dg/wallhaven-dgzj9o.jpg) !important;
background-position: top center !important;
background-attachment: fixed !important;
background-size: cover !important;
}
 

/* Лого ИРТЕX */
.header .block-logo, .header {
background: transparent;
}


.header .logo-school {
background: url(https://d.radikal.ru/d24/1809/6d/838073fb37fa.png) no-repeat;
background-size: 50px 50px !important;
-webkit-box-shadow: none;
box-shadow: none; 
border-radius: 0;     
}


html body .centered .entry-form>img {
opacity: 0;  
}

html body .centered .entry-form {
background: url(https://d.radikal.ru/d24/1809/6d/838073fb37fa.png) no-repeat;
background-position: center 2%; 
}


.footer .logo-footer {
background: url(https://d.radikal.ru/d24/1809/6d/838073fb37fa.png) no-repeat;

}


