} 

*/







/* Цвет текста */
.header p, .school .block-content .top-right-menu a, .header p, .label.control-label.col-md-4.col-lg-3.col-sm-4, label.control-label.col-md-4.col-lg-3.col-sm-4, div.col-md-12, .buttons-panel .buttons-panel-right button.btn, .buttons-panel .buttons-up button.btn{
color:white; 
text-shadow: 0.5px 0.5px 0 black, -0.5px -0.5px 0 black
}

/* Цвет Иконок в мини-меню */
/* 
.icon-bullhorn:hover, .icon-envelope mail:hover, .icon-comments-alt:hover, .icon-question-sign:hover, .icon-envelope:hover, .school .block-content .top-right-menu a:hover, .buttons-panel .buttons-panel-right button.btn span:hover, .buttons-panel .buttons-up button.btn span:hover, .buttons-panel .buttons-panel-right, div.buttons-panel-right{
color:#e8e8e8; 
}





/* Прозрачность */
.school .header, .school .header .navbar, .school .block-content h1, .footer, html body .centered .entry-form .sectiontable .form-horizontal, .school .btn-menu, .btn-menu .btn-menu-body {
background-color: transparent;
border: transparent;
color:white;
}

/* Полу-Прозрачность в разделе Войти */
a.button-login, html body .button-login, button-login-title, html body .row-center .button-login>.button-login-title, html body .row-center {
background:transparent;
border-color:transparent;
color:white;
font-weight:300;
border-radius: 550px
}

a.button-login {
background:transparent;  
}

html body .message-form .form-horizontal .two-lines-decoration {
display: none;
}

html body .message-form input.control-input.col-md-6, html body .message-form input.control-input.col-md-6:focus {
background-color: transparent;
background: transparent;
}


            
html body .centered .entry-form .tabs-form li {
background-color: rgb(12%, 12%, 12%, .5);
border-color:transparent;    
}

html body .centered .entry-form .tabs-form li.current {
background-color: rgb(12%, 12%, 12%, .5);
border-color:transparent;
}

html body .message-form {
background-color: rgb(12%, 12%, 12%, .5);
-webkit-border-bottom-right-radius: 25px;
-webkit-border-bottom-left-radius: 25px;
-moz-border-radius-bottomright: 25px;
-moz-border-radius-bottomleft: 25px;
border-bottom-right-radius: 25px;
border-bottom-left-radius: 25px;
border-color:transparent;
}

html body .centered .box-form {
background:transparent;
border:transparent;
}

html body .message-form .form-horizontal .select-main span, html body .message-form .form-horizontal .select-main select, html body #message .col-md-6 {
background:transparent;   
}

/* Окно с уведомлением  */
.alert-warning {
background-color: rgb(12%, 12%, 12%, .5);
background-color: rgb(12%, 12%, 12%, .5);
color: white;
border-color: white;    
}


table.table.table-bordered {
margin: auto;
margin-bottom: 15px;
}


/* Расписание */
table.schedule-table td.leftheader, table.schedule-table th.leftheader, table.schedule-table th.schedule-table-header {
background-color:transparent;
color:white;
}

.school .alert-info {
background-color: transparent;
color:white;
font-size: 12pt;
font-weight: 600;
}

