div.entry-form.loginbox.appear {
background: url(https://d.radikal.ru/d24/1809/6d/838073fb37fa.png) no-repeat;
} 

html body .centered .entry-form>img {
background: url(https://d.radikal.ru/d24/1809/6d/838073fb37fa.png) no-repeat;
margin:203;
}
*/

/* Расписание Год */
.school .year-calendar>li>a, .year-calendar li>div>div.vacation {
background-color: rgb(12%, 12%, 12%, .5);
}

.year-calendar li>div>div.weeknum a {
color:lime;
}

.year-calendar li>div>div.school-day a, .year-calendar, .year-calendar li>div>div:last-child {
color:white;
background:transparent;
}

.weeknum {
background:transparent;
}

/* Расписание Месяц */
.school a {
color:white;
}
table.schedule-table td.week {
background-color:transparent;
}

/* Расписание Неделя */
.table {
width:0;
}
    
table.schedule-table.table.table-bordered.table-condensed.print-block {
background-color: rgb(12%, 12%, 12%, .5);
}


/* Таблица прозрачная */
.school .table th, .table.table th, .bg-success {
background-color: rgb(12%, 12%, 12%, .5);  
}


/* Урок в дневнике */
tr {
background-color: rgb(12%, 12%, 12%, .5);  
} 


/* Помечается красным если долг */
tr[bgcolor="#FFBBBB"] {
background-color: rgb(255, 111, 111, .5);
}



/* МСОКО (Многоуровневая система оценки качества образования) - УДАЛЕН В СВЯЗИ С НЕСТАБИЛЬНОЙ РАБОТОСПОСОБНОСТЬЮ */
div.header:first-child > div.navbar.navbar-default:nth-child(4) > nav.navbar-inner > ul.nav.navbar-nav > li:last-child > a {
display: none;
}

/* Отчеты */
div#report, table.table-print th, tr.body {
background-color: rgb(12%, 12%, 12%, .5);
}

td.cell-text, td.cell-date, table.table-print {
background:transparent;
color:white;
}




/* Быстрый доступ (Белая полоса с функциями) */
.buttons-panel-fixed {
background-color: rgb(12%, 12%, 12%, .5);
}

/* Ресурсы */

/* Каталог ссылок */
.tree>li>span label {
background-color: rgb(12%, 12%, 12%, .5);
background: rgb(12%, 12%, 12%, .5);
}

/* Личный портфолио */
.tree li span .section-level {
background-color: rgb(12%, 12%, 12%, .5);
background: rgb(12%, 12%, 12%, .5);
color: white;
} 

.tree li span {
border-color: transparent;
}

.tree li span label {
background-color: rgb(12%, 12%, 12%, .5);
background: rgb(12%, 12%, 12%, .5);
color: white;
}

.tree li div p:last-child {
background-color: rgb(12%, 12%, 12%, .5);
background: rgb(12%, 12%, 12%, .5);
color: white;
}

.tree li.tree-group-item a {
background-color: rgb(12%, 12%, 12%, .5);
background: rgb(12%, 12%, 12%, .5);
color: #0169D3;
}

/* Почта */
div.jtable-main-container table.jtable thead, div.jtable-main-container table.jtable tbody>tr:hover, div.jtable-main-container table.jtable tbody>tr, div.jtable-main-container div.jtable-bottom-panel {
background-color: rgb(12%, 12%, 12%, .5);
background: rgb(12%, 12%, 12%, .5);
color:white;
}

div.jtable-main-container table.jtable tbody>tr.jtable-row-even {
background-color: rgb(12%, 12%, 12%, .5);
background: rgb(12%, 12%, 12%, .5);
}

form {
color: white;
}

/* Итоговые отметки */
table.table-print-num {
color: #fff;
}

table.table-print-num th {
background-color: transparent;
}

}
