css:
@-moz-document domain("www.tchap.gouv.fr") {
    .tc_Bottom_Links {
        margin: 0 0 10px 0;
        text-align: right;
        background: #333;
        color: #fff;
        border-top: 2px solid #61708b;
        padding: 2px 10px 0 0
    }
    .tc_Bottom_Links a {
        font-size: 0.8em;
        font-weight: bold;
        background: #333;
        color: #fff;
    }
    .tc_RepFr_logo {
        display: none;
    }
    .mx_RoomSubList {
        background: #333;
        color: #fff;
    }
    .mx_RoomTile_name {
        font-size: 1em;
        padding: 0 6px;
        color: #9d9d9c;
        white-space: nowrap;
        overflow-x: hidden;
        text-overflow: ellipsis;
    }
    .mx_SenderProfile_name {
        color: #afafaf !important;
        padding: 0;
    }
    .mx_EventTile .mx_SenderProfile {
        font-size: 0.9em;
    }
    .mx_DateSeparator {
        clear: both;
        margin: 4px 0;
        font-size: 18px;
        font-weight: bold !important;
        color: #c30498;
    }
    .mx_EventTile_highlight .mx_EventTile_content .markdown-body a.mx_UserPill_me,
    .mx_EventTile_content .markdown-body a.mx_AtRoomPill,
    .mx_EventTile_content .mx_AtRoomPill,
    .mx_MessageComposer_input .mx_AtRoomPill {
        color: #f2f5f8;
        background-color: #c30498;
        padding-right: 10px;
        margin: 0 5px 0 0;
    }
    .mx_EventTile_highlight,
    .mx_EventTile_highlight .markdown-body {
        color: #c30498;
    }
    .mx_EventTile_content .markdown-body a.mx_UserPill,
    .mx_UserPill {
        color: #fff;
        background-color: #c30498;
        padding-right: 5px;
    }
    .dark-panel {
        background-color: #333;
    }
    .mx_RoomSubList_label > span {
        text-transform: initial;
        color: #d1d1d1;
        font-weight: 700;
        font-size: 16px;
        margin-left: 8px;
    }
    .mx_RoomSubList:not(.resized-all) > .mx_RoomSubList_scroll.mx_IndicatorScrollbar_topOverflow::before {
        top: 0;
        transition: background-image 0.1s ease-in;
        background: #61708b;
        height: 2px;
        width: 100%;
        padding: 0;
        margin: 0;
    }
    .mx_ResizeHandle.mx_ResizeHandle_vertical > div {
        height: 1px;
    }
    .mx_ResizeHandle.mx_ResizeHandle_vertical > div {
        height: 2px;
    }
    .mx_ResizeHandle > div {
        background: #61708b;
    }
    .mx_RoomSubList_scroll {
        -ms-flex: 0 1 auto;
        flex: 0 1 auto;
        padding: 0;
    }
    .mx_RoomTile:hover {
        border-radius: 4px;
        background-color: #162d58;
    }
    .mx_RoomTile:hover .mx_RoomTile_name {
        font-size: 1em;
        color: #f7f7f7;
        white-space: nowrap;
    }
    .mx_RoomView_MessageList {
        list-style-type: none;
        padding: 18px;
        box-sizing: border-box;
        max-width: 1000px;
        margin: 1em auto;
    }
    .mx_RoomSubList_labelContainer {
        margin: 0 10px 0 0;
        height: 36px;
    }
    
    .mx_EntityTile {
    color: #9d9d9c;
    cursor: pointer;
}
    
}
