css:
@-moz-document url("https://app.roll20.net/editor/") {
body {
	background: url(http://www.aoaforums.com/forum/attachments/digital-image-photo-video-audio-editing/25136d1270228875-camoflage-seamless-texture-maps-free-use-camo_cloth_black_512.png);
}

::-webkit-scrollbar {
	-webkit-appearance: none;
	width: 8px;
	height: 8px;
}

::-webkit-scrollbar-thumb {
	border-radius: 4px;
	border: 2px solid rgba(0,0,0,0);
	background-color: rgba(0,0,0,.5);
}

#floatingtoolbar {
	background: #545454;
	width: unset;
}

#floatingtoolbar ul {
	display: flex;
	flex-direction: column;
}

#floatingtoolbar li {
	padding: 0;
	width: 1.5em;
	height: 1.5em;
	line-height: unset !important;
	color: #EEE;
}

#floatingtoolbar > ul > li {
	padding: .2em !important;
}

#floatingtoolbar li:not(.activebutton):hover {
	background: rgba(255, 239, 219, 0.3);
	color: #EEE;
}

#floatingtoolbar li.activebutton {
	background: #ffefdb;
}

.pictostwo {
	vertical-align: middle;
}

#floatingtoolbar div.submenu {
	top: -25%;
	left: 100%;
}

#floatingtoolbar #diceroller div.submenu {
	top: -450%;
}

#floatingtoolbar div.submenu ul {
	width: max-content;
	display: flex;
	flex-direction: column;
	border-radius: unset;
	border: 1px solid #666;
	box-shadow: 1px 1px 3px #666;
	background: #545454;
	padding: unset;
}

#floatingtoolbar div.submenu ul li {
	width: unset;
	height: unset;
	white-space: nowrap;
	padding: .5em .6em;
	font-size: 16px;
}

.pictosthree {
	vertical-align: middle;
	margin-right: .2em;
}

#floatingtoolbar div.submenu ul li.choosepan img {
	padding-top: 0;
}

#floatingtoolbar div.submenu ul.selZoom {
	animation: unset !important;
	width: 100%;
}

#floatingtoolbar div.submenu ul.selZoom li {
	padding: .2em 2em;
	text-align: center;
	line-height: unset;
	animation: unset !important;
	width: unset !important;
}

#editinglayer.objects div.submenu li:not(.gm_slider_box):not(:hover) {
	filter: hue-rotate(190deg) saturate(20%) brightness(141%);
}

#editinglayer.objects div.submenu li.chooseobjects:hover {
	background-color: rgba(255, 239, 219, 0.3);
	color: #EEE;
}

#editinglayer li.choosegmlayer>img,
#editinglayer li.gm_slider_box>img {
	opacity: unset;
	filter: brightness(4);
}

#editinglayer li.gm_slider_box:not(:hover) {
	color: unset;
	background-color: unset;
}

#zoompanel ul li.zoomClickBack {
	background: #ffefdb;
	color: #333;
	border: none;
}

#measure:not(.activebutton)>img, #measure li:not(.activerulermode)>img {
	filter: brightness(360%);
	opacity: unset;
}

#measure li.activerulermode {
	background: #ffefdb;
}

#diceroller div.submenu ul {
	width: 20vw;
}

#floatingtoolbar div.submenu ul li.quickdice {
	padding: .3em 0;
}

#floatingtoolbar li.quickdice .quickdice-button, #floatingtoolbar li.quickdice .quickdice-label, #floatingtoolbar li.quickdice .quickdice-toggle {
	padding: .3em 0;
	display: flex;
	justify-content: center;
	align-items: center;
}

#floatingtoolbar li.quickdice .quickdice-toggle:first-of-type {
	flex-basis: 25%;
}

#floatingtoolbar li.quickdice .quickdice-toggle:nth-of-type(2) {
	flex-basis: 4%;
}

#floatingtoolbar li.quickdice .quickdice-label {
	flex-basis: 25%;
}

#floatingtoolbar li.quickdice .quickdice-button:hover, #floatingtoolbar li.quickdice .quickdice-label:hover {
	background: #ffefdb;
	color: #333;
}

#floatingtoolbar li.quickdice:hover .quickdice-label:not(:hover) {
	color: unset;
	background-color: unset;
	cursor: unset;
	outline: solid 1px #ffefdb;
}

#diceroller div.submenu .rolltype, #diceroller div.submenu .pictos {
	line-height: unset;
}

.dicerollermenu {
	text-align: center;
}

#zoomclick:not(:hover) {
	opacity: .7;
}

.zoomValue {
	opacity: unset;
	text-shadow: unset;
}

#zoomclick {
	right: 40px;
	margin-right: unset;
	transform: translateX(-30px);
}

#zoomclick .btn {
	width: .8em;
	height: 1.1em;
	border-radius: unset;
	border: 1px solid #666;
	box-shadow: 1px 1px 3px #666;
	background: #545454;
}

.zoomplus:after, .zoomplus:before, .zoomminus:after, .zoomminus:before {
	background: #ffefdb;
}

.zoomplus:hover:after, .zoomplus:hover:before, .zoomminus:hover:after, .zoomminus:hover:before {
	background: #333;
}

#zoomslider {
	opacity: unset;
	border-radius: unset;
	border: 1px solid #666;
	box-shadow: 1px 1px 3px #666;
	background: #545454;
	border-top: none;
	border-bottom: none;
}

#zoomslider a.ui-slider-handle.ui-state-default.ui-corner-all {
	border-radius: unset;
	left: -.6em;
	border: 1px solid #666;
	box-shadow: 1px 1px 3px #666;
	background: #545454;
}

#zoomclick .btn:hover, #zoomslider a.ui-slider-handle.ui-state-default.ui-corner-all:hover {
	background: #ffefdb;
}

#sidebarcontrol {
	transform: translateX(-40px);
	top: 42%;
	width: 2em;
	height: 2em;
	border-radius: 50%;
	border: 1px solid #666;
	box-shadow: 1px 1px 3px #666;
	background: #ffefdb;
	color: #333;
	display: flex;
	justify-content: center;
	align-items: center;
	animation: unset;
}

body.sidebarhidden #sidebarcontrol {
	transform: translateX(-40px);
	color: #ffefdb;
	background: #545454;
}

#sidebarcontrol:hover {
	transform: translateX(-45px) !important;
}

#rightsidebar {
	background: #545454;
	border: unset;
	border-radius: unset;
}

#rightsidebar ul.tabmenu {
	background: unset;
	position: absolute;
	width: 100%;
	display: flex;
	align-items: center;
	height: 3em;
	top: 0;
	padding: unset;
	border-bottom: 1px solid #999;
}

.ui-tabs .ui-tabs-nav li {
	position: unset;
	flex-grow: 1;
	float: unset;
	top: unset;
	padding: unset;
	white-space: unset;
	height: 100%;
	transition: unset;
	border-radius: unset;
	outline: none;
}

#rightsidebar .ui-tabs-nav .pictos, #rightsidebar .ui-tabs-nav .pictostwo {
	outline: none;
	padding: unset;
	margin: unset;
	font-size: 2em;
	width: 100%;
	height: 100%;
	display: flex;
	justify-content: center;
	align-items: center;
}

#rightsidebar .ui-tabs-nav li:not(.ui-state-active) .pictos,
#rightsidebar .ui-tabs-nav li:not(.ui-state-active) .pictostwo {
	color: #ffefdb !important;
}

#rightsidebar .ui-helper-clearfix:after {
	content: unset;
}

.ui-tabs .ui-tabs-nav li:hover {
	background: rgba(255, 239, 219, 0.3);
	color: #ffefdb;
	border: unset;
}

.ui-tabs .ui-tabs-nav li a:hover {
	color: #ffefdb;
	background: unset;
	border: unset;
}

.ui-tabs .ui-tabs-nav li a {
	line-height: unset;
	border: unset;
	float: unset;
	border-radius: unset;
}

.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active {
	color: #333 !important;
	background: #ffefdb !important;
}

.ui-resizable-w {
	left: 0;
	width: 5px;
	background: transparent;
}

.ui-resizable-w:hover {
	transition: background 1s linear;
	background: linear-gradient(to right, rgba(255, 239, 219, 0.5), transparent);
}

.ui-widget-content {
	color: #ffefdb;
}

.textchatcontainer .message {
	padding-top: 7px;
	padding-left: 65px;
}

.textchatcontainer .avatar {
	top: 8px;
}

.textchatcontainer .message.general {
	color: #333;
}

.textchatcontainer .message.private {
	background: #674747;
}

.textchatcontainer .message .spacer {
	box-shadow: 1px 1px 3px #333;
	background: #ffefdb;
	height: 1px;
	width: 100%;
	position: absolute;
	top: 0;
	left: 0;
	margin: unset;
}

.textchatcontainer .avatar img {
	border-radius: 50%;
	max-width: 32px;
	max-height: 32px;
	box-shadow: 1px 1px 3px #333;
}

.textchatcontainer .by {
	vertical-align: middle;
}

.textchatcontainer .formula, .textchatcontainer .rolled {
	border: unset;
	border-radius: unset;
	padding: 6px;
	background: #666;
	color: #ffefdb;
	box-shadow: 1px 1px 3px #333;
}

.textchatcontainer .dicegrouping {
	display: inline-block;
}

.diceroll {
	display: inline-block;
	vertical-align: middle;
}

.diceroll .dicon {
	vertical-align: middle;
}

.diceroll .didroll {
	display: inline;
	vertical-align: middle;
	text-shadow: unset;
	color: #ffefdb;
	position: unset;
	min-height: unset;
}

.diceroll .backing {
	display: none;
}

#rightsidebar #textchat .content {
	padding-bottom: 30vh;
}

.textchatcontainer .rollresult.quantumRoll,
.textchatcontainer .message.you {
	background: unset;
	padding-left: 65px;
	color: #ffefdb;
}

.textchatcontainer .avatar {
	left: 15px;
}

.textchatcontainer .formattedformula {
	margin: 1em 0;
}

.textchatcontainer .rolled {
	margin-left: .5em;
}

.diceroll.critsuccess .didroll {
	color: #70d648;
}

.diceroll.critfail .didroll {
	color: #ff8484;
}

.textchatcontainer .message.system {
	background: #afd8ae;
	color: #333;
}

.textchatcontainer .message.you {
	background: #30506b;
}

.textchatcontainer .message:not(.system) {
	min-height: 2.5em;
}

.textchatcontainer .message.you ~ .message.you {
	min-height: 1em;
}

.textchatcontainer .message.whisper ~ .message.whisper {
	min-height: 1em;
}

.textchatcontainer .message.general:not(.you):not(.whisper):not(.system) ~
.message.general:not(.you):not(.whisper):not(.system) {
	min-height: 1em;
}

#textchat-input {
	background: #545454;
	border: unset;
	height: 130px;
	right: 6px;
}


input[type="text"] {
	padding-top: .5em;
	padding-bottom: .5em;
}

input[type="text"], input[type="number"], textarea {
	border-radius: unset;
	color: #ffefdb;
	border: 1px solid #ffefdb;
	background: unset;
	font-size: 16px;
	resize: none;
	margin-bottom: 1em;
}

input[type="text"]:hover, input[type="number"]:hover, textarea:hover {
	background: rgba(255, 239, 219, 0.3);
	border-color: transparent;
}

input[type="text"]:focus, input[type="number"]:focus, textarea:focus {
	background: #333;
	border-color: transparent;
	outline: none;
}

#textchat-input label {
	font-size: 16px;
	color: #ffefdb;
	margin: unset;
}

.btn, .btn.btn-default, .ui-dialog .ui-dialog-buttonpane button, .ui-dialog-buttonset .ui-button:nth-child(2) {
	padding: .5em 1em;
	text-shadow: unset
}

.macrobox .btn {
	color: #333 !important;
	text-shadow: unset !important;
}

#macrobar_macros {
	background: rgba(126, 123, 123, 0.5);
}

#speakingas, .btn, .btn.btn-default, .ui-dialog .ui-dialog-buttonpane button,
.ui-dialog-buttonpane .ui-dialog-buttonset .ui-button, select, select[disabled],
.zoomoptionsmenu .zoomin, .zoomoptionsmenu .zoomout, .zoomoptionsmenu .zoomdisplay, #jukebox .primarycontrols .btn,
#jukebox .secondarycontrols .btn, #page-toolbar .pages .availablepage .settings,
.chosen-container-multi .chosen-choices, .selectize-input {
	border: unset;
	border-radius: unset;
	background: #666;
	color: #ffefdb !important;
	font-size: 15px;
}

#speakingas:hover, .btn:hover, .btn.btn-default:hover, .ui-dialog .ui-dialog-buttonpane button:hover,
.ui-dialog-buttonpane .ui-dialog-buttonset .ui-button:hover, select:hover, select[disabled]:hover,
.zoomoptionsmenu .zoomin:hover, .zoomoptionsmenu .zoomout:hover, .zoomoptionsmenu .zoomdisplay:hover,
#jukebox .primarycontrols .btn:hover, #jukebox .secondarycontrols .btn:hover,
#page-toolbar .pages .availablepage .settings:hover, .chosen-container-multi .chosen-choices:hover,
.selectize-input:hover {
	background: rgba(255, 239, 219, 0.3);
}

#secondary-toolbar .tokenactions .btn:hover:not(:focus), #secondary-toolbar select:hover:not(:focus) {
	color: #333 !important;
}

#speakingas:focus, .btn:focus, .btn.btn-default:focus, .ui-dialog .ui-dialog-buttonpane button:focus,
.ui-dialog-buttonpane .ui-dialog-buttonset .ui-button:focus, select:focus, select[disabled]:focus,
.zoomoptionsmenu .zoomin:focus, .zoomoptionsmenu .zoomout:focus, .zoomoptionsmenu .zoomdisplay:focus,
#jukebox .primarycontrols .btn:focus, #jukebox .secondarycontrols .btn:focus,
#page-toolbar .pages .availablepage .settings:focus, .chosen-container-multi .chosen-choices:focus,
.selectize-input:focus {
	background: #333;
}

.btn, .btn.btn-default {
	text-shadow: unset;
	box-shadow: unset;
	line-height: unset;
}

.btn-danger {
	background: #c14545;
}

.btn-danger:hover {
	background: #e66363;
}

.btn-danger:focus {
	background: #a83333;
}

.folderroot .dd-content {
	font-size: 16px;
	display: flex;
	align-items: center;
	color: #ffefdb;
	font-size: 16px;
}

.folderroot .dd-content:hover {
	color: #ffefdb;
	text-decoration: underline;
}

.dd-list .journalitem .token, .dd-list .library-item .token {
	position: unset;
	width: 32px;
	text-align: center;
}

.dd-list .journalitem img, .dd-list .library-item img {
	max-width: 32px;
	max-height: 32px;
	box-shadow: 1px 1px 3px #333;
}

.dd-list .journalitem .name, .dd-list .library-item .name {
	width: unset;
	position: unset;
	margin-left: 1em;
}

.dd-item .namecontainer {
	position: unset;
	width: unset;
	height: unset;
}

h1, h2, h3, h4, h5, h6 {
	color: #ffefdb;
}

#rightsidebar .ui-tabs-panel:not(#textchat) .content {
	padding: 5% !important;
}

h3 {
	font-size: 20px;
}

h4 {
	font-size: 16px;
}

p {
	font-size: 15px;
}

#jukeboxwhatsplaying {
	border: 1px solid #ffefdb;
	padding: 2%;
}

#jukeboxwhatsplaying h4 {
	margin-bottom: .5em;
}

.playingitem {
	color: #ffefdb;
}

.playingitem em {
	font-size: 14px;
	color: #999 !important;
}

code {
	font-size: 1em;
	vertical-align: bottom;
	color: #d2355d;
	background: #ddd;
}

#existingmacros td, #existingmacros th {
	font-size: 15px;
}

label, input, button, select, textarea {
	font-size: 15px;
	color: #ffefdb;
}

input[type="image"], input[type="checkbox"], input[type="radio"] {
	margin: unset;
	vertical-align: middle;
	width: 16px;
	height: 16px;
}

.table {
	font-size: 14px;
}

.table thead tr th {
	font-size: 15px;
}

.table-striped tbody tr:not(:hover) {
	background-color: unset !important;
}

.table-striped tbody tr:hover {
	background: rgba(255, 239, 219, 0.3);
}

small, #rightsidebar small {
	color: #999;
	font-size: .9em;
}

.ui-dialog {
	background: #545454;
	color: #ffefdb;
}

.ui-dialog .ui-dialog-title, .ui-dialog .ui-dialog-titlebar-close span {
	color: #ffefdb;
}

.ui-draggable .ui-dialog-titlebar {
	border: unset;
	border-bottom: 1px solid;
}

.ui-dialog .ui-dialog-buttonpane {
	border: unset;
	background: unset;
	box-shadow: unset;
	border-top: 1px solid;
	text-align: right;
}

.ui-dialog .ui-dialog-titlebar-close {
	opacity: unset;
}

.ui-dialog .ui-dialog-titlebar-close span {
	filter: brightness(800%);
}

.zoomoptionsmenu {
	top: 14px;
	right: 100px;
}

.zoomoptionsmenu button {
	height: 2em;
}

.player .player-handle {
	bottom: -7px;
}

#imagedialog .searchbox {
	background-color: unset;
}

#imagedialog .tabcontainer a.active {
	color: unset;
}

#imagedialog #librarysearchroot .googleresult {
	background: unset;
}

#journal .searchbox input {
	width: 75% !important;
}

.dropzone {
	background: unset;
}

#initiativewindow ul li {
	background-color: unset;
}

.attributesabilities .abil {
	background-color: unset;
}

#sortrootjukeboxalpha {
	color: inherit;
}

#existingmacros .rollmacro {
	color: inherit;
}

#page-toolbar .handle {
	transform: translateX(-50px);
	top: 100%;
	border-radius: unset;
	border: 1px solid #666;
	border-top: none;
	box-shadow: 1px 1px 3px #666;
	background: #545454;
	color: #ffefdb;
	opacity: .7;
	z-index: 10800;
}

div#page-toolbar:hover div.handle:not(:hover) {
	opacity: .7;
}

#page-toolbar .handle:hover {
	opacity: 1;
	background: #ffefdb;
	color: #333;
}

#page-toolbar {
	top: 0;
	left: unset;
	width: 100%;
	border-radius: unset;
	border: 1px solid #666;
	border-top: none;
	box-shadow: #666 1px 1px 3px;
	background: #545454;
	color: #ffefdb;
}

#page-toolbar .pages div.availablepage:hover img.pagethumb {
	box-shadow: 0 0 10px #ffefdb;
}

#page-toolbar .activepage img.pagethumb {
	border-color: #355d7e;
}

.nav-tabs {
	border-bottom: 1px solid #ffefdb;
}

.nav-tabs>li>a, input {
	border: unset;
	border-radius: unset;
	background: #666;
	color: #ffefdb;
	font-size: 15px;
}

.nav-tabs>li>a:hover, input:hover {
	background: rgba(255, 239, 219, 0.3);
}

.nav-tabs>li>a:focus, input:focus {
	background: #333
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
	color: #333;
	background: #ffefdb;
}

.charactereditor .avatar, .charactereditor .defaulttoken {
	background: unset;
}

.charactereditor .avatar .inner h4 {
	color: #999;
}

.charactereditor .defaulttoken {
	width: unset
}

.tokenslot small {
	color: unset;
}

.textboxlist-bit-box {
	background: #355d7e;
	color: #ffefdb;
}

.note-editor.note-frame, .note-editor .panel-default>.panel-heading, .note-editor.note-frame .note-statusbar {
	background: unset;
}

.note-editor .panel-default>.panel-heading {
	padding-bottom: 0;
	width: unset;
}

.ui-dialog .ui-dialog-buttonpane {
	margin-top: -19px;
	position: relative;
	background: #545454;
	z-index: 2;
}
}
