css:
@-moz-document domain("metager.org"), domain("metager.de") {

#resultpage-container {
    margin: 8px;
    margin-top: 8px;
    margin-top: 0;
    display: grid;
    grid-template-columns: minmax(0, 150px) 800px 500px;
    grid-template-areas: "whitespace searchbar ." "whitespace foki ." "whitespace options ." "whitespace results additions" "whitespace footer .";
    grid-column-gap: 16px;
    grid-row-gap: 8px;
    justify-items: stretch;
    -webkit-box-align: stretch;
    -ms-flex-align: stretch;
    align-items: stretch;
    background-color: #202946;
}
body#resultpage-body,
body.quicktips {
    background-color: #202946;
}
.searchbar .search-input-submit {
    width: 100%;
    max-width: 600px;
    background-color: #374677;
    -webkit-box-flex: 1;
    -ms-flex-positive: 1;
    flex-grow: 1;
}
#research-bar-container #research-bar {
    background-color: #202946;
    border: 1px solid #202946;
    border-bottom-color: rgb(32, 41, 70);
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-bottom: 1px solid #ff8000;
    -webkit-box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 1px 0 1px 0 rgba(0, 0, 0, .24);
    box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 1px 0 1px 0 rgba(0, 0, 0, .24);
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    padding: 4px;
}
#resultpage-container #research-bar-container {
    grid-area: searchbar;
    padding-top: 8px;
    background-color: #202946;
    -webkit-box-shadow: 0 1px 3px 2px #fff, 1px 0 1px 2px #fff;
    box-shadow: 0 1px 3px 2px #202946, 1px 0 1px 2px #202946;
    position: -webkit-sticky;
    position: sticky;
}
#foki #foki-box {
    width: 100%;
    max-width: 800px;
    background-color: #202946;
    overflow-x: auto;
    padding: 8px;
    border-bottom: 1px solid #3b435c;
}
#foki #foki-box > div > a {
    color: #7f869f;
}
#foki #foki-box > div.active > a {

    border-bottom: 1px solid #6573ff;
    color: #7f869f;
}
#options {

    grid-area: options;
    background-color: #202946;
    max-width: 800px;
    padding: 0 8px;
}
#options #settings a {

    color: #a4a4a4;
}
body {

    font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #7f869f;
}
.result .result-body .result-description {

    -ms-flex-item-align: center;
    align-self: center;
    margin-bottom: 3px;
    color: #f4f5fb;
    font-size: .95em;
    line-height: 1.3;
    clear: both;
    overflow: hidden;
}
.result .result-header .result-headline .result-title a,
.result .result-header .result-headline .result-title a:active,
.result .result-header .result-headline .result-title a:focus,
.result .result-header .result-headline .result-title a:hover,
.result .result-header .result-headline .result-title a:visited {

    color: #a3b1ff;
    text-decoration: none;
}
a,
a:visited {

    color: #31b06e;
}
.result .result-footer .result-open,
.result .result-footer .result-open-newtab,
.result .result-footer .result-open-newtab:active,
.result .result-footer .result-open-newtab:focus,
.result .result-footer .result-open-newtab:hover,
.result .result-footer .result-open-newtab:visited,
.result .result-footer .result-open:active,
.result .result-footer .result-open:focus,
.result .result-footer .result-open:hover,
.result .result-footer .result-open:visited {

    color: #7f869f;
    font-size: .7em;
    text-decoration: none;
}
.result .result-footer .result-open-proxy,
.result .result-footer .result-open-proxy:active,
.result .result-footer .result-open-proxy:focus,
.result .result-footer .result-open-proxy:hover,
.result .result-footer .result-open-proxy:visited {

    color: #7f869f;
    font-size: .7em;
    text-decoration: none;
}
#research-bar-container #research-bar .resultpage-searchbar .search-input-submit .search-input input {

    border-bottom: 1px solid #374677;
}
.searchbar .search-input-submit .search-input #search-delete-btn {

    position: absolute;
    right: 0;
    bottom: 0;
    width: auto;
    height: calc(100% - 4px);
    margin: 2px 2px 2px 0;
    border: none;
    background-color: #374677;
    font-size: 1.8em;
    font-weight: 400;
    display: none;
}
#quicktips .quicktip {
    -webkit-box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 1px 0 1px 0 rgba(0, 0, 0, .24);
    box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 1px 0 1px 0 rgba(0, 0, 0, .24);
    padding: 8px;
    margin: 4px;
    position: relative;
    border: 1px solid #7f869f;
    background-color: #202946;
}
body {
    font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #a4a4a4;
}
body {
    -webkit-box-flex: 1;
    -ms-flex-positive: 1;
    flex-grow: 1;
    background-image: inherit;
    background-color: #202946;
    background-attachment: fixed;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    margin: 0;
    font-size: 1em;
}
.sidebar-opener {
    position: fixed;
    top: 22px;
    right: 25px;
    margin: 0;
    z-index: 20;
    font-size: 36px;
    line-height: 23px;
    border-radius: 5px;
    color: grey;
    background-color: #202946;
}
.sidebar {

    border-left: 1px solid #ccc;
    -webkit-box-shadow: 0 1px 1.5px 1px silver;
    box-shadow: 0 1px 1.5px 1px #797676;
    position: fixed;
    display: block;
    top: 0;
    right: -270px;
    width: 270px;
    height: 100%;
    padding-top: 55px;
    overflow: hidden;
    overflow-y: hidden;
    overflow-y: auto;
    z-index: 10;
    background-color: #202946;
    color: #000;
    -webkit-transition: .5s;
    transition: .5s;
}
.mg-pager > div {
    border: 1px solid #aaa;
    border-radius: 10px;
    padding: 5px;
    margin-left: 10px;
    background-color: #202946;
    -webkit-box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 1px 0 1px 0 rgba(0, 0, 0, .24);
    box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 1px 0 1px 0 rgba(0, 0, 0, .24);
}
#research-bar-container #research-bar .resultpage-searchbar .search-input-submit .search-input input {
    border-bottom: 1px solid #ccc;
    border-bottom: 1px solid #374677;
    color: #ddd;
}
}
