@-moz-document domain("rarbg.to"), domain("rarbgunblock.com"), domain("rarbg.com"), domain("rarbgprx.org"), domain("rarbgmirror.org"), domain("rarbgaccess.org"), domain("rarbgto.org"), domain("rarbgproxied.org"), domain("rarbgmirror.com"), domain("rarbgmirrored.org"), domain("rarbg.is"), domain("rarbgtorrents.org"), domain("rarbgproxy.org") {
body { color: #8798A5; font-family: trebuchet ms; background: #000; }
table.lista-rounded { color: #8798A5; background: #11131a; }
td.block, .black { color: #8798A5; }
.buttonalink { color: #8798A5!important; }
td.header5 { color: #8798A5; background: #11131a; }
a.anal, a.anal:link, a.anal:active, a.anal:visited { color: #8798A5; }
.searchTorrent, .formBox, .formBox2 { background: #00000d; border: 1px solid #000; margin: 0 0 5px 0; }
input, select { background: #11131a; border: 1px solid #000; color: #8798A5; }
td.header3 { color: #8798A5; background: #11131a; }
td.header3.selected { background: #1f1f4f; }
td.header3.selected a.anal { color: #8798A5; }
#pager_links a { border: 1px solid #000; color: #8798A5; }
#pager_links b { border: 1px solid #000; background-color: #1f1f4f; color: #8798A5; }
#pager_links a:hover { background-color: #1f1f4f; }
td.header6 { border-right: 1px solid #000; color: #8798A5; background: #00000d; }
td.header4 { color: #8798A5; background-color: #00000d; }
table.lista2t { border-bottom: 2px solid #000; border-left: 2px solid #000; }
table.lista2t tr.lista2:nth-child(2n) { background: #11131a; border: 1px solid #000; }
table.lista2t tr.lista2 td { border-right: 1px solid #000; }
table.lista2t tr.lista2 td { border-bottom: 1px solid #000; }
td.lista { color: #8798A5; }
table.lista2t tr.lista2:nth-child(2n+1) { background: #15171c; border: 1px solid #000; }
a:link, a:active, a:visited, td.header2 a { color: #A9894F!important; text-decoration: none; }
a:link:hover, td.header2 a:hover { color: #F2C064!important; }
span[style="color:DarkSlateGray"] { color: #8798A5 !important; }
table.lista tbody tr td.lista { border-bottom: 1px solid #000; }
table.header2, tr.header2, td.header2 { color: #8798A5; background: rgb(0, 0, 13); }
td[style*="border-bottom:1px solid #e5e5e5;"] { border-bottom: 1px solid #000 !important; }
div[style*="background: white;"] { background: #00000d !important; }
#comments_id textarea { background: #11131a; color: #8798A5; border: 1px solid black; }
.btn-primary { color: #8798A5; background-color: #000; border-color: #000; }
.btn-primary:hover, .btn-primary:active, .btn-primary.active, .btn-primary.disabled, .btn-primary[disabled] { color: #8798A5; background-color: #1f1f4f; }
td.header6:hover { background: #000; }
.lista-rounded tbody tr td table[style*="background:#fff;"] { background: #00000d !important; outline: 2px solid #000; outline-offset: -1px; }
font[style*="background: #555;"] { background: #00000d !important; color: #8798A5 !important; left: 6px!important; }
table[style*="border:1px solid #d8d8d8 !important;"] { outline: 2px solid #000; outline-offset: -1px; }
table[style*="border:1px solid #d8d8d8 !important;"]::after { content: ""; border-bottom: 1px solid #000; display: block; }
.catalog-post-content { border-top: 1px solid #000; }
.tvshowClickNotReleased { color: #8798A5 !important; }
.tvdivnotreleased { border-bottom: 1px solid #000; color: #8798A5 !important; }
.tvdivhidden, .tvdivshowed { border-bottom: 1px solid #000; color: #8798A5 !important; }
.tvdivhidden { background-color: #000; }
.tvshowClick, .tvshowClickNotReleased { color: #8798A5 !important; }
.tvdivhidden:hover { background-color: #0A0B0B; }
hr[style*="border:1px solid #DEDEDE;"] { border-bottom: 1px solid #000 !important; border-left: none!important; border-top: none!important; border-right: none!important; }
.tvdivshowed { background-color: #000; }
.lista_related:hover { background-color: #00000D !important; }
td.header5:hover, td.header3:hover { background: #1f1f4f; }
table[style*="border-bottom:1px solid #DEDEDE;"] { display: table-header-group; }
.ncomments_container .ncomments_comments .ncomments_comment .ncomments_body { color: #8798A5; }
.ncomments_container .ncomments_comments .ncomments_comment .ncomments_head { color: #8798A5; border-bottom: 1px dotted #000; }
.ncomments_container .ncomments_comments .ncomments_comment .ncomments_head .ncomments_user { color: #8798A5; }
.ncomments_container .ncomments_comments .ncomments_comment .ncomments_avatar img { filter: invert(); }
.ncomments_container .ncomments_showmore { background: #000; color: #8798A5; border-top: 1px dotted #000; }
.ncomments_container .ncomments_showmore:hover, .ncomments_container .ncomments_shownew:hover { background: #080809; color: #8798A5; }
.ncomments_container .ncomments_showmore.disabled:hover, .ncomments_container .ncomments_shownew.disabled:hover { background: #080809; color: #8798A5; }
.top100_filter_form { background: none repeat scroll 0 0 #00000d; border: 1px solid #000; }
.buttonalink:hover { text-decoration: none !important; }
h2[style="color:#444"] { color: #8798A5 !important; }
.jqmWindow { background-color: #000; color: #8798A5; }
hr[style*="border-top: 1px solid #ddd;"] { border-top: 1px solid #11131a !important; }
td[style*="background:#9FAABC;"] { background: #11131a !important; border: 1px solid black !important; color: #8798A5; }
td[style*="background:#9FAABC;"] table td { background: #11131a; }
table[bgcolor="#FFFFFF"] { background: #11131a; }

/* Png Star Rating DataURI*/
:root {
  --url: url("data:image/png;base64,");
}
.unit-rating li.current-rating { background: var(--url) left bottom; }
.unit-rating { background: var(--url) top left repeat-x; }
.unit-rating li a:hover { background: var(--url) left center; }
}
