css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cowlevel.net") {
body,
.setting-list,
.comment-list-sub-box {
    background-color: #F1F1F1 !important;
}
.box-shadow-0-8-16 {
    box-shadow: #000 0 8px 8px;
}
.header-nav {
    background-color: #F1F1F1 !important;
    opacity: 0.6;
    color: #112;
    transition: all 0.25s ease;
}
.header-nav:hover {
    opacity: 1;
}
.profile .nav-list li a {
    color: #112 !important;
}
.main,
.v2-main,
.v3-main {
    color: #224 !important;
}
.header-nav .logo a {
    color: #224 !important;
}
.beta {
    color: #224 !important;
}
.text-shadow3 {
    text-shadow: #111 1px 1px 1px;
}
.quick-href a {
    color: #112 !important;
}
.grey {
    color: #666 !important;
}
.grey5 {
    color: #112 !important;
}
.grey6 {
    color: #222 !important;
}
.gl-user-card-box .grey6,
.gl-user-card-box .white {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000;
}
.grey8 {
    color: #224 !important;
}
#search-result-box {
    background-color: #F1F1F1 !important;
}
#search-result-box a {
    color: #112 !important;
}
.search-game-result li:hover {
    background-color: #d1d1d1 !important;
}
.title-grey {
    color: #224 !important;
}
.white,
.white2,
.item-tit-a {
    color: #112 !important;
}
.part-right .item-tit-a {
    color: #fff !important;
}
.card-list .item-tit {
    color: #fff !important;
}
.gl-hover-text-3:hover {
    color: #448 !important;
}
.tab-left .vote-btn-v2 {
    color: #112 !important;
}
.vote-btn-v2:hover {
    background-color: rgba(255, 66, 95, .4) !important;
}
.gl-load-more-btn {
    background-color: rgba(17, 17, 34, .2) !important;
}
.line,
.line2,
.line3 {
    background: #112 !important;
    opacity: 0.3;
}
.h-tag li {
    border: 1px solid rgba(17, 17, 34, .3) !important;
}
.h-tag li.on,
.h-tag li:hover {
    box-shadow: 1px 2px 3px #666 !important;
}
.nav-list li::after {
    background: #f6f6f6 !important;
}
.notice-tip-window {
    background: #F1F1F1 !important;
}
.notice-tab-list li.on i {
    color: rgb(255, 66, 95) !important;
}
.notice-tab-list li.on .agree-tag {
    border-bottom: 14px solid rgba(255, 66, 95, .9) !important
}
.notice-tab {
    color: #112 !important;
}
.notice-tab li.weidu {
    background-color: #ddd !important;
}
.star-level-box .fa,
.clock-level-box .fa,
.star-color,
.star-list {
    color: #fa0 !important;
}
.header-nav input {
    background-color: rgba(39, 40, 57, 1) !important;
    box-shadow: inset 0px 2px 4px 0px rgba(0, 0, 0, 0.50) !important;
}
input::-moz-placeholder {
    color: #F1F1F1 !important;
}
.global-search-box i {
    color: #fff !important;
}
.global-search-box input {
    padding: 3px 12px 3px 35px;
}
.quick-href.on {
    background: rgba(0, 0, 0, .1) !important;
}
.repassword-box h2,
.notify-box h2 {
    border-bottom: 1px solid #224 !important;
}
.bg-gradient,
.bg-gradient-2 {
    background: -moz-linear-gradient(top, rgba(241, 241, 241, .1), rgba(241, 241, 241, 1)) !important;
    background: -webkit-linear-gradient(top, rgba(241, 241, 241, .1), rgba(241, 241, 241, 1)) !important;
}
.vote-btn-v3 .agree-tag {
    border-bottom: 14px solid #224 !important;
}
.grey3 {
    color: #112 !important;
}
.gl-hover-text {
    color: #224 !important;
}
.achievement-list span {
    color: #112 !important;
}
.tip-window::after {
    border-bottom: 16px solid rgba(241, 241, 241, 1) !important;
}
.tip-window {
    border: 1px solid rgba(255, 66, 95, .9);
    box-shadow: 1px 4px 5px rgba(0, 0, 0, .5);
}
.wangEditor-container .wangEditor-btn-container-btn-default {
    color: #224;
}
.wangEditor-container .wangEditor-btn-container,
.wangEditor-container .wangEditor-textarea,
.wangEditor-container .wangEditor-drop-menu,
.wangEditor-container .wangEditor-drop-menu a,
.wangEditor-modal-container .wangEditor-modal {
    background: #F1F1F1 !important;
    color: #224;
}
.wangEditor-container .wangEditor-btn-container-group {
    border-right: 1px solid rgba(0, 0, 0, .1);
}
.wangEditor-modal-container .wangEditor-modal-header-line {
    border-top: 1px solid rgba(0, 0, 0, .1);
}
.wangEditor-modal-container .wangEditor-modal input[type="text"] {
    color: #224;
    padding: 7px;
}
/* ****************************** */
.vm-feed-card-article,
.vm-feed-card-answer,
.vm-feed-card-reivew,
.vm-feed-card-question,
.vm-feed-card-game,
.vm-personal-card,
.vm-layer-more-user,
.vm-layer-more-user .vm-avatar {
    background-color: #f1f1f1;
    border: 1px solid #ccc !important;
}
.textdiv,
textarea,
.comment-box .textdiv,
.comment-box textarea {
    background: #F1F1F1;
    border: 1px solid #ccc;
}
.vm-btn {
    color: #233;
    border: 1px solid #ccc;
}

.comment-box textarea.comment_focus,
.comment-box .textdiv.comment_focus {
    box-shadow: none;
    color: #233 !important;
}
.vm-quick-href .quick-nav a[_v-64c951e6],
.vm-action-together,
.vm-action-together a,
.vm-avatar .avatar-intro,
.vm-personal-card .followers,
.vm-personal-card .user-intro,
.vm-personal-card .type-name,
.vm-personal-card .data-count {
    color: #233;
}
.vm-comment .comment-content[_v-61e65266] {
    color: #233;
}
[placeholder]:empty:before {
    content: attr(placeholder);
    color: #666;
    cursor: text;
}
.vm-feed-card-answer p,
.vm-feed-card-reivew p,
.vm-feed-card-question p,
.vm-feed-card-answer .desc,
.vm-feed-card-article .tab-brief-text,
.vm-feed-card-reivew .tab-brief-text,
.vm-feed-card-question .tab-brief,
.custom-theme-show,
.custom-theme .fr-view p,
.vm-paging .page-box {
    color: #233 !important;
}

.textdiv:focus {
    box-shadow: none;
}
.gl-user-card-box .grey6,
.gl-user-card-box .white,
.vm-game-vertical .color-white-6,
.vmpage-element-detail-game .element-search-game input {
    color: #233 !important;
    text-shadow: none !important;
}
.vm-style-title-underline[_v-5377752e],
.vmblock-gameV2 .vm-style-title-underline {
    color: #233 !important;
    border-bottom: 1px solid #666;
}
.vm-feed-card-answer .vm-btn.vm-btn-follow,
.vm-feed-card-answer .vm-btn.vm-btn-vote,
.vm-feed-card-reivew .vm-btn.vm-btn-vote,
.vm-btn.vm-btn-publish {
    border: 1px solid transparent !important;
    color: white;
    background-color: #fc425f;
    opacity: 0.8;
}
.vm-btn.vm-btn-vote:hover,
.vm-btn.vm-btn-publish:hover {
    border: 1px solid transparent !important;
    color: white;
    background-color: #fc425f;
    opacity: 1;
}
.vm-btn.vm-btn-vote .icon {
    color: white !important;
}
.comment-textarea,
.textdiv {
    color: #233;
}
.vm-comment .vm-btn-publish[_v-61e65266],
.vm-btn.vm-btn-follow.on {
    border: 1px solid transparent !important;
    color: white;
    background-color: #fc425f;
    opacity: 0.8;
}
.vm-comment .cancel-btn[_v-61e65266] {
    border: 1px solid #ccc !important;
}
.vm-btn.vm-btn-publish:hover {
    opacity: 1;
}
.header-nav input {
    background: rgba(0, 0, 0, .2) !important;
}
.gl-hover-text-white:hover {
    color: #233 !important;
}
.editor-content-box a,
div[contenteditable=true] a,
.comment-content a {
    text-decoration: underline;
    color: rgba(255, 66, 95, .9) !important;
}
.vm-comment .comment-reply-open[_v-61e65266] {
    color: rgb(255, 66, 95) !important;
}
.tag-box,
.vmblock-gameV2 .vm-element-item {
    border: 1px solid #ccc !important;
    background: #f1f1f1 !important;
}
.tag-box.on,
.vm-element-item.on {
    background: rgb(255, 66, 95) !important;
}
.vmblock-gameV2 {
    border: 1px solid #ccc !important;
    background: #f1f1f1 !important;
}
::-webkit-scrollbar-track-piece {
    background-color: #f1f1f1;
}
.vm-game-vertical .game-cover {
    box-shadow: 0px 1px 1px 0px rgba(0, 0, 0, 0.30);
    border: 1px solid #ccc !important;
}
.vm-paging .chevron-box.disable,
.vm-paging .chevron-box {
    color: hsla(0, 0%, 0%, 0.5);
}
.vm-paging .chevron-box:hover {
    color: hsla(0, 0%, 0%, 1);
}
.tip-game,
.vm-select,
.vm-select .vm-select-list,
.vm-rows-picker .picker-text-for,
.vm-rows-picker .picker-dropdown .picker-list {
    background: #f1f1f1 !important;
    color: #233;
}
.btn-unclicked {
    color: #233;
}
.vmpage-element-detail-game .filter-box .filter-item,
.vm-rows-picker .picker-dropdown .picker-list ul li,
.vmpage-element-detail-game .show-type-btn-box,
.vmpage-element-detail-game .show-type-btn-box.mr0 {
    color: #233;
}
.vm-game-vertical .color-star,
.vm-star,
.vmpage-element-detail-game .filter-box .filter-item.on,
.vmpage-element-detail-game .filter-box .filter-item:hover,
.vm-select:hover .vm-select-for,
.vm-select .vm-select-list .select-item:hover,
.vm-rows-picker .picker-dropdown .picker-list ul li.active,
.vmpage-element-detail-game .show-type-btn-box.on,
.vmpage-element-detail-game .show-type-btn-box:hover,
.not-played-box .change:hover,
.relative-user-box .change:hover {
    color: #fc425f !important;
}

.not-played-box .change,
.relative-user-box .change,
.not-played-box .change,
.relative-user-box .change,
.custom-theme-show h2,
.custom-theme .fr-view h2,
.custom-theme-show b,
.custom-theme-show strong,
.custom-theme .fr-view b,
.custom-theme .fr-view strong,
.custom-theme-show blockquote,
.custom-theme .fr-view blockquote,
.vm-rows-picker .picker-dropdown .picker-list ul .hint {
    color: #233 !important;
}
.custom-theme-show a,
.custom-theme .fr-view a {
    color: #fc425f;
}

.tip-window,
.tip-tag,
.bg-gradient-3,
.bg-server-pic,
.vm-element-item {
    color: #233;
    background: #f1f1f1 !important;
}

.swiper-cl-button-next,
.swiper-cl-button-prev {
    border: 1px solid rgb(14, 14, 14);
}

h1 {
    color: #233 !important;
}
.header-nav {
    display: none;
}
.related-tag,
.mt40,
.cl-item-bg {
    background: #f1f1f1 !important;
}
article.wrap-outer-padding {
    background: #f1f1f1 !important;
}

#right-spy .nav li p:hover {
    color: #d92e2e !important;
}
#right-spy li.active a {
    color: #233 !important;
}

.vm-edit-card-article {
    background: #f1f1f1 !important;
}
}

@-moz-document regexp("^[^.]*cowlevel.net/about") {
.main {
    color: #fff !important;
}
}

@-moz-document url("https://cowlevel.net/") {
.grey5 {
    color: rgba(255, 255, 255, .7) !important;
}
}
