/* ==UserStyle==
@name            Google Play Console Dark Theme - L@L
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Google Play Console Dark Theme
@author         L@L_PR@K@SH
==/UserStyle== */

@-moz-document url-prefix("https://play.google.com/apps/publish/") {
    /* Insert code here... */
    #loadingContainer{
        filter:invert(1);
    }
    .GNVPVGB-T-e,.GNVPVGB-V-m,.GNVPVGB-y-p,.GNVPVGB-G-p,.GNVPVGB-y-o.GNVPVGB-y-a{
            background-color: #1f1f1f;
    }
    .GNVPVGB-T-f {
        filter: invert(1);
    }
    .GNVPVGB-T-d nav a.gwt-Anchor,.GNVPVGB-f-m,.GNVPVGB-u-h,.GNVPVGB-v-e,.GNVPVGB-u-j{
        color: #7f7f7f !important;
    }
    .GNVPVGB-G-h {
        background: #000000 !important;
    }
    .GNVPVGB-j-U {
/*         background: #282828; */
        filter: invert(1);
    }
    .GNVPVGB-H-a {
        background-color: transparent !important;
    }
    img.gwt-Image {
        filter: invert(0);
    }
    .GNVPVGB-hd-a a[data-column="TITLE"] img,.GNVPVGB-E-b>img,.GNVPVGB-v-e::before,.GNVPVGB-u-j::before,.GNVPVGB-v-H::before,.GNVPVGB-v-M::before,.GNVPVGB-cp-a,.gwt-Image,.GNVPVGB-al-c,.GNVPVGB-P-h,.GNVPVGB-dq-d.GNVPVGB-dq-e,.GNVPVGB-E-b,.GNVPVGB-M-k,.GNVPVGB-j-z,input[type="text"] {
        filter: invert(1);
    }
    .GNVPVGB-E-b {
        background: black;
    }
    .GNVPVGB-E-b.GNVPVGB-j-E.GNVPVGB-al-m.GNVPVGB-E-g {
        background: black;
        filter: invert(1);
    }
/*     .gwt-viz-container>div>div>div{
        filter: invert(1);
    }*/
    .GNVPVGB-E-h.GNVPVGB-j-E.GNVPVGB-E-i .gwt-viz-container>div>div>div{
        filter: invert(12%);
    } 
    .gwt-viz-container>div>div>div{
        filter: invert(88%);
    }
    .GNVPVGB-Pm-i {
        filter: invert(45%);
    }
    .GNVPVGB-y-p .popupTopCenterInner,.GNVPVGB-Bb-o,.GNVPVGB-O-F {
        background-color: black !important;
    }
    .GNVPVGB-y-p .item-selected>div{
        background-color: black;
    }
    .app-grid > .expandable,.app-grid > * {
        color: #000;
    }
    .GNVPVGB-E-i, .GNVPVGB-E-g {
        color: #838383;
    }
    .GNVPVGB-J-f h2 {
        color: #848484;
    }
    .GNVPVGB-dp-a {
        filter: invert(15%);
    }
    .GNVPVGB-y-p .popupTopCenterInner, .GNVPVGB-Bb-o, .GNVPVGB-O-F {
        background-color: black !important;
        color: #1f1f1f;
    }
    .GNVPVGB-O-j, .GNVPVGB-O-i, .GNVPVGB-O-c {
        color: #989898;
    }
    .GNVPVGB-O-d:hover, .GNVPVGB-O-t:hover, .GNVPVGB-O-n {
        background-color: #1f1f1f;
    }
    .GNVPVGB-Eh-a.GNVPVGB-O-I tr:hover{
        background-color: #e0e0e0;
    }
    .GNVPVGB-J-d h3,.GNVPVGB-yj-h,.GNVPVGB-ti-g p {
        color: #8c8c89;
    }
    .GNVPVGB-Uc-f:hover{
        background-color: #e6e6e6 !important;
    }
    html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, hr, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video{
        color: #8c8c89;
    }
}
