css:
@-moz-document domain("github.com") {
.mr-3 {

    margin-right: 1px !important;
}
.px-2 {
    padding-left: 1px !important;
    padding-right: 1px !important;
}
.js-project-issue-details-container {
    padding-left: 4px !important;
}
.js-project-issue-details-container span.position-absolute {
    position: inherit !important;
    top: unset !important;
    left: unset !important;
}
.card-octicon div {
    display: inline !important;
}
.js-project-issue-details-container a.d-block {
    display: inline !important;
}
.js-project-issue-details-container span.d-block {
    display: inline !important;
    padding-right: 0 !important;
}
.js-project-issue-details-container small.d-block {
    font-size: 1px !important;
    display: inline !important;
}
.js-issue-number {
    font-size: 12px !important;
}
article.project-card > div > div > div.pl-5 {
    padding-left: 4px !important;
}
.card-note-octicon {
    display: none;
}
.project-column {
    min-width: 400px;
    max-width: 400px;
}

article[data-card-type*="pr"] {
    background-color: #eef !important;
}

article[data-card-state*="closed"] {
    background-image: linear-gradient(45deg, #fcfcf2 25%, #e8f3fa 25%, #e8f3fa 50%, #fcfcf2 50%, #fcfcf2 75%, #e8f3fa 75%, #e8f3fa 100%);
    background-size: 56.57px 56.57px;
}

.IssueLabel {
    height: unset !important;
    font-size: 10px !important;
    font-weight: normal !important;
}

.project-card .contains-task-list {
    margin-left: 0 !important;
}
article.project-card > div > div > div.pl-5 > div > small {
    display: none;
}
.project-columns .p-2 {
    padding: 0 !important;
}
.task-list-item-checkbox {
    margin: unset !important;
}
}
