css:
@-moz-document domain("web.algebranation.com") {
body {
	background: black;
}
.video-player-wrap {
	background: black;
}
.content-tabs .button-wrap *, .content-tabs button.active, .content-tabs button:hover {
	background-color: #000;
}
.the-wall-wrap .comment-area textarea {
	background: black;
	color: white;
}
#wall-posts .single-post, #wall-posts .single-post .new-comment-box {
	background: #2b2a2a;
	color: whitesmoke;
}
.float-left a{
	background: #a4a4a4;
}
#wall-posts .single-post .single-comment {
	background: black;
}
#wall-posts .single-post .single-comment:hover {
	background: grey;
}
 
tr {
	background: black !important;
	color: white;
}
.comment-form .disable_wall_msg::before {
	background: black;
}
.comment-form .disable_wall_msg {
	background: #3e3e3e;
	color: #cecece;
}
.modal-content, .modal-header {
	background: black !important; 
	color: white !important;
}
.leaderboard-wrap .leaderboard-table tr:nth-child(2n+1) td {
	background: #242424;
}
#leaderboard-pagination span.current.prev, #leaderboard-pagination span.current.next {
	background: black;
}
.btn-group>.btn:first-child {
	background: grey;
}
}

@-moz-document url-prefix("https://web.algebranation.com/testyourself") {
.questions-container {
	background: black;
	color: white;
}
.testyourself .questions-header, .testyourself .questions-header #questionPositionLabel {
	background: black;
	color: white;
}
.testyourself .container.main {
	background: black;
}
.testyourself {
	background: black;
}
}
