css:
@-moz-document url-prefix("https://www.mudah.my/") {
.header {
    border-top: 1px solid #000;
    padding-bottom: 33px;
    background-color: #1c1c1c;
}
.slick-initialized .slick-slide {
    display: block;
    background-color: #000 !important;
}
.content-bg {
    background-color: #000;
}
.prefooter-bg {
    background-color: #000;
}
.footer-bg {
    background-color: #000;
}
.header {
    border-top: 1px solid #000;
    padding-bottom: 33px;
    background-color: #000;
}
.landing-panel-fixed {
    background-color: #000;
    color: #fff;
}
.landing-panel-title {
    font-size: 38px;
    font-weight: 700;
    color: #fff;
    line-height: 44px;
}
.category-items > a:hover {
    border: 1px solid #fff;
    background-color: #000;
    border-radius: 10px;
}
.category-items > a:link {
    border: 1px solid #e21e30;
    background-color: #000;
    border-radius: 10px;
}
.modal-content {
    padding: 0;
    background-color: #000;
}
.select-location-row > div > a:hover {
    background-color: #111;
}
.modal-body {
    border-top: 1px solid #393939;
}
.entire-mal-kl {
    border-left: 1px solid #393939;
}
.icon-esc {
    background-color: #fd0000;
    padding-left: 10px;
    padding-bottom: 5px;
}
.icon-close {
    font-family: gotham-regular, Helvetica;
    font-size: 30px;
    font-weight: lighter;
    padding-right: 0px;
    background-color: #fd0000;
}
.close {
    float: right;
    font-size: 21px;
    font-weight: 700;
    line-height: 1;
    color: #000;
    text-shadow: 0 1px 0 #fff;
    opacity: 0.8;
}
body {
    background-color: #000;
}
.panel_container_super {
    background-color: #2d2d2d;
    border-bottom: 0px solid #2d2d2d;
}
#ContainerSuper {
    background-color: #000;
}
.panel_container {
    background-color: #2d2d2d;
}
#nav a:link, #nav a:visited, nav.navbar .dropdown-menu a:link, nav.navbar .dropdown-menu a:visited {
    color: #99f !important;
    font-size: 13px !important;
    font-weight: 400;
}
.main-container-for-search-menu {
    background-color: #000;
}
#search_form .SearchBarCenter {
    background-color: #2d2d2d;
}
.footerline {
    margin: 5px 0 0;
    border-bottom: 1px solid #2d2d2d;
}
.gallery_item {
    background-color: #000;
    border: 1px solid #2d2d2d;
}
.gallery_view_ad_btn {
    background-color: #2d2d2d;
    color: #8e8e8e;
}
.gallery .gallery_advertiser {
    background-color: #2d2d2d;
    color: #fff;
}
#ContainerMain, #ContainerIndex {
    background-color: #000;
}
element {
}
.list_big_thumbnail .thumbnail_images, .gallery .gallery_thumb {
    border-radius: 5px;
}
.gallery .gallery_details .gallery_thumb_container {
    border: 0
}
.gallery_item {
    background-color: #282828;
    border: 1px solid #2d2d2d;
}
.gallery_title_name {
    color: #828282;
}
.gallery_info, .gallery_param {
    color: #b5b5b5;
}
.complex_header {
    background-color: #2d2d2d;
    border-radius: 10px;
}
.left-top-container h2 {
    color: #737373;
}
div.content_gallery {
    border: 0px solid #dadbdb;
}
div#image-gallery {
    background-color: #2d2d2d;
    border-top: 0px solid #2d2d2d !important;
    border-bottom: 0px solid #2d2d2d !important;
    border-radius: 10px;
    border:0 !important;
}
.listing_info {
    background-color: #2d2d2d;
}
.left-top-container {
    width: 640px;
    background-color: #2d2d2d;
    border-radius: 10px;
}
.fader {
    background-image: none;
}
ul#other-items-gallery-thumbnail-items div ul li {
    min-height: 212px;
    width: 150px;
    border: 0;
    background-color: #2d2d2d;
    color: #fff;
    border-radius: 5px;
}
ul#other-items-gallery-thumbnails li a div.title, ul#more-items-gallery-thumbnails li a div.title {
    color: #959393;
}
.col-xs-9 {
    color: #b0b0b0;
}
ul#other-items-gallery-thumbnails li a div.price, ul#more-items-gallery-thumbnails li a div.price , .ads_price , .price_feature_ad {
    color: #ffd700;
}
.select-location-row > div > a:hover {
    color: #ffd700;
}
#list_ads_container .list_ads:hover {
    background-color: #151515;
}
#footerline_ad {
    border-bottom: 0px solid #e2e2e2;
}
}
