css:
@-moz-document regexp("https://www.bilibili.com/video/.*") {
.v-wrap {
    width:90vw!important;
    
}
.l-con{
    width:100%!important;
}
#bofqi {
    width:100%!important;
    height:calc(var(--screen-height) - 29px)!important;
}
#viewbox_report {
    position:absolute;
    top:calc(var(--screen-height) + 65px);
    z-index:12345;
}
#arc_toolbar_report{
    margin-top: calc(var(--title-height) + 10px);
}
#v_upinfo{
/*     background-color:#fff; */
/*     width: 100%; */
    padding: 10px 0 10px 0;
/*     box-shadow:0 0 5px 10px #fff; */
    position:absolute;
    top:calc(var(--screen-height) + 60px);
    right:360px;
    z-index:10;
}
.list-box {
    max-height:calc(var(--screen-height) - var(--top-distance))!important;
/*     max-height:100000000px!important; */

}
/* #reco_list{
    margin-top:calc(var(--profile-height) + 10px);
} */
#slide_ad {
    display:none;
}
.nav-link-item span a{
    display:none;
}
.r-con{
    width:390px;
}
#right-bottom-banner, #live_recommand_report{
    display:none;
}
.video-page-card{
    padding:2px 0;
}
.video-page-card .card-box .pic-box .pic{
    border-radius:0px;
}
}

@-moz-document regexp("https://www.bilibili.com/medialist/.*") {
.container{
    max-width:100vw!important;
    margin:20px 0;
    padding:0 20px!important;
}
#playContainer{
    width:99vw!important;
}
}

@-moz-document regexp("https://space.bilibili.com/.*/favlist.*") {
.n-inner,
.n-tab-links,
.n-btn,
.n-statistics,
.n-data {
    height: 35px!important;
    line-height: 35px!important;
}
.n-data {
    display: flex!important;
    flex-direction: row!important;
    width: 80px!important;
}
.n-data-v {
    margin-top: 0!important;
    margin: 0 0 0 10px!important;
    font-size: 16px!important;
}
.n-data-k,
.n-data-v {
    line-height: 16px!important;
}
.h-inner {
    padding-top: 12px!important;
}
.h-inner {
    display: none;
}
#page-fav .fav-sidenav .fav-list-container {
/*     max-height: calc(var(--screen-height) - 20px); */
    max-height:100000000000px;
}
.favList-info {
    display: none!important;
    margin: 0;
    padding: 0;
}
.wrapper {
    width: 100vw!important;
}
.n {
    margin-bottom: 0px;
}
#page-fav .fav-main {
    /*     width: 1300px; */
    width: var(--fav-main-width);
    background: var(--fav-bg);
}
.fav-video-list{
    display:flex;
/*     align-items:center; */
    justify-content:center;
    flex-wrap:wrap;
/*     height: calc(var(--screen-height) - 80px); */
    overflow: scroll;
}
.video-edit{
    margin-right:12px;
}
#page-fav .fav-main .small-item:nth-child(5n) {
    margin-right: 5px;
}
#page-fav .fav-main .small-item {
    padding: 0;
    margin: 0 5px 20px 0;
    border-radius: 0px;
    border: 0px solid #fff;
    box-sizing: border-box;
    
}
.small-item .cover {
    background: url(https://s1.hdslb.com/bfs/static/jinkela/space/asserts/video-placeholder.png) 50%;
    background-size: cover;
    border-radius: 0px;
    display: block;
    width: 160px;
    height: 100px;
    overflow: hidden;
    position: relative;
}
.small-item img {
    border-radius: 0px;
    display: block;
    width: 160px;
    height: 100px;
}
.be-pager {
    background-color: var(--fav-bg);
    position: fixed;
    bottom:-48px;
/*     bottom: -100px; */
    z-index: 5;
/*     background: #fff; */
    width: var(--fav-main-width);
/*     height: 100px; */
    padding: 10px 0 10px 0;
    /*     box-shadow:0 0px 20px -10px #00000029; */
}
.be-pager-item{
    line-height:25px;
    padding:0 8px;
}
.be-pager-next, .be-pager-prev{
    padding:0 8px;
}
.be-pager-item-jump-next, .be-pager-item-jump-prev, .be-pager-next, .be-pager-prev{
    line-height:25px;
}
.space_input{
    height:25px;
    line-height:25px;
}
.be-pager-options-elevator{
    height:27px;
    line-height:27px;
}
::-webkit-scrollbar {
    /*隐藏滚轮*/
    display: none;
}
.favList-info {
/*     position: sticky; */
    top: 40px;
}
.fav-sidenav {
    width: var(--sidenav-width)!important;
    height: var(--screen-height);
    overflow: scroll;
/*     position: fixed; */
}
.s-space {
    /*     width:100vw; */
    /*     height:100vh; */
    overflow: hidden;
}
#page-fav .fav-main .fav-header {
    margin: 0px 20px 0;
}
#page-fav .fav-main .search-input input{
    background: #fff;
}
.icon-ic_collect, .icon-ic_sub, .icon-ic_channel, .icon-ic_video, .icon-ic_following, .icon-ic_home, .icon-ic_setting{
    color:#808080!important;
}
#page-fav .fav-main .filter-item.search{
    background:#fff;
    border:none;
}
#page-fav .fav-main .search-types{
    border-right: 1px solid #eeeeee;
}
.fav-main{
    height:var(--screen-height);
    overflow:scroll;
}
}

@-moz-document domain("bilibili.com") {
:root{
    --player-width:1175px;
    --title-height:104px;
    --profile-height:100px;
/*     --screen-height:690px; */
    --screen-height:87vh;
    --top-distance: 136px;
    --sidenav-width:240px;
    --fav-main-width:calc(100vw - var(--sidenav-width));
    --fav-bg:#eeeeee;
}

}

@-moz-document domain("bilibili.com") {
.bili-banner{
    background-image:url(https://bing.rthe.net/wallpaper)!important;
}
}

@-moz-document domain("bilibili.com") {
/*顶栏简化*/
.nav-link-item *[target*="_blank"]{
    font-size:0!important;
/*     color:#fff!important; */
/*     font-weight:0; */
/*     height:0; */
/*     overflow:hidden; */
}
.bili-icon_dingdao_xiazaiapp{
    display:none;
}
.user-con .item:nth-child(2), .user-con .item:nth-child(7), .mini-upload {
    display:none!important;
}
}
