css:
@-moz-document domain("languagetools.io") {
body {
  background-color: #111 !important;
}

.navbar,.card,.community-post {
	background-color: #333 !important;
}

.navbar .navbar-brand {
	background-color: #b53471 !important
}

footer {
    background-color: #222 !important;
}

.text-black,.text-muted,.badge-light,* {
	color: #fff !important;
}

.bg-light {
	background-color: #444 !important;
}

.list-group-item,.form-control {
	background-color: #333;
}

.form-control:active, .form-control:focus {
	background-color: #444 !important;
}

.form-control:disabled, .form-control[readonly] {
	background-color: #555;
}

.Select, .Select-control  {
	background-color: #333 !important;
}
.Select-value {
	background-color: #333 !important;
}
.Select-menu-outer * {
	background-color: #333 !important;
    color: #FFF !important;
}
.Select-menu-outer *:hover {
	background-color: #555 !important;
}

.dropdown-menu {
	background-color: #333;
}

.list-group, .jumbotron {
	background-color: #000 !important;
}

.text-darkgray {
	color: #AAA !important;
}

.nav-tabs-dashboard .nav-link:focus, .nav-tabs-dashboard .nav-link:hover {
	background-color: #333 !important;
    color: #FFF !important
}

.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-item.true .nav-link, .nav-tabs .nav-link.active {
	background-color: #333 !important;
	color: #FFF !important;
}
.nav-tabs .nav-link:hover {
	background-color: #333 !important;
	color: #FFF !important;
}

.dropdown-item.active, .dropdown-item:active {
	background-color: #555 !important;
	color: #FFF !important;
}
.dropdown-item:focus, .dropdown-item:hover  {
	background-color: #444 !important;
	color: #FFF !important;
}

.list-group-item-action:active,.clickable:hover {
	background-color: #444!important;
}

.page-link {
	background-color: #444!important;
}

.badge-light {
	background-color: #444!important;
}

.text-info,.text-powder-blue {
	color: #CCC !important;
}

.bg-white {
	background-color: #000 !important;
}

.modal-content {
	background-color: #444 !important;
}

.sendIndex {
	background-color: #222 !important;
}

.RichEditor-root {
	background-color: #222 !important;
}

.RichEditor-editor {
	color: #FFF;
}

.public-DraftEditor-content {
	background-color: #222 !important;
}

.btn-light, .input-group-text, .btn-success-light {
	background-color: #222 !important;
}

.del, del {
	color: #b53471 !important;
} 

.col-sticky {
	background-color: #444 !important;
}

.vocabulary-nav .nav-link {
	background-color: #444 !important;
}

.btn-online-primary {
	background-color: #444 !important;
}

/** Reading tool - Specific **
/* default / known word*/
.reading-word-container {
    background-color: #555 !important;
	color: #FFF !important;
    line-height: 1.9em;
    font-size: 1.4em !important;
}

.reading-lesson-container  .badge.badge-pill.bg-white {
    background-color: #555 !important;
	color: #FFF !important;
}

.word-definitions-container .btn.bg-white {
    background-color: #555 !important;
	color: #FFF !important;
}

.word-definitions-translations-container .btn.bg-white {
    background-color: #555 !important;
	color: #FFF !important;
}


/* Unknown reading word */
.reading-word-container .bg-deep-orange {
    background-color: blue !important;
	color: #FFF !important;
}

.reading-lesson-container .badge.badge-pill.bg-deep-orange {
    background-color: blue !important;
	color: #FFF !important;
}

.word-definitions-container .bg-deep-orange {
    background-color: blue !important;
}
.word-definitions-translations-container .bg-deep-orange {
    background-color: blue !important;
}

/* Looked up / Learning reading word*/
.reading-word-container .bg-success {
    background-color: green !important;
}

.reading-lesson-container .badge.badge-pill.bg-success {
    background-color: green !important;
	color: #FFF !important;
}
.word-definitions-container .btn-success {
    background-color: green !important;
	color: #FFF !important;
}
.word-definitions-translations-container .btn-success {
    background-color: green !important;
	color: #FFF !important;
}
/* Selected reading word  */
.reading-word-container  .reading-word-selected {
    background-color: #000 !important;
}

/* Ignored reading word  */
.reading-word-container .bg-light-grey {
    background-color: #666 !important;
}

.word-definitions-translations-container .btn.bg-light-grey  {
    background-color: #666!important;
}

.word-definitions-container .btn.bg-light-grey {
    background-color: #666!important;
}

.word-definitions-container .text-danger {
	color: #FFF !important;
}

.text-danger {
	color: #FFF !important;
}

.alert-light {
    background-color: #222 !important;
}

blockquote,.blockquote {
    background-color: #222 !important;
}

.community-post-highlighted {
    background-color: #333 !important;
}

.badge-dark {
	background-color: #CCC; 
    color: #000 !important;
}

.badge-dark .text-powderblue {
    color: #000 !important;
}

div[data-test-id="dropTarget"] {
    background-color: #333 !important
}

#notificationsLinkColor a {
	color: #FFF !important;
}

.recharts-wrapper * {
	background-color: #333 !important;
    color: #FFF !important;
}
}
