css:
@-moz-document domain("geeksforgeeks.org") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.index-top_banner__card,
.main_wrapper .active,
.side--container_wscard .card-content,
.wp-pagenavi a:hover,
.wp-pagenavi span.current,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.responsive-tabs .responsive-tabs__list__item--active,
.responsive-tabs .responsive-tabs__list__item--active:hover,
.responsive-tabs .responsive-tabs__panel {
    background: #0e0e0e;
    border: 1px solid #282828;
    border-top: 1px solid #282828;
}

/*___ Fonts ________________________________________________________________________*/

.comment-content p,
.entry-content p,
.entry-summary p,
.mu_register p {
    font-family: arial;
}

.article--viewer_content .a-wrapper .content .text pre,
.comment-content h2,
.entry-content h2,
.leftbar-dropdown .dropdown-title,
.mu_register h2
{
	font-size: 3rem!important;
}

.article--container .articles-list_item .content .text,
.article--viewer_content .a-wrapper .content .text code,
.article--viewer_content .a-wrapper .content .text p,
.entry-content li, .entry-content p, .entry-content pre, .entry-content table,
.sideBar .sideBar--wrap .leftBarList li a
{
	font-size: 2.5rem!important;
}

.article--viewer_content .a-wrapper .content .text ul li:not(.responsive-tabs__list__item),
.article--viewer_content .a-wrapper .content .text ol li
{
	font-size: 1.92rem!important;
}

/*___ Remove Items _________________________________________________________________*/

.article--viewer .article-meta .vote-wrap,
.textBasedMannualAds_2,

[id*='GFG_AD_Desktop_']
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

#_fbn_._bsa_show ._bsa_fancybar {
	display: none!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.mega-dropdown__list-item .mega-dropdown {
    left: 98%;
}

.header-main__list-item .mega-dropdown__list-item>.mega-dropdown .mega-dropdown__list-item>.mega-dropdown {
    left: 98.15%;
}

#scrollTopBtn {
    right: calc(9% - 20px);
}

/*___ Margins ______________________________________________________________________*/

.article--container .articles-list_item .content,
.article--container .articles-list_item,
.card-layout
{
	margin: 1.2rem!important;
}

/*___ Paddings _____________________________________________________________________*/

.main_wrapper li a,
.wp-pagenavi a
{
	padding: 13px 26px!important;
}


}

@-moz-document domain("geeksforgeeks.org") {
/* Font Settings */

a[href],
a[href] strong
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.leftbar-dropdown .currentpage a,
.main_wrapper .active,
.rightbar--articles_container .content-wrapper_links li:before,
.sno:before,
.wp-pagenavi span.current,

a[href] strong:hover,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/*__________________________________________________________________________________*/

.article--container .articles-list_item .content .text,
.index-top_banner__card .right .flex-text,
.index-top_banner__card .right .text,

body,
li,
p
 {
	color: #fff!important;
}

/*___ Khaki ________________________________________________________________________*/

.gfg-footer .footer-wrapper_links-list li:first-child a,
.gfg-footer .footer-wrapper_links-list li:first-child,
.leftbar-dropdown .dropdown-title,

b,
h1,
h2
 {
	color: #F0E890!important;
}

/*___ Burlywood ____________________________________________________________________*/

h3,
h4,
i,
strong
 {
	color: #e0b880!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

.c-wrap .meta,
time
 {
	color: #07beff!important;
}

/*___ Green ________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/*___ Red __________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/*___ Misc _________________________________________________________________________*/

.syntaxhighlighter .plain, .syntaxhighlighter .plain a {
    color: #fff!important;
}

.syntaxhighlighter .keyword {
    color: #00aaff!important;
}

.syntaxhighlighter .string, .syntaxhighlighter .string a {
    color: #b0b0ff!important;
}

.syntaxhighlighter .color1, .syntaxhighlighter .color1 a {
    color: #ff8383!important;
}

.syntaxhighlighter .comments a,
.syntaxhighlighter .comments,
.syntaxhighlighter.night .comments a,
.syntaxhighlighter.night .comments
{
    color: #9e9e9e!important;
}

}

@-moz-document domain("geeksforgeeks.org") {
/* Background Settings */

#home-page,
#home-page,
.page .wrapper,

body,
html
{
	background: #000!important;
}

/*___ Background Gray ______________________________________________________________*/

.gfg-footer .footer-wrapper,
.header-sidebar__list,
.main_wrapper,
.header-main__slider,
.header-sidebar__wrapper,
.leftbar-dropdown
{
	background: #060505!important;
	background-color: #060505!important;
}

/*___ Background 0000 ______________________________________________________________*/

#_fbn_ ._bsa_fancybar,

.article--container .card-layout .content-wrapper,
.article--container .ql-list,
.article--container_content,
.header-main__slider ul,
.index-top_banner,
.index-top_banner__card,
.side--container_wscard .card-content,
.side--container_wscard,
.syntaxhighlighter .line.alt1,
.syntaxhighlighter .line.alt2,
.syntaxhighlighter.night .line.alt1,
.syntaxhighlighter.night .line.alt2,
.wp-pagenavi a,
.wrapper,

body #home-page,
body.page .wrapper,
html #home-page
{
	background: #0000!important;
	background-color: #0000!important;
}

/*___ Green ________________________________________________________________________*/

.consent-btn,
.products,
.runIdeBtn
{	
	background: radial-gradient(circle, #09b705a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.consent-btn::before,
.products::before,
.runIdeBtn::before
{
	background: radial-gradient(circle, #000 0%, #09b705 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.consent-btn:hover::before,
.products:hover::before,
.runIdeBtn:hover::before
{
  opacity: 1!important;
}

/*___ Plain Black __________________________________________________________________*/

.card-layout,
.widget-area
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/*___ Plain ________________________________________________________________________*/

.article--container .articles-list_item,
.article--container_content .right-top,
.article--recommended,
.article--viewer_content .a-wrapper .content .text blockquote,
.mega-dropdown
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.article--container .articles-list_item:hover,
.article--container_content .right-top:hover,
.article--recommended:hover,
.article--viewer_content .a-wrapper .content .text blockquote:hover,
.card-layout:hover,
.mega-dropdown:hover,
.widget-area:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

p[style="text-align:center"]
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/*__ Gray Gradient _______________________________________________________________*/

.leftbar-dropdown[style="background-color: var(--gfg-darkgrey); border-bottom: 1px solid rgb(204, 204, 204);"],
.header-main__wrapper
{
	background: linear-gradient(to bottom, #292929 0%, #000 100%)!important;
}

/*__ Code ___________________________________________________________________________*/

.syntaxhighlighter
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/*___ Highlight Background _________________________________________________________*/

.article--container .ql-list_item a:not(.no-hover):hover,
.currentpage,
.header-main__profile+.mega-dropdown li:hover,
.mega-dropdown__list-item.selected,
.sideBar .sideBar--wrap .leftBarList li:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Radial Background _________________________________________________________*/

.main_wrapper .active,
.wp-pagenavi a:hover, .wp-pagenavi span.current
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("geeksforgeeks.org") {
/* Image Settings */

.article--container .articles-list_item,
.article--container .ql-list_item a:not(.no-hover),
.article--container .ql-list_item,
.article--viewer_content .a-wrapper .content .text ol li,
.article--viewer_content .a-wrapper .content .text p,
.article--viewer_content .a-wrapper .content .text ul li:not(.responsive-tabs__list__item),
.card-layout,
.gfg--carousel_wrap-slide .slide-item_list .slide-item
 {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.article--container .articles-list_item:hover,
.article--container .ql-list_item a:not(.no-hover):hover,
.article--container .ql-list_item:hover,
.article--viewer_content .a-wrapper .content .text ol li:hover,
.article--viewer_content .a-wrapper .content .text p:hover,
.article--viewer_content .a-wrapper .content .text ul li:not(.responsive-tabs__list__item):hover,
.card-layout:hover,
.entry-content li:hover, 
.entry-content p:hover,
.gfg--carousel_wrap-slide .slide-item_list .slide-item:hover
 {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("geeksforgeeks.org") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}


}

@-moz-document domain("geeksforgeeks.org") {
/* Scrollbar Settings */

 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
