css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(webmail.pananess.com),
 domain(webmail.pananess.com)
{
   /* general */
  html,
   body,
   div,
   ul,
   table
  {
    color: #797979 !important;
    background: none #232323 !important;
  }

  *
  {
    border-color: #555555 !important;
  }

  input,
   textarea
  {
    color: #191919 !important;
    background-color: #373737 !important;
  }

  a.active,
   td.add-button a,
   span.googie_check_spelling_link
  {
    color: #007700 !important;
  }

  div#taskbar a
  {
    color: #797979 !important;
  }

  div#taskbar a:hover
  {
    text-decoration: underline !important;
  }


   /* Folders */
  
   .listbox .listitem.selected, .listbox .tablink.selected, .listbox .listitem.selected > a, .listbox .tablink.selected > a, .listing tbody tr.selected td, .listing li.selected, .listing li.selected > a
   {
     background-color: #555;
     color: #fff;
   }
 
   .listbox .listitem a, .listbox .listitem span, .listbox .tablink a, .listing tbody td, .listing li a
   {
     color: #fff;
   }
 
   ul.listing li
   {
     background: #616161;
   }
 
   .listbox
   {
     background-color: #616161;
   }
 
   .listbox .boxfooter
   {
     background-color: #616161;
   }
 
   .boxfooter .listbutton
   {
     background-color: #616161;
   }
   

   /* preferences */
  div#userprefs-title
  {
    border-color: #555555 !important;
    color: #797979 !important;
    background: none #191919 !important;
  }

  div#tabsbar span.tablink
  {
    border: 1px solid #555555 !important;
    background: none #191919 !important;
  }

  div#tabsbar span.tablink a
  {
    color: #797979 !important;
    background: none #191919 !important;
  }

  div#tabsbar span.tablink-selected
  {
    border: 1px solid #555555 !important;
    background: none #555555 !important;
  }

  div#tabsbar span.tablink-selected a
  {
    font-weight: bold;
    color: #191919 !important;
    background: none #555555 !important;
  }


   /* dynamic drop down menu */
  div#rcmKSearchpane li.selected
  {
    color: #191919 !important;
    background-color: #555555 !important;
  }


   /* login page */
  div#login-form div.boxtitle
  {
    color: #191919 !important;
    background: none #555555 !important;
  }

   /* direct access menu */
  div#mailboxlist-container h3#mailboxlist-header
  {
    margin: 0 0 3px 0 !important;
    color: #797979 !important;
    background: none #191919 !important;
  }

  div#mailboxlist-container
  {
    margin-top: 7px !important;
    border-width: 0 !important;
  }

  div#mailboxlist-container li
  {
    border: 1px solid !important;
    margin: 0 0 3px 0 !important;
  }

  div#mailboxlist-container li a
  {
    color: #797979 !important;
  }

  div#mailboxlist-container li.selected
  {
    background-color: #555555 !important;
  }

  div#mailboxlist-container li.selected a
  {
    color: #191919 !important;
  }

  div#mailboxlist-container li:hover,
   div#mailboxlist-container li:hover a
  {
    color: #797979 !important;
    background-color: #333333 !important;
  }


/* Message list */
  
.widescreen #messagelistheader, #directorylist-header
{
  background: #444;
  color: #ffff;
}

.widescreen #messagelistcontainer
{
  background-color: #616161;
}

.widescreen #messagelistfooter
{
  background: #444;
}

.uibox .boxtitle, .uibox .listing thead th, .uibox .listing thead td
{
  background-color: #444;
  color: #fff;
}

.listbox table.listing
{
  background-color: #616161;
}

.records-table tbody td
{
  background-color: unset;
}

.widescreen .messagelist td.subject span.fromto
{
  color: #fff;
}

.messagelist tbody td a
{
  color: #ddd;
}

.records-table.focus tr.selected td
{
  background-color: #444;
}

.records-table tr.selected td
{
  background-color: #555;
}

.messagelist tr.thread.expanded td
{
  background-color: #444;
}
}

@-moz-document regexp(".*watermark.html.*")
{
body
{
  background: unset;
  background-color: #616161;
}

body .moreheaderstoggle
{
  background-color: #444;
}

body #messageheader.previewheader
{
  background-color: #444;
}

   /* inbox */
  div#mailcontframe
  {
    background-color: #282828 !important;
  }

  div#mailcontframe td
  {
    padding: 4px 0 !important;
  }

  div#mailcontframe thead td,
   div#mailcontframe thead td *
  {
    border-color: #333333 !important;
    color: #797979 !important;
    background: none #191919 !important;
  }

  div#mailcontframe tbody tr.odd td,
   div#mailcontframe tbody tr.odd td *
  {
    border-color: #555555 !important;
    color: #797979 !important;
    background: none #292929 !important;
  }

  div#mailcontframe tbody tr.even td,
   div#mailcontframe tbody tr.even td *
  {
    border-color: #555555 !important;
    color: #797979 !important;
    background: none #373737 !important;
  }

  div#mailcontframe tbody tr.deleted img,
   div#mailcontframe tbody tr.deleted td,
   div#mailcontframe tbody tr.deleted a
  {
    color: #555555 !important;
    background-color: #333333 !important;
  }

  div#mailcontframe tbody tr.unread img,
   div#mailcontframe tbody tr.unread td,
   div#mailcontframe tbody tr.unread a
  {
    color: #9A9A9A !important;
    background-color: #555555 !important;
  }

  div#mailcontframe tbody tr.selected img,
   div#mailcontframe tbody tr.selected td,
   div#mailcontframe tbody tr.selected a
  {
    color: #007700 !important;
    background-color: #222222 !important;
  }

  div#mailcontframe tbody tr:hover img,
   div#mailcontframe tbody tr:hover td,
   div#mailcontframe tbody tr:hover a,
   div#mailcontframe tbody tr:hover span
  {
    background-color: #191919 !important;
  }

  div#messagecountbar *,
   div#abookcountbar *
  {
    color: #797979 !important;
  }


   /* reading message */
   /* header (sender(s), subject... */
  div#messageframe
  {
    border-width: 1px !important;
    padding: 1em !important;
    background-color: #292929 !important;
  }

  div#messageframe table.headers-table
  {
    border: 1px solid #797979 !important;
    color: #797979 !important;
    background-color: #333333 !important;
  }

  div#messageframe table.headers-table *
  {
    color: #797979 !important;
    border-width: 0 !important;
  }
   /*message body */
  div#messagebody,
   div#messagebody *
  {
    background-color: #292929 !important;
  }

  div#messagebody a
  {
    color: #797979 !important;
    background-color: #292929 !important;
  }

  div#messagebody blockquote
  {
    color: #007700 !important;
  }

   /* contacts */
  div#addresslist thead td.name
  {
    border-color: #333333 !important;
    color: #797979 !important;
    background: none #191919 !important;
  }

  div#addresslist tbody tr.odd td,
   div#addresslist tbody tr.odd td *
  {
    border-color: #555555 !important;
    color: #797979 !important;
    background: none #282828 !important;
  }

  div#addresslist tbody tr.even td,
   div#addresslist tbody tr.even td *
  {
    border-color: #555555 !important;
    color: #797979 !important;
    background: none #373737 !important;
  }

  div#contact-title
  {
    border-color: #333333 !important;
    color: #797979 !important;
    background: none #191919 !important;
  }

  div#contact-details td,
   div#contact-details a
  {
    color: #797979 !important;
  }


   /* notices */
  #message div
  {
    color: #797979 !important;
    background-color: #373737 !important;
  }
}
