css:
@-moz-document url-prefix("https://malaysiagazette.com/") {
#td-outer-wrap, .td-container-border {
    background-color: #000;
}
#respond {
    display:none
}
.td-container-border {
    border:0
}
#onesignal-bell-container.onesignal-reset .onesignal-bell-launcher.onesignal-bell-launcher-md .onesignal-bell-launcher-button {
    width: 48px;
    height: 48px;
    display: none;
}
.td-header-sp-top-menu .td_data_time, #menu-item-328892 a, #menu-item-2022 a {
    color: #fff;
}
.td-pb-row[class*="td-pb-span"], .td-pb-border-top, .page-template-page-title-sidebar-php .td-page-content > .wpb_row:first-child, .td-post-sharing, .td-post-content, .td-post-next-prev, .author-box-wrap, .td-comments-title-wrap, .comment-list, .comment-respond, .td-post-template-5 header, .td-container, .wpb_content_element, .wpb_column, .wpb_row, .white-menu .td-header-container .td-header-main-menu, .td-post-template-1 .td-post-content, .td-post-template-4 .td-post-sharing-top, .td-header-style-6 .td-header-header .td-make-full, #disqus_thread, .page-template-page-pagebuilder-title-php .td-page-content > .wpb_row:first-child, .td-footer-container::before {
    border:0;
}
.td-header-style-1 .td-header-top-menu {
    position: relative;
    top: 10px;
    background: rgb(0, 0, 0);
    background: linear-gradient(74deg, rgba(0, 0, 0, 1) 47%, rgba(103, 6, 9, 1) 100%);
}
.vc_row .vc_column-inner {
    padding-left: 0;
    padding-right: 0;
    background-color: #000;
}
.td-header-row {
    width: 100%;
    *zoom: 1;
    background: rgb(0, 0, 0);
    background: linear-gradient(74deg, rgba(0, 0, 0, 1) 47%, rgba(103, 6, 9, 1) 100%);
}
.top-bar-style-1 .td-header-sp-top-widget {
    float: right;
    background-color: #ffffff5e;
    border-radius: 5px;
}
.td-module-title a {
    color: #bbb;
}
.td-post-template-3 header h1 {
    font-size: 48px;
    line-height: 58px;
    margin-top: 6px;
    margin-bottom: 5px;
    color: #ccc;
}
body, p {
    font-family: Verdana, Geneva, sans-serif;
    font-size: 13px;
    line-height: 21px;
    color: #b7b7b7;
    overflow-wrap: break-word;
    word-wrap: break-word;
    border:0 !important;
}
.td-mega-menu ul .td-pb-border-top {
    border-top: none;
    border-left: 0px solid #e6e6e6;
    border-right: 0px solid #e6e6e6;
    background-color: #000;
}
*, #desktop-homepage {
    border: 0 !important;
}
#post-359243 {
    border:0 !important
}
.wp-block-calendar tbody td, .wp-block-calendar th {
    padding:4px;
    border:0px solid #e2e4e7
}
.block-editor-block-list__layout .reusable-block-edit-panel {
    border:0px dashed rgba(145, 151, 162, .25);
    border-bottom:none
}
.block-editor-block-list__layout .reusable-block-indicator {
    border:0px dashed #e2e4e7;
}


}
