css:
@-moz-document domain("nookazon.com") {
/* General */
.navbar {
    background-color: #18447D;
}

body {
    background-color: #262626;
    color: #DDDDDD;
}

.footer {
    background-color: #18447D;
}

a:link {
    color: #EA685E;
}

a:visited {
    color: #EA685E;
}

a.nav-link.link-btn.nav-login-btn {
    color: #18447d;
}

/* Home page */
.home-card {
    background-color: #161616;
    color: #DDDDDD !important;
}

div.home-categories {
    background-color: #161616;
}

.home-category {
    color: #DDDDDD !important;
}

.home-category-img {
    background-color: #3F6F9E;
}

div.home-browse {
    color: #DDDDDD;
}

/* Listing items */
a.item-category {
    background-color: #18447D;
    border: 2px solid #18447D;
}

a.listing-title {
    color: #DDDDDD;
}

.listing-item-link {
    color: #DDDDDD !important;
}

.listing-seller-name a {
    color: #DDDDDD;
}

.listing-seller-contact {
    color: #DDDDDD;
}

a.home-listing-button {
    color: #DDDDDD;
}

div.listing-date {
    color: #DDDDDD;
}

.no-listings {
    Color: #DDDDDD;
}

/* Products */
.item-name {
    color: #DDDDDD;
}

.no-items {
    color: #DDDDDD;
}

/* Wishlist stuff */
.wishlist-link.wishlist-link-active {
    background-color: #18447D;
}

.wishlist-link-active > div {
    color: #DDDDDD !important;
}

.wishlist-link:hover {
    background-color: #18447D;
}

.css-yk16xz-control {
    background-color: #161616;
    border-color: #EA685E;
}

.css-1pahdxg-control {
    background-color: #161616;
    border-color: #EA685E;
}

.css-1g6gooi {
    color: #DDDDDD;
}

.css-10ultgv-singleValue {
    color: #DDDDDD;
}

.css-1qa5byt-singleValue {
    color: #DDDDDD;
}

.css-26l3qy-menu {
    color: #161616;
}

.css-2613qy-control {
    background-color: #161616;
}

/* User profile */
a.profile-tab {
    color: #DDDDDD;
}

/* User review page */
div.review-date {
    color: #757575;
}

.review > div > div > a {
    color: #8CB3D9 !important;
}
}
