css:
@-moz-document domain("supergames.lt"), domain("www.supergames.lt") {
body {
    background: #464646;
    color: #bdbdbd;
}
#ipsLayout_header, #ipsLayout_header header, #ipsLayout_header nav, html[dir="ltr"] .ipsNavBar_primary > ul > li, html[dir="ltr"] .ipsNavBar_secondary > li {
    background: #2d2d2d;
    color: #bdbdbd;
    border: 0;
}
.ipsNavBar_primary > ul > li > a, #elUserNav > li > a, #elUserNav > li > a .fa, .ipsBreadcrumb[data-action=defaultStream], .ipsBreadcrumb[data-action=markSiteRead] {
    color: #93B1D6;
}
.p-header-logo {
    background: url('https://i.imgur.com/Os8z1qB.png') left bottom no-repeat;
    background-size: auto 85%;
}
.p-header-logo img {
    opacity: 0;
}
#elSearchExpanded {
    background: #373737;
    border: 0;
}
#elSearchExpanded .ipsMenu_title, #elSearchExpanded .ipsSideMenu_list {
    background: #2d2d2d;
    color: #fff;
    border: 0;
}
.ipsSideMenu_item:not(.ipsSideMenu_itemActive) a:hover, a.ipsSideMenu_item:not(.ipsSideMenu_itemActive):hover, span.ipsSideMenu_item:not(.ipsSideMenu_itemActive):hover {
    background: #1d1d1d;
}
.ipsMenu {
    background: #32393d;
    color: #fff;
    border: 0;
}
.ipsMenu_title {
    background: #252C30;
    color: #fff;
}
.ipsMenu_item > a:not(.ipsMenu_itemInline), .ipsMenu_item > span:not(.ipsMenu_itemInline), .sg-logo, .ipsDataItem_title a, .ipsType_sectionHead {
    color: #fff;
}
ul:not(.ipsMenu_keyNav) .ipsMenu_item:not(.ipsMenu_itemClicked):not(.ipsMenu_itemDisabled) a:not(.ipsMenu_itemInline):hover, .ipsMenu_item[data-selected] a, .ipsMenu_item[data-selected] span, .ipsMenu_item.ipsMenu_hover {
    background: #1d1d1d;
}
.ipsMenu.ipsMenu_bottomCenter:before, .ipsMenu.ipsMenu_bottomCenter::after {
    border-color: transparent transparent #32393d transparent;
}
.ipsMenu_headerBar, .ipsMenu_footerBar {
    background: #32393d;
    border-color: #252C30;
    color: #fff;
}
a, .ipsMenu_headerBar .ipsType_light {
    color: #fff;
}
.ipsDataList.ipsDataList_zebra > .ipsDataItem {
    background: #32393d;
}
.userBlock_info {
    background: #2d2d2d;
    border-color: #2d2d2d;
}
.userBlock, .userBlock_counters a, .userBlock_counters a:first-child {
    border: none;
    color: #f3f3f3;
}
.userBlock_name p {
    color: #f3f3f3;
}
.userBlock_counters a:hover {
    background: #1d1d1d;
    color: #fff;
}
#ipsLayout_sidebar .ipsWidget_title, #elVNC_sidebar .ipsFieldRow_label, #elSearch_sidebar .ipsFieldRow_label, .ipsTable.ipsMatrix .ipsMatrix_subHeader, .ipsSideMenu_subTitle, .ipsSideMenu_title, .ipsSideMenu_list + .ipsSideMenu_title {
    background: #252C30;
    color: #fff;
    border: none;
}
#ipsLayout_sidebar .ipsDataItem, .ipsButtonBar {
    background: #32393d;
    color: #f3f3f3;
}
#ipsLayout_sidebar a.ipsTruncate.ipsTruncate_line {
    color: #fff;
}
.ipsType_richText {
    color: #bdbdbd;
}
.ipsAreaBackground {
    background: transparent;
}
.ipsType_richText div, .ipsType_richText p, .ipsType_richText a {
    background: transparent !important;
    color: #bdbdbd !important;
}
hr.ipsHr.ipsHr_small {
    border-color: #888;
}
.ipsTabs_panel {
    background: transparent;
}
.ipsTabs.ipsTabs_stretch.ipsTabs_small .ipsTabs_item, section#elTopContributors_content .ipsAreaBackground_light a {
    background: #3d3d3d !important;
    color: #fff;
}
.ipsDataList .ipsDataItem.ipsDataItem_unread {
    background: #1d1d1d;
}
.ipsTabs_activeItem, .ipsDataList .ipsDataItem {
    background: #2d2d2d !important;
}
.ipsDataItem_main strong, .ipsDataItem .ipsDataItem_stats_number {
    color: #fff;
}
.ipsDataItem_lastPoster time, html[dir="ltr"] .ipsDataItem_lastPoster.ipsDataItem_withPhoto li, .ipsDataItem .ipsDataItem_lastPoster a.ipsType_blendLinks, .cWidgetComments.ipsType_blendLinks, .ipsDataList.ipsDataList_reducedSpacing .ipsDataItem_title, .cForumList .ipsDataItem_lastPoster .ipsContained, .cForumRow .ipsDataItem .ipsDataItem_meta, .ipsDataItem .ipsDataItem_stats_number, [data-pageapp="forums"] .ipsDataItem .ipsDataItem_stats_type, .ipsDataItem_lastPoster .ipsType_blendLinks a {
    color: #999;
}
.ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {
    background: transparent;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    background: #657089;
    color: #fff;
}
.ipsAreaBackground_reset, #elCmsPageWrap .ipsAreaBackground_reset, #extended_footer {
    background: #2d2d2d;
}
.ipsType_pageTitle, .ipsDataItem_subList li a, .ipsPagination.ipsPagination_mini a, .ipsType_blendLinks a, .ipsQuote .ipsQuote_citation a, .ipsReact_reactCount span:last-child, .ipsReact_reactCount > span > span:last-child, .ipsReact_reactCount > a > span:last-child, .ipsTabs_activeItem:hover, .ipsPager_type, .ipsPager .ipsPager_next a::after, .ipsPager .ipsPager_prev a::after, #ipsLayout_footer a, #ipsLayout_footer p {
    color: #fff;
}
.cForumList .ipsDataItem:hover {
    background: #1d1d1d !important;
}
.ipsApp .ipsButton_link, .ipsPagination.ipsPagination_mini a, .ipsApp .ipsButton_link.ipsFollow, .ipsApp .ipsButton_link.ipsPromote {
    background: #8d9aa6;
}
.ipsButtonBar .ipsPagination .ipsPagination_pageJump a {
    color: #fff !important;
}
.ipsWidget_inner.ipsPad, .cPost .ipsColumn {
    background: #2f2f2f;
}
.ipsComment_author {
    background: #2d2d2d !important;
}
.ipsFaded.ipsFaded_more {
    opacity: 0.5;
}
.ipsQuote {
    background: #292929;
    border-color: #292929;
}
.ipsReact_reactCount, .ipsReact_types ul, body.ipsApp_noTouch .ipsReact_types:hover .ipsReact_button, body:not(.ipsApp_noTouch) .ipsReact_types.ipsReact_types_active .ipsReact_button {
    background: #1d1d1d;
}
hr.ipsHr, html[dir="ltr"] .ipsReact_blurb:not(.ipsHide) + .ipsReact_types {
    border-color: #777;
}
.ipsComposeArea_editor {
    background: #2d2d2d;
    border-color: #2d2d2d;
}
html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor::before, html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor::after {
    border-color: transparent #2d2d2d transparent transparent;
}
.cke .cke_top {
    background: #242424;
    color: #fff;
    border-color: #252525;
}
a.cke_button_on {
    background: #101010 !important;
    color: #fff !important;
}
a.cke_button_off:hover, a.cke_button_off:focus, a.cke_button_off:active, a.cke_button_disabled:hover, a.cke_button_disabled:focus, a.cke_button_disabled:active, a.cke_combo_button:hover, a.cke_combo_button:focus {
    background: #191919 !important;
    color: #fff !important;
}
.ipsTabs {
    background: transparent;
}
.ipsTabs_activeItem {
    color: #fff !important;
}
.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"] {
    background: #1d1d1d;
    color: #fff;
    border-color: #1d1d1d;
}
.ipsApp .ipsButton_normal {
    background: #bdbdbd;
    color: #464646;
}
.ipsAlert, .ipsAutocompleteMenu_item[data-selected] {
    background: #1d1d1d;
    color: #fff;
}
.ipsAutocompleteMenu_item {
    background: #2d2d2d;
}
div[data-role='profileContent'] .ipsAreaBackground_reset, #elProfileStats {
    background: #373737;
}
.ipsStreamItem_action, #elProfileTabs_content .ipsType_light, #elProfileTabs_content .ipsType_light, .ipsStreamItem_meta .ipsType_light a, div[data-role='profileContent'] .ipsWidget_title a, div[data-role='profileContent'] .ipsDataItem_main a, .ipsDataItem_generic .ipsType_break, #elProfileInfoColumn a[data-action="repLog"] .ipsType_reset, .cProfileSidebarBlock .ipsType_light, .ipsDataItem_meta time {
    color: #f1f1f1;
}
.cProfileSidebarBlock, #elProfileInfoColumn a[data-action="repLog"] .ipsType_reset, div[data-role='profileContent'] .ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {
    background: #373737 !important;
    color: #fff !important;
}
div[data-role='profileContent'] .ipsTabs_item {
    background: #2d2d2d !important;
}
div[data-role='profileContent'] .ipsTabs_panel {
    background: transparent;
}
div[data-role='profileContent'] .ipsWidget.ipsWidget_vertical .ipsWidget_title {
    border-color: transparent;
    color: #fff;
}
.ipsTable th, .ipsTable_zebra tbody tr:nth-child(even):not(.ipsTable_highlight):not(.ipsModerated) {
    background: transparent;
}
.ipsBreadcrumb[data-action=defaultStream], .ipsBreadcrumb[data-action=markSiteRead] {
    color: #f1f1f1;
}
#elCopyright::after {
    content:'night mode for SuperGames.lt v1.1 by PovilasC';
}
body[data-role="internalEmbed"] {
    background: #333;
}
.ipsRichEmbed_header, iframe[data-embedContent].ipsEmbed_finishedLoading, .ipsApp .ipsRichEmbed_stats.ipsSpacer_top {
    border-color: #777;
}
.ipsTabs_panels.ipsTabs_contained {
    background: transparent;
    border: none;
}
.ipsComment:not(.ipsModerated) .ipsComment_header {
    background: #1f1f1f;
    border: none;
}
div[data-role="commentFeed"] article, .ipsDialog > div {
    background: #2d2d2d;
}
#elVNC_sidebar, #modcp_menu, #elSearch_sidebar, .ipsSideMenu.ipsAreaBackground_light, #elProfileTabContent, #elProfileTabContent .ipsTabs_panel {
    background: #3d3d3d;
}
.ipsDialog_title, .ipsDialog_close {
    color: #fff;
}
.ipsDialog .ipsTabs_item, .ipsDialog .ipsTabs_item:hover {
    background: transparent !important;
    color: #999;
}
.ipsHovercard {
    background: #464646;
    border-color: #464646;
}
.ipsHovercard_stemTop .ipsHovercard_stem {
    border-color: #464646 transparent transparent transparent;
}
.ipsHovercard_stemTop .ipsHovercard_stem:before {
    border-color: #464646 transparent transparent transparent;
}
.ipsFeedHovercard .ipsComment {
    background: transparent;
}
.cAuthorPaneFeedback_neutral {
    background: #464646;
    color: #f3f3f3;
}
fieldset.awards_fieldset {
    border-color: #555;
}
#ipsLayout_sidebar .ipsWidget_inner {
    background: transparent;
}
.ipsComposeArea_editor[data-role="statusDummy"] {
    background: transparent;
    border: none;
}
.ipsComposeArea_dummy {
    background: #222;
    color: #fff;
}
.cStatusUpdateWidget .ipsPad_half {
    padding: 10px 0;
}
.ipsApp .ipsButton_light {
    border-color: transparent;
}
html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem.ipsStreamItem_contentBlock::before {
    border-color: transparent #2d2d2d transparent transparent;
}
html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem::after {
    border-color: transparent #2d2d2d transparent transparent;
}
.cStreamFilter {
    background: #1d1d1d;
}
.cStreamFilter a, .cStreamFilter h3 {
    color: #fff;
}
.cStreamFilter > li > a.cStreamFilter_active {
    background: #111;
}
}
