css:
@-moz-document domain("evernote.com") {
  :root {
    --main-color: #193549;
    --editor-color: #b9b9b9;
    --main-color__hover: #1f4662;
    --border-color: #1f4662;
    --text-on-main-color: white;
    --accent-color: #ffc600;
  }
  #qa-NAV,
  #qa-NAV_POSITION_BOTTOM,
  #qa-NOTE_DETAIL,
  #qa-NOTES_SIDEBAR,
  #qa-SPACE_TITLE,
  section > div {
    background-color: var(--main-color);
    color: var(--text-on-main-color);
    border-color: var(--border-color);
  }
  #qa-NAV {
    border-right: 1px solid var(--border-color);
  }
  #qa-NAV_POSITION_BOTTOM hr {
    border-color: var(--border-color);
  }
  #qa-ALL_NOTEBOOKS_TABLE {
    background-color: var(--editor-color);
  }
  #qa-COMMON_EDITOR_IFRAME,
  #qa-NOTE_EDITOR,
  #qa-NOTE_EDITOR div,
  #qa-ACTIONS_MODAL {
    background-color: var(--editor-color);
    border-color: var(--border-color);
  }
  [aria-label="Note List"] {
    border-color: var(--border-color);
  }
  [aria-label="Note List"] header {
    background: var(--main-color);
    border-color: var(--border-color);
  }
  #qa-NOTES_HEADER,
  [id$="NOTES_SIDEBAR_NOTE_TITLE"] {
    color: var(--text-on-main-color);
  }
  [id$="NOTES_SIDEBAR_NOTE_TITLE"] {
    color: var(--accent-color);
  }
  [id$="NOTES_SIDEBAR_NOTE"] {
    background: var(--main-color);
    color: var(--text-on-main-color);
    border-color: var(--border-color);
  }
  [id$="NOTES_SIDEBAR_NOTE"]:hover {
    background: var(--main-color__hover);
  }
}
