css:
@-moz-document domain("coursera.org") {
body {
  background-color: #282828;
  color: #ccc;
}

select {
  background-color: #282828;
}

/******************** Course Page e.g. www.coursera.org/learn/neural-networks-deep-learning/  ********************/

/*** Middle Header ***/

.bg-white {
  background-color: #282828;
}

.border-bottom {
  border-bottom-color: #666;
}

._1rzw47j {
  color: #aaa;
}

/******************** Course Individual Page e.g. www.coursera.org/learn/neural-networks-deep-learning/lecture/eAE2G/what-is-a-neural-network ********************/

/*** Header ***/

.rc-PageHeader .c-ph-nav {
  background-color: #282828;
}

.rc-PageHeader .c-ph-nav.full-width {
  border-bottom-color: #444;
}

.rc-ItemPage {
  background-color: #f00;
}

/* Logo */
.branding-image img {
  filter: invert(85%);
}

.rc-UniversityLogo {
  border-left-color: #444;
}

/* 'Explore' dropdown */

.explore-Button-bottom-border-mask {
  background-color: #282828;
}

.mega-menu {
  background-color: #282828;
  box-shadow: 0 1px 6px rgba(31,31,31,.12), 0 1px 4px rgba(31,31,31,.12);
}

.menu-banner-ads-container {
  background-color: #282828;
}

.white {
  background-color: #282828;
  border-color: #444;
}

.mega-menu-items .menu-item {
  color: #ccc;
}

.mega-menu-items .menu-item.selected {
  background-color: #333;
}

.mega-menu-items .menu-item svg {
  fill: #aaa !important;
}

.soapbox-inner-container {
  color: #ccc;
}

/* Search */

.rc-AutoComplete input[type="text"].react-autosuggest__input {
  background-color: #282828;
  border-color: #666;
}

.rc-AutoComplete .react-autosuggest__suggestion--highlighted {
  background-color: #333;
}

.rc-AutoComplete .react-autosuggest__suggestions-container--open {
  background-color: #282828;
  border-color: #666;
}

.rc-AutoComplete .section-item .item-name {
  color: #aaa;
}

.color-hint-text, .search-bar .input-area .cif-cancel-hint {
  color: #666;
}

/* Notification bell */
.rc-NotificationIcon svg {
  fill: #666 !important;
}

.rc-AuthenticatedAccountDropdown .c-authenticated-dropdown-menu-container button {
  border-left-color: #444;
}

/* Notification dropdown */

.rc-NotificationListView .notification-list-view-content {
  background-color: #282828;
  border-color: #666;
}

.rc-NotificationHeader {
  border-bottom-color: #444;
}

.rc-PageHeader {
  color: #aaa;
}

.rc-NotificationListView .notification-list-view-triangle::after {
  background-color: #282828;
  border-color: #666;
}

/* User */

.rc-PageHeader .c-ph-nav .c-container .c-ph-right-nav span.c-navbar-item > .c-ph-right-nav-button.c-ph-avatar-button .rc-UserPortrait, .rc-PageHeader .c-ph-nav .c-container .c-ph-right-nav ul.c-navbar-list > .c-ph-right-nav-button.c-ph-avatar-button .rc-UserPortrait {
  color: #aaa;
}

.rc-UserPortrait.pii-hide svg {
  fill: #333 !important;
}

/* User dropdown */

.rc-PageHeader .c-ph-right-nav-expanded {
  background-color: #282828;
}

.rc-AuthenticatedAccountDropdown:hover {
  background-color: #333;
}

.rc-AuthenticatedAccountDropdown .c-authenticated-dropdown-menu-container .c-authenticated-dropdown-menu {
  background-color: #282828;
}

.rc-HeaderRightNavButton a, .rc-HeaderRightNavButton button {
  color: #aaa;
}

.rc-HeaderRightNavButton.dropdown-btn a:focus, .rc-HeaderRightNavButton.dropdown-btn a:hover {
  background-color: #333;
}

.rc-HeaderRightNavButton a:focus, .rc-HeaderRightNavButton a:hover, .rc-HeaderRightNavButton button:focus, .rc-HeaderRightNavButton button:hover {
  color: #aaa;
}

.rc-AuthenticatedAccountDropdown .c-authenticated-dropdown-menu-container .c-authenticated-dropdown-menu .sign-out {
  background-color: #333;
}

.rc-AuthenticatedAccountDropdown .c-authenticated-dropdown-menu-container .c-authenticated-dropdown-menu .sign-out:focus, .rc-AuthenticatedAccountDropdown .c-authenticated-dropdown-menu-container .c-authenticated-dropdown-menu .sign-out:hover {
  background-color: #333;
  color: #aaa;
}

/**** Body ****/

.rc-ItemNavigation {
  background-color: #282828;
}

.rc-ItemPrimaryNavigation {
  border-bottom-color: #444;
}

.rc-PreviousAndNextItem .divider {
  border-right-color: #444;
}

/*** Left column ***/

.theme-white {
  background-color: #282828;
}

.rc-NavSingleItemDisplay.highlighted, .rc-NavSingleItemDisplay:hover {
  background-color: #282828;
}

.color-primary-text, ul.styleguide.dropdown li > button.secondary:not([disabled]), ul.styleguide.dropdown li > button:not([disabled]) {
  color: #aaa;
}

.rc-CollapsibleLesson button:focus, .rc-CollapsibleLesson button:hover {
  background-color: #333;
}

.rc-ItemIcon.horizontal-box svg {
  fill: #666 !important;
}

.rc-NavSingleItemDisplay.highlighted, .rc-NavSingleItemDisplay:hover {
  background-color: #303030; /* #ededed; */
}

/*** Right column ***/

/* Left Navigation panel */
.rc-SecondaryNavTogglePanel {
  background-color: #282828;
  box-shadow: 3px 0 3px 0 #333;
  border-left-color: #666;
}

.rc-SecondaryNavTogglePanel .nav-tool-button.active {
  background-color: #282828;
}

.rc-SecondaryNavTogglePanel .nav-tool-button.active svg {
  fill: #aaa !important;
}

.rc-ItemNavigation .item-scroll-container {
  background-color: #282828;
}

.rc-ItemBox .item-box-content {
  color: #aaa;
}

/* Lecture Video */
.rc-VideoItemWithHighlighting .vjs-coursera-skin.vjs-coursera-phoenix-skin video {
  opacity: 0.8;
}

/* Right Navigation panel */

.rc-VideoHighlightSidebar {
  background-color: #282828;
  box-shadow: -3px 0 3px 0 #333;
}

.rc-HighlightSidebarTogglePanel {
  background-color: #282828;
  border-right-color: #444;
}

.cui-iconWrapper svg {
  fill: #aaa !important;
}

/* Right Navigation Notes */

.rc-HighlightSidebarTogglePanel .notes-tool-button.active {
  background-color: #333;
}

.rc-VideoHighlights {
  background-color: #282828;
}

/* Assessment Pop-up Question after video */

.coursera-assess-assessment-in-video {
  background-color: #282828;
}

.coursera-assess-assessment-in-video .coursera-assess-assessment-in-video-bar {
  background-color: #282828;
  border-top-color: #444;
}

.alert-feedback {
  background-color: #333;
}

.c-assess-common-arrow::after {
  border-bottom-color: #333;
}

/* 'Save Note' */
.rc-VideoToolbar {
  border-bottom-color: #666;
}

.rc-VideoToolbarButton {
  background-color: #333;
}

.rc-VideoToolbarButton:disabled, .rc-VideoToolbarButton:disabled:hover {
  background-color: #222 !important;
}

.rc-VideoToolbarButton:disabled:hover svg, .rc-VideoToolbarButton:disabled svg {
  fill: #666 !important;
}

.rc-VideoToolbarButton span {
  color: #aaa;
}

.rc-VideoToolbarButton:disabled span {
  color: #666;
}

.rc-DownloadsDropdown {
  background-image: none;
}

.rc-DownloadsDropdown.bt3-open .bt3-dropdown-toggle, .rc-DownloadsDropdown.bt3-open .bt3-dropdown-toggle:hover {
  background-image: none;
  background-color: #333;
  color: #aaa;
}

.rc-DownloadsDropdown .bt3-dropdown-toggle {
  background-color: #333;
  color: #aaa;
}

.bt3-dropdown-menu {
  background-color: #282828;
}

/* Life, Dislike, Flag */

.rc-LikeContent .c-button-icon > svg {
  fill: #666 !important;
}

.rc-Flag .c-button-icon > svg {
  fill: #666 !important;
}


/* Subtitles */

.rc-TrackChooser select:not([multiple]) {
  background-color: #333;
}

#select-language {
  border-color: #333;
}

/* Transcript Paragraphs */

.rc-Paragraph .timestamp {
  background-color: #282828;
  color: #aaa;
}

.rc-Paragraph .timestamp:hover {
  color: #ccc;
}

.rc-Phrase {
  color: #aaa;
}

.rc-Phrase:hover {
  background-color: #333;
}

/***** Coursera Honor Code Pop-up Page *****/

.rc-Modal.box .c-modal-content, .rc-Modal.side .c-modal-content {
  background-color: #282828;
}

.rc-Modal.box .c-modal-overlay, .rc-Modal.side .c-modal-overlay {
  background-color: #666;
}

.rc-Modal {
  color: #aaa;
}

/***** Quiz Sub-Page *****/

/* 'Get closer to your goal' side panel */
.rc-CoverPageNudges {
  background-color: #282828;
}

.rc-CoverPageRow {
  border-bottom-color: #666;
}

.rc-CoverPageRowRightSideGrade {
  border-left-color: #666;
}

/***** Graded Quiz Page *****/

.rc-TunnelVisionWrapper .rc-TunnelVisionClose, .rc-TunnelVisionWrapper .rc-TunnelVisionClose:hover {
  background-color: #282828;
}

.rc-TunnelVisionHeader {
  border-bottom-color: #444;
}

.rc-AttemptPageTopBanner__grade-info {
  border-left-color: #444;
}

div[data-classname="tunnelvision-window-0"] {
  background-color: #282828;
}

.rc-AttemptPage__header, .rc-PracticeQuizAttemptPage__header {
  border-bottom-color: #444;
}

/* Questions */
legend {
  color: #ccc;
}

.rc-QuestionPoints__pill {
  border-color: #666 !important;
  color: #ccc;
}

.rc-CML.styled figure img {
  opacity: 0.6;
}

input.honor-code-agreement.name-input.pii-hide {
  background-color: #282828;
  border-color: #444;
}

.rc-SubmissionControls__button {
  background-color: #333;
}

/* Results */

.rc-GradeNotification--success {
  background-color: #333;
}

.rc-GradeNotification--partial {
  background-color: #333;
}

.rc-AttemptPageTopBanner--finished, .rc-AttemptPageTopBanner--passed {
  background-color: #2f3130; /* #f3faf7; */
}

.rc-Option.rc-Option--isReadOnly .cui-Checkbox:hover {
  color: #0156b8;
}

/******************** Course Home Page e.g. www.coursera.org/learn/neural-networks-deep-learning/home/  ********************/

/***** Left column *****/

.rc-CourseNavigation {
  background-color: #282828;
}

.rc-NavigationLink {
  color: #ccc;
}

.rc-NavigationLink:active, .rc-NavigationLink:focus, .rc-NavigationLink:hover {
  background-color: #333;
  color: #ccc;
}

.rc-NavigationDrawerLink {
  color: #aaa;
}

.rc-NavigationLink.selected {
  background-color: #333;
}

.rc-NavigationDrawerLink.selected, .rc-NavigationDrawerLink:active, .rc-NavigationDrawerLink:focus, .rc-NavigationDrawerLink:hover {
  background-color: #333;
  color: #ccc;
}

/***** Right column *****/

/**** Overview ****/

.rc-CourseNameHeader .course-name {
  color: #aaa;
}

/** Timeline **/

.rc-ItemIcon.horizontal-box svg {
  color: #f00 !important;
}

.rc-TooltipAssignmentProgressIcon .icon-wrapper {
  background-color: #333;
}

.rc-CourseMaterial {
  color: #aaa;
}

.rc-WeekCardHeader {
  background-color: #333;
}

.rc-WeekCardBody {
  background-color: #282828;
}

.module-section {
  color: #aaa;
}

.rc-UngradedItem:hover {
  background-color: #333;
}

.rc-AssignmentRow:hover {
  background-color: #333;
}

/*** Overview > Week ***/

.rc-Home .course-content {
  background-color: #282828;
}

.rc-PeriodPage .week-description .week-number {
  color: #aaa;
}

.rc-PeriodPage .week-description .course-title {
  color: #ccc;
}

.card-no-action, .card-one-clicker, .card-one-clicker:hover, .card-rich-interaction, .modal.card-one-clicker, .modal.card-one-clicker:hover, .modal.card-rich-interaction, [class*="card-"].selected, ul.styleguide.dropdown {
  background-color: #282828;
  border-color: #333;
}

.card-headline-text {
  color: #aaa;
}

.card-one-clicker:hover, .card-one-clicker:hover:hover, .card-rich-interaction:hover, .modal.card-one-clicker:hover, .modal.card-one-clicker:hover:hover, .modal.card-rich-interaction:hover, [class*="card-"].selected:hover, ul.styleguide.dropdown:hover {
  border-color: #666;
}

.rc-WeekSingleItemDisplay.highlighted, .rc-WeekSingleItemDisplay:hover {
  background-color: #333;
}

/* hovering Help widget */
.rc-Help .help-widget {
  background-color: #333;
}

/**** Resources ****/

.rc-CML.styled .cml-asset .asset-container {
  border-color: #666;
}

.rc-Home {
  background-color: #282828;
}

/**** Course Info ****/

.rc-TogglableContent.white-gradient .content .overlay {
  background: linear-gradient(transparent,transparent,#444),transparent;
}

/******************** Course Discussions Forum e.g. www.coursera.org/learn/neural-networks-deep-learning/discussions/weeks/N ********************/

/***** Middle column *****/

/** Tags **/

/* e.g. Mentor Replied */
.bgcolor-accent-brown-light {
  background-color: #3f382a; /* #ece9e3; */
}

.rc-ThreadBadge {
  color: #cb9025; /* #8a6219 */
}

/* e.g. Staff */
.bgcolor-success-light {
  background-color: #3f612f; /* #ecf4e8; */
}

.color-secondary-text {
  color: #ccc;
}

/** Posts **/

.rc-ThreadsListEntry.focused, .rc-ThreadsListEntry:hover {
  background-color: #333;
}

/* 'Most Recent' */

ol.tabs li.colored-tab.selected, ol.tabs li.tab.selected, ol.tabs li.tab:hover, ul.tabs li.colored-tab.selected, ul.tabs li.tab.selected, ul.tabs li.tab:hover {
  color: #aaa;
}

/** Search **/

.rc-ListBody .c-pre-fossil.c-fossilized {
  background-color: #222a;
}

.search-bar .input-area .search-input {
  background-color: #333;
}

/***** Right column *****/

.rc-SubforumSidebar .subforum-entry:hover {
  background-color: #333;
}

/*** Reply Editor ***/

.rc-CMLEditor .scribe-editor {
  background-color: #333;
  border-color: #444;
}

.rc-CMLEditorToolbar {
  background-color: #282828;
  border-color: #444;
}

.rc-CMLEditorToolbar .scribe-toolbar-button:hover:not(:disabled) {
  background-color: #333;
  outline: 1px solid #444;
}

/* Reply button */
.search-bar .search-button[disabled], button.facebook[disabled], button.linkedin[disabled], button.passive[disabled], button.primary[disabled], button.secondary[disabled] {
  background-color: #333;
  border-color: #444;
  color: #aaa;
}

.c-dropdown-chevron {
  color: #aaa;
}

.ace-tomorrow {
  background-color: #282828;
  color: #aaa;
}

.ace-tomorrow .ace_gutter {
  color: #aaa;
}

.ace-tomorrow .ace_gutter {
  background-color: #333;
}

.rc-CodeBlock {
  border-color: #444;
}

.rc-CML.styled pre {
  border: 1px solid #444;
}

pre {
  background-color: #333;
}

.ace-tomorrow .ace_gutter-active-line {
  background-color: #444;
}

/******************** Course Page (without login) e.g. www.coursera.org/learn/neural-networks-deep-learning/  ********************/

/* 'Go To Course' button */
.EnrollButton button {
  background-color: #333 !important;
  color: #aaa !important;
}

/* Tags */

.border-a {
  border-color: #444;
}

._rsc0bd.m-r-1s.m-b-1s {
  background-color: #333 !important;
}

._rsc0bd {
  color: #aaa;
}

/* Progress Card on right */

._ols6wi {
  background-color: #333;
}

._1dfpihp {
  border-color: #444;
}

.ProductGlance svg {
  fill: #aaa !important;
}

/* Testimonials */
.rc-UserTestimonialsSection {
  background-color: #333;
}

.slick-slider.slick-initialized svg {
  fill: #aaa !important;
}

.rc-UserTestimonial .normal-content-container {
  background-color: #282828;
}

.rc-UserTestimonials .rc-Carousel .rc-UserTestimonial .normal-content-container {
  box-shadow: 2px 2px 2px #333;
}

/* 'Start learning today' */
.text-primary {
  color: #aaa;
}

.rc-CertificateBlock {
  filter: invert(80%);
}

/* other Course recommendations */

._1hdqj4r4 {
  background-color: #333;
}

._1meetf18 {
  color: #aaa !important;
}

.border-top {
  border-top-color: #444;
}

.border-bottom {
  border-bottom-color: #444;
}

._bsitzg {
  border-color: #666;
  background-color: #333;
}

._bsitzg div {
  opacity: 0.7;
}

/* FAQ */

.bg-light-blue {
  background-color: #1e293c; /* #f5f7fa; */
}

.rc-GlobalFooter {
  border-top-color: #444;
}

.rc-GlobalFooter_column-container--border-top {
  border-top-color: #444;
}

.rc-GlobalFooter_social-media_list img {
  filter: invert(80%);
}

.rc-SubFooter {
  background-color: #282828;
}

.rc-SubFooterSection__header {
  color: #aaa;
}

.rc-SubFooterSection__content-column-link-text {
  color: #aaa;
}

.rc-SubFooter__social-media-and-copyright {
  border-top-color: #444;
}

.rc-SocialMediaLinksV2__social-media-list-item img {
  filter: invert(80%);
}



}
