css:
@-moz-document domain(www.zerohedge.com) {
html, body {
    background: #222;
    color: #bbb;
    font-size: 15px;
}
h1 {
    color: #bbb;
    font-size: 26px;
}
h2 a, h2 a:hover, h2 a:visited {
    color: #bbb;
}
img {
    filter:brightness(90%);
}
h2, .node--type-article.node--view-mode-teaser h2 {
    font-size: 22px;
}
p, h3 {
    font-size: 16px;
}
li[class*='Header_menuItem__'] a {
    color: #bbb !important;
}
img[class*='Header_overflowMenuIndicator'] {
    filter: invert(75%);
}
.node--sticky {
    background: #222 !important;
}
.node--type-contributor-article {
    background: transparent !important;
}
.view-content {
    background: #222 !important;
}
.node {
    background: #222;
    color: #bbb;
}
.node a {
    display: inline-block;
}
.node__content ul li {
    font-size: 16px;
}
.node span,
.field--name-comment-body p,
.up-current-score,
.down-current-score,
li[class*='ZeroHedgeReads_listItem__'] a,
div[class*='ZeroHedgeReads_header__'] {
    color: #bbb;
}
div[class*='BlockLogin_container__'],
div[class*='ZeroHedgeReads_listControl__'],
div[class*='ZeroHedgeReads_container__'] {
    background: #222 !important;
    color: #eee;
}
.sidebar-right-wrapper, #exit-modal, .newsletter-insert, .carousel-placement,
.ExitModalNewsletter_visible__rdEkd {
    display:none !important
}
#header{
    padding: 15px;
}
#header .header-left {
    width: 70%;
}
#block-header-menu {
    margin: auto 0;
}
#header .header-left .menu--header-menu > ul li {
    border-right: 2px solid #ccc;
    padding: 0 0.6% 0 0.6%;
}
#header .header-left .menu--header-menu > ul li a {
    color: #bbb;
    padding: 0 0 0 2px;
    font-size: 15px;
}
#header .header-left .menu--header-menu > ul li a:hover {
    color: #2856c6;
}
a[href="/video"] {
    visibility: hidden;
    text-indent: -9999px;
    line-height: 0;
}
a[href="/video"]:after {
    content: 'VIDEO';
    text-indent: 0;
    line-height: initial;
    display: block;
    visibility: visible;
}
#header .header-logo {
    overflow: hidden;
}
#header .header-logo .logo-full,
img[class^='Header_siteHeaderLogo__'] {
    filter: invert(85%);
    padding-left: 0;
    padding-right: 25px;
    width: 105%;
    height: 105%;
}
#header .header-left .menu--header-menu > ul li:last-child {
    border-right: none
}
a {
	color: #2856c6;
}
.menu--zero-hedge-reads h2 {
	color: #eee;
    font-size: 16px;
}
#block-zerohedgereads a {
    font-size: 12px;
}
p a:hover, #block-zerohedgereads a:hover, blockquote a:hover {
	color: #2856c6;
    text-decoration: underline dotted;
}
p a:visited, #block-zerohedgereads a:visited, blockquote a:visited {
	color: #3054ab;
}
a:hover {
	color: #1f449b;
}
.item-list {
    background: transparent;
}
.embedded > body {
    background: #222;
    color: #bbb;
}
#block-zerohedgereads-menu {
    background-color: #2b2b2b;
}
.talk-tab-button {
    color: #ccc;
}
button[class^='ViewingOptions__button'] {
    color: #ccc;
}
blockquote p {
    background-color: #333;
    color: #ccc;
}
div[class^='talk-plugin'] blockquote {
	background-color: #333;
}
.cke_editable blockquote::before, .talk-embed-stream blockquote::before {
	border-left: 5px solid #888;
}
.cke_editable, .talk-stream-comment {
	font-size: 15px;
}
.Tab__buttonActive___2Tiaz, .Tab__buttonActive___2Tiaz:focus, .Tab__buttonActive___2Tiaz:hover {
	background: #333;
    border-color: #555;
}
.SignInButton__button___3ZCyR:hover {
	background-color: #182648
}

blockquote::before {
	border-left: 5px solid #888;
    background-color: #333;
}
blockquote {
	background-color: #333;
}
.talk-load-all-comments:hover {
	background-color: #182648;
}
.talk-stream-show-all-comments-button-container .talk-stream-show-all-comments-button, .talk-load-more button {
	background-color: rgb(40, 42, 44);
}
.talk-stream-show-all-comments-button-container .talk-stream-show-all-comments-button:hover, .talk-load-more button:hover {
	background-color: #182648;
}
.talk-load-more-replies .talk-load-more:hover button {
	background-color: #182648;
}
p span {
    background-color: transparent !important;
    color: #bbb !important;
}
.node.node--view-mode-full .node__meta .node__submitted, .node.node--view-mode-full .node__meta .node__submitted a {
	color: #999;
}
.pager .pager__item--next, .pager .pager__item--previous, .pager .pager__item--last, .pager .pager__item--first {
	background: #333;
}
.pager .pager__item--next:hover, .pager .pager__item--previous:hover, .pager .pager__item--last:hover, .pager .pager__item--first:hover {
    background-color: #182648;
}
.pager .pager__item--next span, .pager .pager__item--previous span, .pager .pager__item--last span, .pager .pager__item--first span {
    color: #bbb;
}
.pager__items li {
    color: #999;
}
#talk-embed-stream-container p {
    color: #ccc;
}
.node.node--view-mode-full figure.caption figcaption {
    background: #333;
    color: #ccc;
}
div[class*='Article_stickyContainer__'] {
    background-color: #333;
}
hr {
    background-color: #888;
}
div[class*='BlockComments_showBtn__'],
div[class*='HeaderUserMenu_loginBtn'] {
    color: #222;
}
header[class*='Header_sticky__'] {
    display: none;
}
a[class*='BlockLogin_link__'] {
    color: hsl(211, 45%, 40%);
}

div[class*='ZeroHedgeReads_btn__'] {
    color: #bbb;
}
form[class*='BlockSearch_container__'],
div[class*='TopPosts_posts__'],
div[class*='BlockContactInfo_dropdown__'] {
    background-color: #222;   
}
div[class*='SidebarExpandableTitle_container__'] {
    color: #bbb;
}
}
