    --white1: #FFF;
    --white2: #F7F7F7;
    --white3: #DDD;
    --black1: #000;
    --black2: #111111;
    --black3: #121212;
    --black4: #131313;
    --black5: #151515;
    --black6: #161616;
    --black7: #1C1C1C;
