    content:"";
}
.icon-user {
    display: none;
}
button:focus, select:focus {
    outline: 0;
}
.body {
    background: var(--gray1);
}
.mainmenuwrapper {
    background:  url("http://i.imgur.com/7ZKIcIF.jpg") top left;
    border-right: none;
    background-size: cover;
}
.ps-overlay {
    background: rgba(0, 0, 0, 0.3);
}
.ps-room.ps-room-light {
    background-color: rgba(0, 0, 0, 0);
