    transition: 0.3s;
}
.tournament-tools.active {
    background: var(--customcolor1)!important;
}
.tournament-wrapper.active {
    background: var(--gray1);
}
.tournament-status {
    background: var(--gray1);
    border-color: var(--gray1);
    color: var(--white2);
    text-shadow: 0 0px;
}
.tournament-toggle {
    background: var(--gray1);
    border-color: var(--gray1);
    color: var(--white2);
    text-shadow: 0 0px;
}
.tournament-tools .button {
    background: transparent;
    border-color: var(--customcolor2);
    color: var(--customcolor2);
    text-shadow: none;
    box-shadow: 0px 0px 3px var(--customcolor2);
    transition: 0.3s;
    width: 113px;
    border-radius: 4px;
}
.tournament-tools .button:hover {
    background: var(--customcolor3);
    border-color: var(--customcolor4);
    color: var(--customcolor4);
    box-shadow: 0px 0px 3px var(--customcolor4);
    text-shadow: none;
    transition: 0.3s
}
.tournament-tools .button:active {
    background: var(--customcolor3);
    border-color: var(--customcolor4);
    color: var(--customcolor4);
    box-shadow: none;
    text-shadow: none;
    transition: 0.3s
}
.tournament-title {
    background: rgba(0, 0, 0, 0.5)!important;
    color: var(--white1);
    text-shadow: 0px 0px 3px var(--black1);
}
.tournament-bracket {
    background: url("http://i.imgur.com/Q8vHT0Y.png");
}
.ps-room .battle-controls {
    background: transparent!important;
}
}
