css:
@-moz-document domain("frontier-rpg.boards.net") {
/* GENERAL */

:root {
  --space-blue: #2788f5;
  --space-haze: rgba(0,0,0,0.6);
}

html {
  font-size: 16px;
}

body {
  font-size: medium;
  font-family: Roboto;
  line-height: 1.5;
}

#wrapper {
   width: 100%;
   max-width: 980px;
 }

a { transition: all 300ms ease;}

a:hover,
a:focus {
  color: orange !important;
}

.container abbr.time { 
  color: inherit;
  font-size: inherit; 
}

hr {
  border-color: rgba(255, 255, 255, 0.25);
}

/* HEADER */

#navigation-menu {
  display: flex;
  justify-content: space-between;
}

#navigation-menu > ul {
  float: none;
}

#navigation-menu > ul li a {
  padding: 0.25rem 0.5rem;
  font-weight: normal;
  font-size: 0.85rem;
  line-height: 1 !important;
}

#navigation-menu > ul li a.state-active,
#navigation-menu > ul li a:hover,
#navigation-menu > ul li a:focus {
  font-weight: normal;
  color: #ffffff !important;
  font-size: 0.85rem;
  line-height: 1 !important;
  border-radius: 3rem;
  background: var(--space-blue);
}

#welcome {
  float: none;
  font-size: 0.85rem;
  line-height: 1 !important;
}

#welcome b {
  font-weight: normal;
}

#welcome a:hover,
#welcome a:focus,
#welcome a:hover font,
#welcome a:focus font {
  color: orange !important;
}

#navigation-menu > b,
#navigation-menu.ui-helper-clearfix:after {
  display: none;
}

.search-filters-button img {
  height: 20px;
}

/* BOARD INDEX */

.rfbdi-board_img_link {
  display: block;
  height: 5rem;
}

.viewing {
  display: block;
  font-family: 'Inconsolata', monospace;
}

.board .board-link {
  font-weight: normal !important;
  font-size: 0.9rem !important;
}

.board .description {						
  line-height: 1.25 !important;
  opacity: 0.75;
}

.moderators {
  display: none;
}

.threads,
.posts,
.latest {
  font-size: 0.85rem;
  font-weight: normal;
}

.latest a {
  font-size: 0.85rem;
}

td.threads,
td.posts {
  font-family: 'Inconsolata', monospace !important;
}

abbr {
  font-weight: normal !important;
}

a.thread-link,
.state-hover a.thread-link {
  font-size: 0.95rem !important;
}



#recent-threads-cell {
  height: auto;
  font-weight: normal;
}

.pad-all {
  padding: 1rem !important;
}

.stats {
  font-weight: normal;
}

.stats .info {
  padding: 1rem;
}

.stats td.icon { 
  display: none;
}

.stats img {
  display: none;
}

.stats th {
  font-weight: normal;
}


/* MINI POFILE */

.even .mini-profile {
  background: #363636;
}

.mini-profile br {
  display: none;
}

.mini-profile .o-user-link {
  display: block;
  margin-bottom: 0.5rem;
  font-size: 1.15rem !important;
}

.mini-profile .o-user-link + img {
  width: 100% !important;
  height: auto !important
}

.mini-profile .custom-title {
  display: none;
}

.mini-profile .avatar {
  position: relative;
  margin: 1rem 0;
  border: 1px solid var(--space-blue);
}

.avatar_size_default {
  height: auto;
}
.mini-profile .o-user-link[style*='ece41b'] ~ .avatar {
  border-color: #ece41b;
}

.mini-profile .o-user-link[style*='06aac0'] ~ .avatar {
  border-color: #06aac0;
}

.mini-profile .o-user-link[style*='f55133'] ~ .avatar {
  border-color: #f55133;
}

.mini-profile .o-user-link[style*='2ab135'] ~ .avatar {
  border-color: #2ab135;
}

.mini-profile .o-user-link[style*='c3c3c3'] ~ .avatar {
  border-color: #c3c3c3;
}

.mini-profile .personal-text {
	display: block;
    margin-bottom: 0.5rem;
    font-weight: normal !important;
    font-size: 0.9em !important;
    font-style: italic !important;
    text-align: center !important;
}
  
.mini-profile .info {
  font-weight: normal !important;
}

.mini-profile .info br {
  display: block;
}

.mini-profile font[color="blue"] {
	display: none;
}

/* POST */

.posts .post.even {
  color: #d5d5d5;
  background: #3d3d3d;
}

.post-number a {
  font-size: 0.7rem;
}

.posts .post.even .quote div.quote_body {
  color: #d5d5d5;
  background: #333;
}

.message,
.post .quote div.quote_body {
  font-size: 1rem;
  line-height: 1.55;
  color: #d5d5d5;
}

.quote_header + br {
  display: none;
}

.message font[color] {
  background: black;
}

.message font[color="#fdfcfc"],
.message font[color="#ffffff"] {
  color: #d5d5d5;
  background: transparent;
}

.button .status img {
  height: 20px;
}

span[itemprop="name"] {
  display: inline-block;
  margin-bottom: 0.25rem;
  padding: 0.25rem 0.5rem;
  font-size: 0.85rem;
  background: rgba(0, 0, 0, 0.25);
  border-radius: 0.25rem;
}

.edited_by .o-user-link { 
  font-size: 0.8em !important;
}

.signature {
  opacity: 0.5;
}

/* POSTING */

textarea {
  font-size: 1rem;
  line-height: 1.25;
  font-family: 'Inconsolata', monospace;
  box-sizing: border-box;
}

.quick-reply textarea {
  padding: 1rem;
}

.editor textarea {
  padding: 1rem !important;
  background: #313131 !important;
}

/* FORMS */

input[type="text"],
input[type="submit"] {
  padding: 0.25rem 0.5rem;
  color: #e0e0e0;
  font-size: 0.9rem;
  line-height: 1.25;
  font-weight: normal;
  border: 1px solid #020202;
  background: #353535;
}

input[type="text"]:focus,
input[type="submit"]:focus {
  border: 1px solid var(--space-blue);
}

input[type="submit"]:hover {
  font-size: 0.9rem;
  font-weight: normal;
}

/* PROFILE */

.ui-tabMenu ul li a,
.ui-tabMenu ul li.ui-active a,
.ui-tabMenu ul li:hover a {
  font-weight: normal;
  color: #e0e0e0;
}

.ui-tabMenu ul li {
  background: #252525;
}

.ui-tabMenu ul li.ui-active {
  background: #3b3b3b;
}

.name_and_group,
.controls {
  font-weight: normal;
}


.show-user td.status-input input {
  width: calc(100% - 1rem);
}

.content-box.center-col,
.content-box.center-col .headings,
.custom-field-rank,
.custom-field-position,
.custom-field-species,
.custom-field-characterage,
.custom-field-charactergender,
.custom-field-discordusername,
.custom-field-playertimezone {
  font-weight: normal;
}

.activity-item {
  font-size: 0.9rem;
}

.activity-item .avatar {
  width: 60px !important;
}

.activity-item .avatar_size_medium,
.activity-item .avatar_size_medium img {
  height: 50px;
  width: 50px;
}

.show-user .list .main {
  padding: 0;
}

}
