css:
@-moz-document url-prefix("https://www.youtube.com"), url-prefix("https://youtube.com") {
/* VARS */
html {
    --theme-color: #f00;
    --ytd-grid-max-width: 1400px !important;
    --ytd-grid-5-columns-width: 1100px !important;
}

html:not(.style-scope) {
    --shadow: #bbb4;
    --search-bg-color: #eee;
    --card-background-color: #f5f5f5;
    --card-background-hover-color: #eeeeee;
    --scroll-bg: #ddd;
    --scroll: #aaa;
    --scroll-hover: #999;
}

html:not(.style-scope)[dark],
:not(.style-scope)[dark] {
    --shadow: #0004;
    --search-bg-color: #111;
    --card-background-color: #222;
    --card-background-hover-color: #333;
    --scroll-bg: #111;
    --scroll: #333;
    --scroll-hover: #444;
}
}

